INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1150, 'nvi', 'Gênesis', 'gn', 39, 1, 'José havia sido levado para o Egito, onde o egípcio Potifar, oficial do faraó e capitão da guarda, comprou-o dos ismaelitas que o tinham levado para lá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1151, 'nvi', 'Gênesis', 'gn', 39, 2, 'O Senhor estava com José, de modo que este prosperou e passou a morar na casa do seu senhor egípcio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1152, 'nvi', 'Gênesis', 'gn', 39, 3, 'Quando este percebeu que o Senhor estava com ele e que o fazia prosperar em tudo o que realizava,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1153, 'nvi', 'Gênesis', 'gn', 39, 4, 'agradou-se de José e tornou-o administrador de seus bens. Potifar deixou a seu cuidado a sua casa e lhe confiou tudo o que possuía.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1154, 'nvi', 'Gênesis', 'gn', 39, 5, 'Desde que o deixou cuidando de sua casa e de todos os seus bens, o Senhor abençoou a casa do egípcio por causa de José. A bênção do Senhor estava sobre tudo o que Potifar possuía, tanto em casa como no campo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1155, 'nvi', 'Gênesis', 'gn', 39, 6, 'Assim, deixou ele aos cuidados de José tudo o que tinha, e não se preocupava com coisa alguma, exceto com sua própria comida. José era atraente e de boa aparência,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1156, 'nvi', 'Gênesis', 'gn', 39, 7, 'e, depois de certo tempo, a mulher do seu senhor começou a cobiçá-lo e o convidou: "Venha, deite-se comigo! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1157, 'nvi', 'Gênesis', 'gn', 39, 8, 'Mas ele se recusou e lhe disse: "Meu senhor não se preocupa com coisa alguma de sua casa, e tudo o que tem deixou aos meus cuidados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1158, 'nvi', 'Gênesis', 'gn', 39, 9, 'Ninguém desta casa está acima de mim. Ele nada me negou, a não ser a senhora, porque é a mulher dele. Como poderia eu, então, cometer algo tão perverso e pecar contra Deus? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1159, 'nvi', 'Gênesis', 'gn', 39, 10, 'Assim, embora ela insistisse com José dia após dia, ele se recusava a deitar-se com ela e evitava ficar perto dela.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1160, 'nvi', 'Gênesis', 'gn', 39, 11, 'Um dia ele entrou na casa para fazer suas tarefas, e nenhum dos empregados ali se encontrava.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1161, 'nvi', 'Gênesis', 'gn', 39, 12, 'Ela o agarrou pelo manto e voltou a convidá-lo: "Vamos, deite-se comigo! " Mas ele fugiu da casa, deixando o manto na mão dela.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1162, 'nvi', 'Gênesis', 'gn', 39, 13, 'Quando ela viu que, ao fugir, ele tinha deixado o manto em sua mão,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1163, 'nvi', 'Gênesis', 'gn', 39, 14, 'chamou os empregados e lhes disse: "Vejam, este hebreu nos foi trazido para nos insultar! Ele entrou aqui e tentou abusar de mim, mas eu gritei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1164, 'nvi', 'Gênesis', 'gn', 39, 15, 'Quando me ouviu gritar por socorro, largou seu manto ao meu lado e fugiu da casa".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1165, 'nvi', 'Gênesis', 'gn', 39, 16, 'Ela conservou o manto consigo até que o senhor de José chegasse em casa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1166, 'nvi', 'Gênesis', 'gn', 39, 17, 'Então repetiu-lhe a história: "Aquele escravo hebreu que você nos trouxe aproximou-se de mim para me insultar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1167, 'nvi', 'Gênesis', 'gn', 39, 18, 'Mas, quando gritei por socorro, ele largou seu manto ao meu lado e fugiu".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1168, 'nvi', 'Gênesis', 'gn', 39, 19, 'Quando o seu senhor ouviu o que a sua mulher lhe disse: "Foi assim que o seu escravo me tratou", ficou indignado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1169, 'nvi', 'Gênesis', 'gn', 39, 20, 'Mandou buscar José e lançou-o na prisão em que eram postos os prisioneiros do rei. José ficou na prisão,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1170, 'nvi', 'Gênesis', 'gn', 39, 21, 'mas o Senhor estava com ele e o tratou com bondade, concedendo-lhe a simpatia do carcereiro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1171, 'nvi', 'Gênesis', 'gn', 39, 22, 'Por isso o carcereiro encarregou José de todos os que estavam na prisão, e ele se tornou responsável por tudo o que lá sucedia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1172, 'nvi', 'Gênesis', 'gn', 39, 23, 'O carcereiro não se preocupava com nada do que estava a cargo de José, porque o Senhor estava com José e lhe concedia bom êxito em tudo o que realizava.');
