INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (80, 'nvi', 'Gênesis', 'gn', 4, 1, 'Adão teve relações com Eva, sua mulher, e ela engravidou e deu à luz Caim. Disse ela: "Com o auxílio do Senhor tive um filho homem".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (81, 'nvi', 'Gênesis', 'gn', 4, 2, 'Voltou a dar à luz, desta vez a Abel, irmão dele. Abel tornou-se pastor de ovelhas, e Caim, agricultor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (82, 'nvi', 'Gênesis', 'gn', 4, 3, 'Passado algum tempo, Caim trouxe do fruto da terra uma oferta ao Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (83, 'nvi', 'Gênesis', 'gn', 4, 4, 'Abel, por sua vez, trouxe as partes gordas das primeiras crias do seu rebanho. O Senhor aceitou com agrado Abel e sua oferta,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (84, 'nvi', 'Gênesis', 'gn', 4, 5, 'mas não aceitou Caim e sua oferta. Por isso Caim se enfureceu e o seu rosto se transtornou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (85, 'nvi', 'Gênesis', 'gn', 4, 6, 'O Senhor disse a Caim: "Por que você está furioso? Por que se transtornou o seu rosto?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (86, 'nvi', 'Gênesis', 'gn', 4, 7, 'Se você fizer o bem, não será aceito? Mas se não o fizer, saiba que o pecado o ameaça à porta; ele deseja conquistá-lo, mas você deve dominá-lo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (87, 'nvi', 'Gênesis', 'gn', 4, 8, 'Disse, porém, Caim a seu irmão Abel: "Vamos para o campo". Quando estavam lá, Caim atacou seu irmão Abel e o matou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (88, 'nvi', 'Gênesis', 'gn', 4, 9, 'Então o Senhor perguntou a Caim: "Onde está seu irmão Abel? " Respondeu ele: "Não sei; sou eu o responsável por meu irmão? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (89, 'nvi', 'Gênesis', 'gn', 4, 10, 'Disse o Senhor: "O que foi que você fez? Escute! Da terra o sangue do seu irmão está clamando.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (90, 'nvi', 'Gênesis', 'gn', 4, 11, 'Agora amaldiçoado é você pela terra, que abriu a boca para receber da sua mão o sangue do seu irmão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (91, 'nvi', 'Gênesis', 'gn', 4, 12, 'Quando você cultivar a terra, esta não lhe dará mais da sua força. Você será um fugitivo errante pelo mundo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (92, 'nvi', 'Gênesis', 'gn', 4, 13, 'Disse Caim ao Senhor: "Meu castigo é maior do que posso suportar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (93, 'nvi', 'Gênesis', 'gn', 4, 14, 'Hoje me expulsas desta terra, e terei que me esconder da tua face; serei um fugitivo errante pelo mundo, e qualquer que me encontrar me matará".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (94, 'nvi', 'Gênesis', 'gn', 4, 15, 'Mas o Senhor lhe respondeu: "Não será assim; se alguém matar Caim, sofrerá sete vezes a vingança". E o Senhor colocou em Caim um sinal, para que ninguém que viesse a encontrá-lo o matasse.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (95, 'nvi', 'Gênesis', 'gn', 4, 16, 'Então Caim afastou-se da presença do Senhor e foi viver na terra de Node, a leste do Éden.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (96, 'nvi', 'Gênesis', 'gn', 4, 17, 'Caim teve relações com sua mulher, e ela engravidou e deu à luz Enoque. Depois Caim fundou uma cidade, à qual deu o nome do seu filho Enoque.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (97, 'nvi', 'Gênesis', 'gn', 4, 18, 'A Enoque nasceu-lhe Irade, Irade gerou a Meujael, Meujael a Metusael, e Metusael a Lameque.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (98, 'nvi', 'Gênesis', 'gn', 4, 19, 'Lameque tomou duas mulheres: uma chamava-se Ada e a outra, Zilá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (99, 'nvi', 'Gênesis', 'gn', 4, 20, 'Ada deu à luz Jabal, que foi o pai daqueles que moram em tendas e criam rebanhos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (100, 'nvi', 'Gênesis', 'gn', 4, 21, 'O nome do irmão dele era Jubal, que foi o pai de todos os que tocam harpa e flauta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (101, 'nvi', 'Gênesis', 'gn', 4, 22, 'Zilá também deu à luz um filho, Tubalcaim, que fabricava todo tipo de ferramentas de bronze e de ferro. Tubalcaim teve uma irmã chamada Naamá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (102, 'nvi', 'Gênesis', 'gn', 4, 23, 'Disse Lameque às suas mulheres: "Ada e Zilá, ouçam-me; mulheres de Lameque, escutem minhas palavras: Eu matei um homem porque me feriu, e um menino, porque me machucou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (103, 'nvi', 'Gênesis', 'gn', 4, 24, 'Se Caim é vingado sete vezes, Lameque o será setenta e sete".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (104, 'nvi', 'Gênesis', 'gn', 4, 25, 'Novamente Adão teve relações com sua mulher, e ela deu à luz outro filho, a quem chamou Sete, dizendo: "Deus me concedeu um filho no lugar de Abel, visto que Caim o matou".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (105, 'nvi', 'Gênesis', 'gn', 4, 26, 'Também a Sete nasceu um filho, a quem deu o nome de Enos. Nessa época começou-se a invocar o nome do Senhor.');
