INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1173, 'nvi', 'Gênesis', 'gn', 40, 1, 'Algum tempo depois, o copeiro e o padeiro do rei do Egito fizeram uma ofensa ao seu senhor, o rei do Egito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1174, 'nvi', 'Gênesis', 'gn', 40, 2, 'O faraó irou-se com os dois oficiais, o chefe dos copeiros e o chefe dos padeiros,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1175, 'nvi', 'Gênesis', 'gn', 40, 3, 'e mandou prendê-los na casa do capitão da guarda, na prisão em que José estava.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1176, 'nvi', 'Gênesis', 'gn', 40, 4, 'O capitão da guarda os deixou aos cuidados de José, que os servia. Depois de certo tempo,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1177, 'nvi', 'Gênesis', 'gn', 40, 5, 'o copeiro e o padeiro do rei do Egito, que estavam na prisão, sonharam. Cada um teve um sonho, ambos na mesma noite, e cada sonho tinha a sua própria interpretação.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1178, 'nvi', 'Gênesis', 'gn', 40, 6, 'Quando José foi vê-los na manhã seguinte, notou que estavam abatidos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1179, 'nvi', 'Gênesis', 'gn', 40, 7, 'Por isso perguntou aos oficiais do faraó que também estavam presos na casa do seu senhor: "Por que hoje vocês estão com o semblante triste? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1180, 'nvi', 'Gênesis', 'gn', 40, 8, 'Eles responderam: "Tivemos sonhos, mas não há quem os interprete". Disse-lhes José: "Não são de Deus as interpretações? Contem-me os sonhos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1181, 'nvi', 'Gênesis', 'gn', 40, 9, 'Então o chefe dos copeiros contou o seu sonho a José: "Em meu sonho vi diante de mim uma videira,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1182, 'nvi', 'Gênesis', 'gn', 40, 10, 'com três ramos. Ela brotou, floresceu e deu uvas que amadureciam em cachos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1183, 'nvi', 'Gênesis', 'gn', 40, 11, 'A taça do faraó estava em minha mão. Peguei as uvas, e as espremi na taça do faraó, e a entreguei em sua mão".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1184, 'nvi', 'Gênesis', 'gn', 40, 12, 'Disse-lhe José: "Esta é a interpretação: Os três ramos são três dias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1185, 'nvi', 'Gênesis', 'gn', 40, 13, 'Dentro de três dias o faraó vai exaltá-lo e restaurá-lo à sua posição; e você servirá a taça na mão dele, como costumava fazer quando era seu copeiro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1186, 'nvi', 'Gênesis', 'gn', 40, 14, 'Quando tudo estiver indo bem com você, lembre-se de mim e seja bondoso comigo; fale de mim ao faraó e tire-me desta prisão,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1187, 'nvi', 'Gênesis', 'gn', 40, 15, 'pois fui trazido à força da terra dos hebreus, e também aqui nada fiz para ser jogado neste calabouço".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1188, 'nvi', 'Gênesis', 'gn', 40, 16, 'Ouvindo o chefe dos padeiros essa interpretação favorável, disse a José: "Eu também tive um sonho: Sobre a minha cabeça havia três cestas de pão branco.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1189, 'nvi', 'Gênesis', 'gn', 40, 17, 'Na cesta de cima havia todo tipo de pães e doces que o faraó aprecia, mas as aves vinham comer da cesta que eu trazia na cabeça".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1190, 'nvi', 'Gênesis', 'gn', 40, 18, 'E disse José: "Esta é a interpretação: As três cestas são três dias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1191, 'nvi', 'Gênesis', 'gn', 40, 19, 'Dentro de três dias o faraó vai decapitá-lo e pendurá-lo numa árvore. E as aves comerão a sua carne".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1192, 'nvi', 'Gênesis', 'gn', 40, 20, 'Três dias depois era o aniversário do faraó, e ele ofereceu um banquete a todos os seus conselheiros. Na presença deles reapresentou o chefe dos copeiros e o chefe dos padeiros:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1193, 'nvi', 'Gênesis', 'gn', 40, 21, 'Restaurou à sua posição o chefe dos copeiros, de modo que ele voltou a ser aquele que servia a taça do faraó,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1194, 'nvi', 'Gênesis', 'gn', 40, 22, 'mas ao chefe dos padeiros mandou enforcar, como José lhes dissera em sua interpretação.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1195, 'nvi', 'Gênesis', 'gn', 40, 23, 'O chefe dos copeiros, porém, não se lembrou de José; ao contrário, esqueceu-se dele.');
