INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1291, 'nvi', 'Gênesis', 'gn', 43, 1, 'A fome continuava rigorosa na terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1292, 'nvi', 'Gênesis', 'gn', 43, 2, 'Assim, quando acabou todo o trigo que os filhos de Jacó tinham trazido do Egito, seu pai lhes disse: "Voltem e comprem um pouco mais de comida para nós".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1293, 'nvi', 'Gênesis', 'gn', 43, 3, 'Mas Judá lhe disse: "O homem nos advertiu severamente: ‘Não voltem à minha presença, a não ser que tragam o seu irmão’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1294, 'nvi', 'Gênesis', 'gn', 43, 4, 'Se enviares o nosso irmão conosco, desceremos e compraremos comida para ti.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1295, 'nvi', 'Gênesis', 'gn', 43, 5, 'Mas se não o enviares conosco, não iremos, porque foi assim que o homem falou: ‘Não voltem à minha presença, a não ser que tragam o seu irmão’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1296, 'nvi', 'Gênesis', 'gn', 43, 6, 'Israel perguntou: "Por que me causaram esse mal, contando àquele homem que tinham outro irmão? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1297, 'nvi', 'Gênesis', 'gn', 43, 7, 'E lhe responderam: "Ele nos interrogou sobre nós e sobre nossa família. E também nos perguntou: ‘O pai de vocês ainda está vivo? Vocês têm outro irmão? ’ Nós simplesmente respondemos ao que ele nos perguntou. Como poderíamos saber que ele exigiria que levássemos o nosso irmão? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1298, 'nvi', 'Gênesis', 'gn', 43, 8, 'Então disse Judá a Israel, seu pai: "Deixa o jovem ir comigo e partiremos imediatamente, a fim de que tu, nós e nossas crianças sobrevivamos e não venhamos a morrer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1299, 'nvi', 'Gênesis', 'gn', 43, 9, 'Eu me comprometo pessoalmente pela segurança dele; podes me considerar responsável por ele. Se eu não o trouxer de volta e não o colocar bem aqui na tua presença, serei culpado diante de ti pelo resto da minha vida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1300, 'nvi', 'Gênesis', 'gn', 43, 10, 'Como se vê, se não tivéssemos demorado tanto, já teríamos ido e voltado duas vezes".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1301, 'nvi', 'Gênesis', 'gn', 43, 11, 'Então Israel, seu pai, lhes disse: "Se tem que ser assim, que seja! Coloquem alguns dos melhores produtos da nossa terra na bagagem e levem-nos como presente ao tal homem: um pouco de bálsamo, um pouco de mel, algumas especiarias e mirra, algumas nozes de pistache e amêndoas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1302, 'nvi', 'Gênesis', 'gn', 43, 12, 'Levem prata em dobro, e devolvam a prata que foi colocada de volta na boca da bagagem de vocês. Talvez isso tenha acontecido por engano.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1303, 'nvi', 'Gênesis', 'gn', 43, 13, 'Peguem também o seu irmão e voltem àquele homem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1304, 'nvi', 'Gênesis', 'gn', 43, 14, 'Que o Deus Todo-poderoso lhes conceda misericórdia diante daquele homem, para que ele permita que o seu outro irmão e Benjamim voltem com vocês. Quanto a mim, se ficar sem filhos, sem filhos ficarei".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1305, 'nvi', 'Gênesis', 'gn', 43, 15, 'Então os homens desceram ao Egito, levando o presente, prata em dobro e Benjamim, e foram à presença de José.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1306, 'nvi', 'Gênesis', 'gn', 43, 16, 'Quando José viu Benjamim junto com eles, disse ao administrador de sua casa: "Leve estes homens à minha casa, mate um animal e prepare-o; eles almoçarão comigo ao meio-dia".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1307, 'nvi', 'Gênesis', 'gn', 43, 17, 'Ele fez o que lhe fora ordenado e levou-os à casa de José.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1308, 'nvi', 'Gênesis', 'gn', 43, 18, 'Eles ficaram com medo quando foram levados à casa de José, e pensaram: "Trouxeram-nos aqui por causa da prata que foi devolvida às nossas bagagens na primeira vez. Ele quer atacar-nos, subjugar-nos, tornar-nos escravos e tomar de nós os nossos jumentos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1309, 'nvi', 'Gênesis', 'gn', 43, 19, 'Por isso, dirigiram-se ao administrador da casa de José e lhe disseram à entrada da casa:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1310, 'nvi', 'Gênesis', 'gn', 43, 20, '"Ouça, senhor! A primeira vez que viemos aqui foi realmente para comprar comida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1311, 'nvi', 'Gênesis', 'gn', 43, 21, 'Mas no lugar em que paramos para pernoitar, abrimos nossas bagagens e cada um de nós encontrou sua prata, na quantia exata. Por isso a trouxemos de volta conosco,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1312, 'nvi', 'Gênesis', 'gn', 43, 22, 'além de mais prata, para comprar comida. Não sabemos quem pôs a prata em nossa bagagem".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1313, 'nvi', 'Gênesis', 'gn', 43, 23, '"Fiquem tranqüilos", disse o administrador. "Não tenham medo. O seu Deus, o Deus de seu pai, foi quem lhes deu um tesouro em suas bagagens, porque a prata de vocês eu recebi. " Então soltou Simeão e o levou à presença deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1314, 'nvi', 'Gênesis', 'gn', 43, 24, 'Em seguida os levou à casa de José, deu-lhes água para lavarem os pés e forragem para os seus jumentos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1315, 'nvi', 'Gênesis', 'gn', 43, 25, 'Eles então prepararam o presente para a chegada de José ao meio-dia, porque ficaram sabendo que iriam almoçar ali.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1316, 'nvi', 'Gênesis', 'gn', 43, 26, 'Quando José chegou, eles o presentearam com o que tinham trazido e curvaram-se diante dele até o chão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1317, 'nvi', 'Gênesis', 'gn', 43, 27, 'Ele então lhes perguntou como passavam e disse em seguida: "Como vai o pai de vocês, o homem idoso de quem me falaram? Ainda está vivo? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1318, 'nvi', 'Gênesis', 'gn', 43, 28, 'Eles responderam: "Teu servo, nosso pai, ainda vive e passa bem". E se curvaram para prestar-lhe honra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1319, 'nvi', 'Gênesis', 'gn', 43, 29, 'Olhando ao redor e vendo seu irmão Benjamim, filho de sua mãe, José perguntou: "É este o irmão caçula de quem me falaram? " E acrescentou: "Deus lhe conceda graça, meu filho".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1320, 'nvi', 'Gênesis', 'gn', 43, 30, 'Profundamente emocionado por causa de seu irmão, José apressou-se em sair à procura de um lugar para chorar, e entrando em seu quarto, chorou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1321, 'nvi', 'Gênesis', 'gn', 43, 31, 'Depois de lavar o rosto, saiu e, controlando-se, disse: "Sirvam a comida".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1322, 'nvi', 'Gênesis', 'gn', 43, 32, 'Serviram a ele em separado dos seus irmãos e também dos egípcios que comiam com ele, porque os egípcios não podiam comer com os hebreus, pois isso era sacrilégio para eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1323, 'nvi', 'Gênesis', 'gn', 43, 33, 'Seus irmãos foram colocados à mesa perante ele por ordem de idade, do mais velho ao mais moço, e olhavam perplexos uns para os outros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1324, 'nvi', 'Gênesis', 'gn', 43, 34, 'Então lhes serviram da comida da mesa de José, e a porção de Benjamim era cinco vezes maior que a dos outros. E eles festejaram e beberam à vontade.');
