INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1325, 'nvi', 'Gênesis', 'gn', 44, 1, 'José deu as seguintes ordens ao administrador de sua casa: "Encha as bagagens desses homens com todo o mantimento que puderem carregar e coloque a prata de cada um na boca de sua bagagem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1326, 'nvi', 'Gênesis', 'gn', 44, 2, 'Depois coloque a minha taça, a taça de prata, na boca da bagagem do caçula, juntamente com a prata paga pelo trigo". E ele fez tudo conforme as ordens de José.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1327, 'nvi', 'Gênesis', 'gn', 44, 3, 'Assim que despontou a manhã, despediram os homens com os seus jumentos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1328, 'nvi', 'Gênesis', 'gn', 44, 4, 'Ainda não tinham se afastado da cidade, quando José disse ao administrador de sua casa: "Vá atrás daqueles homens e, quando os alcançar, diga-lhes: Por que retribuíram o bem com o mal?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1329, 'nvi', 'Gênesis', 'gn', 44, 5, 'Não é esta a taça que o meu senhor usa para beber e para fazer adivinhações? Vocês cometeram grande maldade! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1330, 'nvi', 'Gênesis', 'gn', 44, 6, 'Quando ele os alcançou, repetiu-lhes essas palavras.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1331, 'nvi', 'Gênesis', 'gn', 44, 7, 'Mas eles lhe responderam: "Por que o meu senhor diz isso? Longe dos seus servos fazer tal coisa!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1332, 'nvi', 'Gênesis', 'gn', 44, 8, 'Nós lhe trouxemos de volta, da terra de Canaã, a prata que encontramos na boca de nossa bagagem. Como roubaríamos prata ou ouro da casa do seu senhor?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1333, 'nvi', 'Gênesis', 'gn', 44, 9, 'Se algum dos seus servos for encontrado com ela, morrerá; e nós, os demais, seremos escravos do meu senhor".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1334, 'nvi', 'Gênesis', 'gn', 44, 10, 'E disse ele: "Concordo. Somente quem for encontrado com ela será meu escravo; os demais estarão livres".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1335, 'nvi', 'Gênesis', 'gn', 44, 11, 'Cada um deles descarregou depressa a sua bagagem e abriu-a.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1336, 'nvi', 'Gênesis', 'gn', 44, 12, 'O administrador começou então a busca, desde a bagagem do mais velho até a do mais novo. E a taça foi encontrada na bagagem de Benjamim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1337, 'nvi', 'Gênesis', 'gn', 44, 13, 'Diante disso, eles rasgaram as suas vestes. Em seguida, todos puseram a carga de novo em seus jumentos e retornaram à cidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1338, 'nvi', 'Gênesis', 'gn', 44, 14, 'Quando Judá e seus irmãos chegaram à casa de José, ele ainda estava lá. Então eles se lançaram ao chão perante ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1339, 'nvi', 'Gênesis', 'gn', 44, 15, 'E José lhes perguntou: "Que foi que vocês fizeram? Vocês não sabem que um homem como eu tem poder para adivinhar? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1340, 'nvi', 'Gênesis', 'gn', 44, 16, 'Respondeu Judá: "O que diremos a meu senhor? Que podemos falar? Como podemos provar nossa inocência? Deus trouxe à luz a culpa dos teus servos. Agora somos escravos do meu senhor, como também aquele que foi encontrado com a taça".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1341, 'nvi', 'Gênesis', 'gn', 44, 17, 'Disse, porém, José: "Longe de mim fazer tal coisa! Somente aquele que foi encontrado com a taça será meu escravo. Os demais podem voltar em paz para a casa do seu pai".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1342, 'nvi', 'Gênesis', 'gn', 44, 18, 'Então Judá dirigiu-se a ele, dizendo: "Por favor, meu senhor, permite-me dizer-te uma palavra. Não se acenda a tua ira contra o teu servo, embora sejas igual ao próprio faraó.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1343, 'nvi', 'Gênesis', 'gn', 44, 19, 'Meu senhor perguntou a estes seus servos se ainda tínhamos pai e algum outro irmão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1344, 'nvi', 'Gênesis', 'gn', 44, 20, 'E nós respondemos: Temos um pai já idoso, cujo filho caçula nasceu-lhe em sua velhice. O irmão deste já morreu, e ele é o único filho da mesma mãe que restou, e seu pai o ama muito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1345, 'nvi', 'Gênesis', 'gn', 44, 21, '"Então disseste a teus servos que o trouxessem a ti para que os teus olhos pudessem vê-lo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1346, 'nvi', 'Gênesis', 'gn', 44, 22, 'E nós respondemos a meu senhor que o jovem não poderia deixar seu pai, pois, caso o fizesse, seu pai morreria.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1347, 'nvi', 'Gênesis', 'gn', 44, 23, 'Todavia disseste a teus servos que se o nosso irmão caçula não viesse conosco, nunca mais veríamos a tua face.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1348, 'nvi', 'Gênesis', 'gn', 44, 24, 'Quando voltamos a teu servo, a meu pai, contamos-lhe o que o meu senhor tinha dito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1349, 'nvi', 'Gênesis', 'gn', 44, 25, '"Quando o nosso pai nos mandou voltar para comprar um pouco mais de comida,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1350, 'nvi', 'Gênesis', 'gn', 44, 26, 'nós lhe dissemos: Só poderemos voltar para lá, se o nosso irmão caçula for conosco. Pois não poderemos ver a face daquele homem, a não ser que o nosso irmão caçula esteja conosco.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1351, 'nvi', 'Gênesis', 'gn', 44, 27, '"Teu servo, meu pai, nos disse então: ‘Vocês sabem que minha mulher me deu apenas dois filhos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1352, 'nvi', 'Gênesis', 'gn', 44, 28, 'Um deles se foi, e eu disse: Com certeza foi despedaçado. E até hoje, nunca mais o vi.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1353, 'nvi', 'Gênesis', 'gn', 44, 29, 'Se agora vocês também levarem este de mim, e algum mal lhe acontecer, a tristeza que me causarão fará com que os meus cabelos brancos desçam à sepultura’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1354, 'nvi', 'Gênesis', 'gn', 44, 30, '"Agora, pois, se eu voltar a teu servo, a meu pai, sem levar o jovem conosco, logo que meu pai, que é tão apegado a ele,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1355, 'nvi', 'Gênesis', 'gn', 44, 31, 'perceber que o jovem não está conosco, morrerá. Teus servos farão seu velho pai descer seus cabelos brancos à sepultura com tristeza.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1356, 'nvi', 'Gênesis', 'gn', 44, 32, '"Além disso, teu servo garantiu a segurança do jovem a seu pai, dizendo-lhe: Se eu não o trouxer de volta, suportarei essa culpa diante de ti pelo resto da minha vida!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1357, 'nvi', 'Gênesis', 'gn', 44, 33, '"Por isso agora te peço, por favor, deixa o teu servo ficar como escravo do meu senhor no lugar do jovem e permite que ele volte com os seus irmãos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1358, 'nvi', 'Gênesis', 'gn', 44, 34, 'Como poderei eu voltar a meu pai sem levar o jovem comigo? Não! Não posso ver o mal que sobreviria a meu pai".');
