INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1359, 'nvi', 'Gênesis', 'gn', 45, 1, 'A essa altura, José já não podia mais conter-se diante de todos os que ali estavam, e gritou: "Façam sair a todos! " Assim, ninguém mais estava presente quando José se revelou a seus irmãos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1360, 'nvi', 'Gênesis', 'gn', 45, 2, 'E ele se pôs a chorar tão alto que os egípcios o ouviram, e a notícia chegou ao palácio do faraó.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1361, 'nvi', 'Gênesis', 'gn', 45, 3, 'Então disse José a seus irmãos: "Eu sou José! Meu pai ainda está vivo? " Mas os seus irmãos ficaram tão pasmados diante dele que não conseguiam responder-lhe.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1362, 'nvi', 'Gênesis', 'gn', 45, 4, '"Cheguem mais perto", disse José a seus irmãos. Quando eles se aproximaram, disse-lhes: "Eu sou José, seu irmão, aquele que vocês venderam ao Egito!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1363, 'nvi', 'Gênesis', 'gn', 45, 5, 'Agora, não se aflijam nem se recriminem por terem me vendido para cá, pois foi para salvar vidas que Deus me enviou adiante de vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1364, 'nvi', 'Gênesis', 'gn', 45, 6, 'Já houve dois anos de fome na terra, e nos próximos cinco anos não haverá cultivo nem colheita.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1365, 'nvi', 'Gênesis', 'gn', 45, 7, 'Mas Deus me enviou à frente de vocês para lhes preservar um remanescente nesta terra e para salvar-lhes as vidas com grande livramento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1366, 'nvi', 'Gênesis', 'gn', 45, 8, '"Assim, não foram vocês que me mandaram para cá, mas sim o próprio Deus. Ele me tornou ministro do faraó, e me fez administrador de todo o palácio e governador de todo o Egito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1367, 'nvi', 'Gênesis', 'gn', 45, 9, 'Voltem depressa a meu pai e digam-lhe: Assim diz o seu filho José: Deus me fez senhor de todo o Egito. Vem para cá, não te demores.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1368, 'nvi', 'Gênesis', 'gn', 45, 10, 'Tu viverás na região de Gósen e ficarás perto de mim — tu, os teus filhos, os teus netos, as tuas ovelhas, os teus bois e todos os teus bens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1369, 'nvi', 'Gênesis', 'gn', 45, 11, 'Eu te sustentarei ali, porque ainda haverá cinco anos de fome. Do contrário, tu, a tua família e todos os teus rebanhos acabarão na miséria.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1370, 'nvi', 'Gênesis', 'gn', 45, 12, '"Vocês estão vendo com os seus próprios olhos, e meu irmão Benjamim também, que realmente sou eu que estou falando com vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1371, 'nvi', 'Gênesis', 'gn', 45, 13, 'Contem a meu pai quanta honra me prestam no Egito e tudo o que vocês mesmos testemunharam. E tragam meu pai para cá depressa".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1372, 'nvi', 'Gênesis', 'gn', 45, 14, 'Então ele se lançou chorando sobre o seu irmão Benjamim e o abraçou, e Benjamim também o abraçou, chorando.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1373, 'nvi', 'Gênesis', 'gn', 45, 15, 'Em seguida beijou todos os seus irmãos e chorou com eles. E só depois os seus irmãos conseguiram conversar com ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1374, 'nvi', 'Gênesis', 'gn', 45, 16, 'Quando se ouviu no palácio do faraó que os irmãos de José haviam chegado, o faraó e todos os seus conselheiros se alegraram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1375, 'nvi', 'Gênesis', 'gn', 45, 17, 'Disse então o faraó a José: "Diga a seus irmãos que ponham as cargas nos seus animais, voltem para a terra de Canaã');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1376, 'nvi', 'Gênesis', 'gn', 45, 18, 'e retornem para cá, trazendo seu pai e suas famílias. Eu lhes darei o melhor da terra do Egito e vocês poderão desfrutar a fartura desta terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1377, 'nvi', 'Gênesis', 'gn', 45, 19, '"Mande-os também levar carruagens do Egito para trazerem as suas mulheres, os seus filhos e seu pai.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1378, 'nvi', 'Gênesis', 'gn', 45, 20, 'Não se preocupem com os seus bens, pois o melhor de todo o Egito será de vocês".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1379, 'nvi', 'Gênesis', 'gn', 45, 21, 'Assim fizeram os filhos de Israel. José lhes providenciou carruagens, como o faraó tinha ordenado, e também mantimentos para a viagem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1380, 'nvi', 'Gênesis', 'gn', 45, 22, 'A cada um deu uma muda de roupa nova, mas a Benjamim deu trezentas peças de prata e cinco mudas de roupa nova.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1381, 'nvi', 'Gênesis', 'gn', 45, 23, 'E a seu pai enviou dez jumentos carregados com o melhor do que havia no Egito e dez jumentas carregadas de trigo, pão e outras provisões para a viagem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1382, 'nvi', 'Gênesis', 'gn', 45, 24, 'Depois despediu-se dos seus irmãos e, ao partirem, disse-lhes: "Não briguem pelo caminho! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1383, 'nvi', 'Gênesis', 'gn', 45, 25, 'Assim partiram do Egito e voltaram a seu pai Jacó, na terra de Canaã,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1384, 'nvi', 'Gênesis', 'gn', 45, 26, 'e lhe deram a notícia: "José ainda está vivo! Na verdade ele é o governador de todo o Egito". O coração de Jacó quase parou! Não podia acreditar neles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1385, 'nvi', 'Gênesis', 'gn', 45, 27, 'Mas, quando lhe relataram tudo o que José lhes dissera, e vendo Jacó, seu pai, as carruagens que José enviara para buscá-lo, seu espírito reviveu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1386, 'nvi', 'Gênesis', 'gn', 45, 28, 'E Israel disse: "Basta! Meu filho José ainda está vivo. Irei vê-lo antes que eu morra".');
