INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1387, 'nvi', 'Gênesis', 'gn', 46, 1, 'Israel partiu com tudo o que lhe pertencia. Ao chegar a Berseba, ofereceu sacrifícios ao Deus de Isaque, seu pai.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1388, 'nvi', 'Gênesis', 'gn', 46, 2, 'E Deus falou a Israel por meio de uma visão noturna: "Jacó! Jacó! " "Eis-me aqui", respondeu ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1389, 'nvi', 'Gênesis', 'gn', 46, 3, '"Eu sou Deus, o Deus de seu pai", disse ele. "Não tenha medo de descer ao Egito, porque lá farei de você uma grande nação.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1390, 'nvi', 'Gênesis', 'gn', 46, 4, 'Eu mesmo descerei ao Egito com você e certamente o trarei de volta. E a mão de José fechará os seus olhos. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1391, 'nvi', 'Gênesis', 'gn', 46, 5, 'Então Jacó partiu de Berseba. Os filhos de Israel levaram seu pai Jacó, seus filhos e as suas mulheres nas carruagens que o faraó tinha enviado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1392, 'nvi', 'Gênesis', 'gn', 46, 6, 'Também levaram os seus rebanhos e os bens que tinham adquirido em Canaã. Assim Jacó foi para o Egito com toda a sua descendência.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1393, 'nvi', 'Gênesis', 'gn', 46, 7, 'Levou consigo para o Egito seus filhos, seus netos, suas filhas e suas netas, isto é, todos os seus descendentes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1394, 'nvi', 'Gênesis', 'gn', 46, 8, 'Estes são os nomes dos israelitas, Jacó e seus descendentes, que foram para o Egito: Rúben, o filho mais velho de Jacó.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1395, 'nvi', 'Gênesis', 'gn', 46, 9, 'Estes foram os filhos de Rúben: Enoque, Palu, Hezrom e Carmi.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1396, 'nvi', 'Gênesis', 'gn', 46, 10, 'Estes foram os filhos de Simeão: Jemuel, Jamim, Oade, Jaquim, Zoar e Saul, filho de uma cananéia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1397, 'nvi', 'Gênesis', 'gn', 46, 11, 'Estes foram os filhos de Levi: Gérson, Coate e Merari.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1398, 'nvi', 'Gênesis', 'gn', 46, 12, 'Estes foram os filhos de Judá: Er, Onã, Selá, Perez e Zerá. Er e Onã morreram na terra de Canaã. Estes foram os filhos de Perez: Hezrom e Hamul.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1399, 'nvi', 'Gênesis', 'gn', 46, 13, 'Estes foram os filhos de Issacar: Tolá, Puá, Jasube e Sinrom.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1400, 'nvi', 'Gênesis', 'gn', 46, 14, 'Estes foram os filhos de Zebulom: Serede, Elom e Jaleel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1401, 'nvi', 'Gênesis', 'gn', 46, 15, 'Foram esses os filhos que Lia deu a Jacó em Padã-Arã, além de sua filha Diná. Seus descendentes eram ao todo trinta e três.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1402, 'nvi', 'Gênesis', 'gn', 46, 16, 'Estes foram os filhos de Gade: Zefom, Hagi, Suni, Esbom, Eri, Arodi e Areli.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1403, 'nvi', 'Gênesis', 'gn', 46, 17, 'Estes foram os filhos de Aser: Imna, Isvá, Isvi e Berias, e a irmã deles, Sera. Estes foram os filhos de Berias: Héber e Malquiel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1404, 'nvi', 'Gênesis', 'gn', 46, 18, 'Foram esses os dezesseis descendentes que Zilpa, serva que Labão tinha dado à sua filha Lia, deu a Jacó.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1405, 'nvi', 'Gênesis', 'gn', 46, 19, 'Estes foram os filhos de Raquel, mulher de Jacó: José e Benjamim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1406, 'nvi', 'Gênesis', 'gn', 46, 20, 'Azenate, filha de Potífera, sacerdote de Om, deu dois filhos a José no Egito: Manassés e Efraim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1407, 'nvi', 'Gênesis', 'gn', 46, 21, 'Estes foram os filhos de Benjamim: Belá, Bequer, Asbel, Gera, Naamã, Eí, Rôs, Mupim, Hupim e Arde.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1408, 'nvi', 'Gênesis', 'gn', 46, 22, 'Foram esses os catorze descendentes que Raquel deu a Jacó.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1409, 'nvi', 'Gênesis', 'gn', 46, 23, 'O filho de Dã foi Husim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1410, 'nvi', 'Gênesis', 'gn', 46, 24, 'Estes foram os filhos de Naftali: Jazeel, Guni, Jezer e Silém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1411, 'nvi', 'Gênesis', 'gn', 46, 25, 'Foram esses os sete descendentes que Bila, serva que Labão tinha dado à sua filha Raquel, deu a Jacó.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1412, 'nvi', 'Gênesis', 'gn', 46, 26, 'Todos os que foram para o Egito com Jacó, todos os seus descendentes, sem contar as mulheres de seus filhos, totalizaram sessenta e seis pessoas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1413, 'nvi', 'Gênesis', 'gn', 46, 27, 'Com mais os dois filhos que nasceram a José no Egito, os membros da família de Jacó que foram para o Egito chegaram a setenta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1414, 'nvi', 'Gênesis', 'gn', 46, 28, 'Ora, Jacó enviou Judá à sua frente a José, para saber como ir a Gósen. Quando lá chegaram,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1415, 'nvi', 'Gênesis', 'gn', 46, 29, 'José, de carruagem pronta, partiu para Gósen para encontrar-se com seu pai Israel. Assim que o viu, correu para abraçá-lo e, abraçado a ele, chorou longamente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1416, 'nvi', 'Gênesis', 'gn', 46, 30, 'Israel disse a José: "Agora já posso morrer, pois vi o seu rosto e sei que você ainda está vivo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1417, 'nvi', 'Gênesis', 'gn', 46, 31, 'Então José disse aos seus irmãos e a toda família de seu pai: "Vou partir e informar ao faraó que os meus irmãos e toda a família de meu pai, que viviam em Canaã, vieram para cá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1418, 'nvi', 'Gênesis', 'gn', 46, 32, 'Direi que os homens são pastores, cuidam de rebanhos, e trouxeram consigo suas ovelhas, seus bois e tudo quanto lhes pertence.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1419, 'nvi', 'Gênesis', 'gn', 46, 33, 'Quando o faraó mandar chamá-los e perguntar: ‘Em que vocês trabalham? ’,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1420, 'nvi', 'Gênesis', 'gn', 46, 34, 'respondam-lhe assim: ‘Teus servos criam rebanhos desde pequenos, como o fizeram nossos antepassados’. Assim lhes será permitido habitar na região de Gósen, pois todos os pastores são desprezados pelos egípcios".');
