INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1474, 'nvi', 'Gênesis', 'gn', 49, 1, 'Então Jacó chamou seus filhos e disse: "Ajuntem-se a meu lado para que eu lhes diga o que lhes acontecerá nos dias que virão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1475, 'nvi', 'Gênesis', 'gn', 49, 2, '"Reúnam-se para ouvir, filhos de Jacó; ouçam o que diz seu pai Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1476, 'nvi', 'Gênesis', 'gn', 49, 3, '"Rúben, você é meu primogênito, minha força, o primeiro sinal do meu vigor, superior em honra, superior em poder.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1477, 'nvi', 'Gênesis', 'gn', 49, 4, 'Turbulento como as águas, já não será superior, porque você subiu à cama de seu pai, ao meu leito, e o desonrou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1478, 'nvi', 'Gênesis', 'gn', 49, 5, '"Simeão e Levi são irmãos; suas espadas são armas de violência.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1479, 'nvi', 'Gênesis', 'gn', 49, 6, 'Que eu não entre no conselho deles, nem participe da sua assembléia, porque em sua ira mataram homens e a bel-prazer aleijaram bois, cortando-lhes o tendão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1480, 'nvi', 'Gênesis', 'gn', 49, 7, 'Maldita seja a sua ira, tão tremenda, e a sua fúria, tão cruel! Eu os dividirei pelas terras de Jacó e os dispersarei em Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1481, 'nvi', 'Gênesis', 'gn', 49, 8, '"Judá, seus irmãos o louvarão, sua mão estará sobre o pescoço dos seus inimigos; os filhos de seu pai se curvarão diante de você.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1482, 'nvi', 'Gênesis', 'gn', 49, 9, 'Judá é um leão novo. Você vem subindo, filho meu, depois de matar a presa. Como um leão, ele se assenta; e deita-se como uma leoa; quem tem coragem de acordá-lo?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1483, 'nvi', 'Gênesis', 'gn', 49, 10, 'O cetro não se apartará de Judá, nem o bastão de comando de seus descendentes, até que venha aquele a quem ele pertence, e a ele as nações obedecerão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1484, 'nvi', 'Gênesis', 'gn', 49, 11, 'Ele amarrará seu jumento a uma videira e o seu jumentinho, ao ramo mais seleto; lavará no vinho as suas roupas, no sangue das uvas, as suas vestimentas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1485, 'nvi', 'Gênesis', 'gn', 49, 12, 'Seus olhos serão mais escuros que o vinho; seus dentes, mais brancos que o leite.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1486, 'nvi', 'Gênesis', 'gn', 49, 13, '"Zebulom morará à beira-mar e se tornará um porto para os navios; suas fronteiras se estenderão até Sidom.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1487, 'nvi', 'Gênesis', 'gn', 49, 14, '"Issacar é um jumento forte, deitado entre as suas cargas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1488, 'nvi', 'Gênesis', 'gn', 49, 15, 'Quando ele perceber como é bom o seu lugar de repouso e como é aprazível a sua terra, curvará seus ombros ao fardo e se submeterá a trabalhos forçados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1489, 'nvi', 'Gênesis', 'gn', 49, 16, '"Dã defenderá o direito do seu povo como qualquer das tribos de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1490, 'nvi', 'Gênesis', 'gn', 49, 17, 'Dã será uma serpente à beira da estrada, uma víbora à margem do caminho, que morde o calcanhar do cavalo e faz cair de costas o seu cavaleiro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1491, 'nvi', 'Gênesis', 'gn', 49, 18, '"Ó Senhor, eu espero a tua libertação!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1492, 'nvi', 'Gênesis', 'gn', 49, 19, '"Gade será atacado por um bando, mas é ele que o atacará e o perseguirá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1493, 'nvi', 'Gênesis', 'gn', 49, 20, '"A mesa de Aser será farta; ele oferecerá manjares de rei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1494, 'nvi', 'Gênesis', 'gn', 49, 21, '"Naftali é uma gazela solta, que por isso faz festa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1495, 'nvi', 'Gênesis', 'gn', 49, 22, '"José é uma árvore frutífera, árvore frutífera à beira de uma fonte, cujos galhos passam por cima do muro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1496, 'nvi', 'Gênesis', 'gn', 49, 23, 'Com rancor arqueiros o atacaram, atirando-lhe flechas com hostilidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1497, 'nvi', 'Gênesis', 'gn', 49, 24, 'Mas o seu arco permaneceu firme, os seus braços fortes, ágeis para atirar, pela mão do Poderoso de Jacó, pelo nome do Pastor, a Rocha de Israel,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1498, 'nvi', 'Gênesis', 'gn', 49, 25, 'pelo Deus de seu pai, que ajuda você, o Todo-poderoso, que o abençoa com bênçãos dos altos céus, bênçãos das profundezas, bênçãos da fertilidade e da fartura.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1499, 'nvi', 'Gênesis', 'gn', 49, 26, 'As bênçãos de seu pai são superiores às bênçãos dos montes antigos, às delícias das colinas eternas. Que todas essas bênçãos repousem sobre a cabeça de José, sobre a fronte daquele que foi separado de entre os seus irmãos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1500, 'nvi', 'Gênesis', 'gn', 49, 27, '"Benjamim é um lobo predador; pela manhã devora a presa e à tarde divide o despojo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1501, 'nvi', 'Gênesis', 'gn', 49, 28, 'São esses os que formaram as doze tribos de Israel, e foi isso que seu pai lhes disse, ao abençoá-los, dando a cada um a bênção que lhe pertencia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1502, 'nvi', 'Gênesis', 'gn', 49, 29, 'A seguir, Jacó deu-lhes estas instruções: "Estou para ser reunido aos meus antepassados. Sepultem-me junto aos meus pais na caverna do campo de Efrom, o hitita,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1503, 'nvi', 'Gênesis', 'gn', 49, 30, 'na caverna do campo de Macpela, perto de Manre, em Canaã, campo que Abraão comprou de Efrom, o hitita, como propriedade para sepultura.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1504, 'nvi', 'Gênesis', 'gn', 49, 31, 'Ali foram sepultados Abraão e Sara, sua mulher, e Isaque e Rebeca, sua mulher; ali também sepultei Lia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1505, 'nvi', 'Gênesis', 'gn', 49, 32, '"Tanto o campo como a caverna que nele está foram comprados dos hititas. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1506, 'nvi', 'Gênesis', 'gn', 49, 33, 'Ao acabar de dar essas instruções a seus filhos, Jacó deitou-se, expirou e foi reunido aos seus antepassados.');
