INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1507, 'nvi', 'Gênesis', 'gn', 50, 1, 'José atirou-se sobre seu pai, chorou sobre ele e o beijou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1508, 'nvi', 'Gênesis', 'gn', 50, 2, 'Em seguida deu ordens aos médicos, que estavam ao seu serviço, que embalsamassem seu pai Israel. E eles o embalsamaram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1509, 'nvi', 'Gênesis', 'gn', 50, 3, 'Levaram quarenta dias completos, pois esse era o tempo para o embalsamamento. E os egípcios choraram sua morte setenta dias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1510, 'nvi', 'Gênesis', 'gn', 50, 4, 'Passados os dias de luto, José disse à corte do faraó: "Se posso contar com a bondade de vocês, falem com o faraó em meu favor. Digam-lhe que');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1511, 'nvi', 'Gênesis', 'gn', 50, 5, 'meu pai fez-me prestar-lhe o seguinte juramento: ‘Estou à beira da morte; sepulte-me no túmulo que preparei para mim na terra de Canaã’. Agora, pois, peçam-lhe que me permita partir e sepultar meu pai; logo depois voltarei".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1512, 'nvi', 'Gênesis', 'gn', 50, 6, 'Respondeu o faraó: "Vá e faça o sepultamento de seu pai como este o fez jurar".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1513, 'nvi', 'Gênesis', 'gn', 50, 7, 'Então José partiu para sepultar seu pai. Com ele foram todos os conselheiros do faraó, as autoridades da sua corte e todas as autoridades do Egito,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1514, 'nvi', 'Gênesis', 'gn', 50, 8, 'e, além deles, todos os da família de José, os seus irmãos e todos os da casa de seu pai. Somente as crianças, as ovelhas e os bois foram deixados em Gósen.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1515, 'nvi', 'Gênesis', 'gn', 50, 9, 'Carruagens e cavaleiros também o acompanharam. A comitiva era imensa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1516, 'nvi', 'Gênesis', 'gn', 50, 10, 'Chegando à eira de Atade, perto do Jordão, lamentaram em alta voz, com grande amargura; e ali José guardou sete dias de pranto pela morte do seu pai.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1517, 'nvi', 'Gênesis', 'gn', 50, 11, 'Quando os cananeus que lá habitavam viram aquele pranto na eira de Atade, disseram: "Os egípcios estão celebrando uma cerimônia de luto solene". Por essa razão, aquele lugar, próximo ao Jordão, foi chamado Abel Mizraim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1518, 'nvi', 'Gênesis', 'gn', 50, 12, 'Assim fizeram os filhos de Jacó o que este lhes havia ordenado:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1519, 'nvi', 'Gênesis', 'gn', 50, 13, 'Levaram-no à terra de Canaã e o sepultaram na caverna do campo de Macpela, perto de Manre, que Abraão havia comprado de Efrom, o hitita, para que lhe servisse de propriedade para sepultura, juntamente com o campo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1520, 'nvi', 'Gênesis', 'gn', 50, 14, 'Depois de sepultar seu pai, José voltou ao Egito, com os seus irmãos e com todos os demais que o tinham acompanhado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1521, 'nvi', 'Gênesis', 'gn', 50, 15, 'Vendo os irmãos de José que seu pai havia morrido, disseram: "E se José guardar rancor contra nós e resolver retribuir todo o mal que lhe causamos? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1522, 'nvi', 'Gênesis', 'gn', 50, 16, 'Então mandaram um recado a José, dizendo: "Antes de morrer, teu pai nos ordenou');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1523, 'nvi', 'Gênesis', 'gn', 50, 17, 'que te disséssemos o seguinte: ‘Peço-lhe que perdoe os erros e pecados de seus irmãos que o trataram com tanta maldade! ’ Agora, pois, perdoa os pecados dos servos do Deus do teu pai". Quando recebeu o recado, José chorou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1524, 'nvi', 'Gênesis', 'gn', 50, 18, 'Depois vieram seus irmãos, prostraram-se diante dele e disseram: "Aqui estamos. Somos teus escravos! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1525, 'nvi', 'Gênesis', 'gn', 50, 19, 'José, porém, lhes disse: "Não tenham medo. Estaria eu no lugar de Deus?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1526, 'nvi', 'Gênesis', 'gn', 50, 20, 'Vocês planejaram o mal contra mim, mas Deus o tornou em bem, para que hoje fosse preservada a vida de muitos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1527, 'nvi', 'Gênesis', 'gn', 50, 21, 'Por isso, não tenham medo. Eu sustentarei vocês e seus filhos". E assim os tranqüilizou e lhes falou amavelmente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1528, 'nvi', 'Gênesis', 'gn', 50, 22, 'José permaneceu no Egito, com toda a família de seu pai. Viveu cento e dez anos');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1529, 'nvi', 'Gênesis', 'gn', 50, 23, 'e viu a terceira geração dos filhos de Efraim. Além disso, recebeu como seus os filhos de Maquir, filho de Manassés.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1530, 'nvi', 'Gênesis', 'gn', 50, 24, 'Antes de morrer José disse a seus irmãos: "Estou à beira da morte. Mas Deus certamente virá em auxílio de vocês e os tirará desta terra, levando-os para a terra que prometeu com juramento a Abraão, a Isaque e a Jacó".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1531, 'nvi', 'Gênesis', 'gn', 50, 25, 'E José fez que os filhos de Israel lhe prestassem um juramento, dizendo-lhes: "Quando Deus intervier em favor de vocês, levem os meus ossos daqui".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1532, 'nvi', 'Gênesis', 'gn', 50, 26, 'Morreu José com a idade de cento e dez anos. E, depois de embalsamado, foi colocado num sarcófago no Egito.');
