INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (138, 'nvi', 'Gênesis', 'gn', 6, 1, 'Quando os homens começaram a multiplicar-se na terra e lhes nasceram filhas,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (139, 'nvi', 'Gênesis', 'gn', 6, 2, 'os filhos de Deus viram que as filhas dos homens eram bonitas e escolheram para si aquelas que lhes agradaram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (140, 'nvi', 'Gênesis', 'gn', 6, 3, 'Então disse o Senhor: "Por causa da perversidade do homem, meu Espírito não contenderá com ele para sempre; e ele só viverá cento e vinte anos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (141, 'nvi', 'Gênesis', 'gn', 6, 4, 'Naqueles dias havia nefilins na terra, e também posteriormente, quando os filhos de Deus possuíram as filhas dos homens e elas lhes deram filhos. Eles foram os heróis do passado, homens famosos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (142, 'nvi', 'Gênesis', 'gn', 6, 5, 'O Senhor viu que a perversidade do homem tinha aumentado na terra e que toda a inclinação dos pensamentos do seu coração era sempre e somente para o mal.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (143, 'nvi', 'Gênesis', 'gn', 6, 6, 'Então o Senhor arrependeu-se de ter feito o homem sobre a terra; e isso cortou-lhe o coração.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (144, 'nvi', 'Gênesis', 'gn', 6, 7, 'Disse o Senhor: "Farei desaparecer da face da terra o homem que criei, os homens e também os animais grandes, os animais pequenos e as aves do céu. Arrependo-me de havê-los feito".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (145, 'nvi', 'Gênesis', 'gn', 6, 8, 'A Noé, porém, o Senhor mostrou benevolência.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (146, 'nvi', 'Gênesis', 'gn', 6, 9, 'Esta é a história da família de Noé: Noé era homem justo, íntegro entre o povo da sua época; ele andava com Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (147, 'nvi', 'Gênesis', 'gn', 6, 10, 'Noé gerou três filhos: Sem, Cam e Jafé.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (148, 'nvi', 'Gênesis', 'gn', 6, 11, 'Ora, a terra estava corrompida aos olhos de Deus e cheia de violência.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (149, 'nvi', 'Gênesis', 'gn', 6, 12, 'Ao ver como a terra se corrompera, pois toda a humanidade havia corrompido a sua conduta,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (150, 'nvi', 'Gênesis', 'gn', 6, 13, 'Deus disse a Noé: "Darei fim a todos os seres humanos, porque a terra encheu-se de violência por causa deles. Eu os destruirei juntamente com a terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (151, 'nvi', 'Gênesis', 'gn', 6, 14, 'Você, porém, fará uma arca de madeira de cipreste; divida-a em compartimentos e revista-a de piche por dentro e por fora.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (152, 'nvi', 'Gênesis', 'gn', 6, 15, 'Faça-a com cento e trinta e cinco metros de comprimento, vinte e dois metros e meio de largura e treze metros e meio de altura.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (153, 'nvi', 'Gênesis', 'gn', 6, 16, 'Faça-lhe um teto com um vão de quarenta e cinco centímetros entre o teto e corpo da arca. Coloque uma porta lateral na arca e faça um andar superior, um médio e um inferior.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (154, 'nvi', 'Gênesis', 'gn', 6, 17, '"Eis que vou trazer águas sobre a terra, o Dilúvio, para destruir debaixo do céu toda criatura que tem fôlego de vida. Tudo o que há na terra perecerá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (155, 'nvi', 'Gênesis', 'gn', 6, 18, 'Mas com você estabelecerei a minha aliança, e você entrará na arca com seus filhos, sua mulher e as mulheres de seus filhos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (156, 'nvi', 'Gênesis', 'gn', 6, 19, 'Faça entrar na arca um casal de cada um dos seres vivos, macho e fêmea, para conservá-los vivos com você.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (157, 'nvi', 'Gênesis', 'gn', 6, 20, 'De cada espécie de ave, de cada espécie de animal grande e de cada espécie de animal pequeno que se move rente ao chão virá um casal a você para que sejam conservados vivos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (158, 'nvi', 'Gênesis', 'gn', 6, 21, 'E armazene todo tipo de alimento, para que você e eles tenham mantimento".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (159, 'nvi', 'Gênesis', 'gn', 6, 22, 'Noé fez tudo exatamente como Deus lhe tinha ordenado.');
