INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (160, 'nvi', 'Gênesis', 'gn', 7, 1, 'Então o Senhor disse a Noé: "Entre na arca, você e toda a sua família, porque você é o único justo que encontrei nesta geração.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (161, 'nvi', 'Gênesis', 'gn', 7, 2, 'Leve com você sete casais de cada espécie de animal puro, macho e fêmea, e um casal de cada espécie de animal impuro, macho e fêmea,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (162, 'nvi', 'Gênesis', 'gn', 7, 3, 'e leve também sete casais de aves de cada espécie, macho e fêmea, a fim de preservá-las em toda a terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (163, 'nvi', 'Gênesis', 'gn', 7, 4, 'Daqui a sete dias farei chover sobre a terra quarenta dias e quarenta noites, e farei desaparecer da face da terra todos os seres vivos que fiz".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (164, 'nvi', 'Gênesis', 'gn', 7, 5, 'E Noé fez tudo como o Senhor lhe tinha ordenado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (165, 'nvi', 'Gênesis', 'gn', 7, 6, 'Noé tinha seiscentos anos de idade quando as águas do Dilúvio vieram sobre a terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (166, 'nvi', 'Gênesis', 'gn', 7, 7, 'Noé, seus filhos, sua mulher e as mulheres de seus filhos entraram na arca, por causa das águas do Dilúvio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (167, 'nvi', 'Gênesis', 'gn', 7, 8, 'Casais de animais grandes, puros e impuros, de aves e de todos os animais pequenos que se movem rente ao chão');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (168, 'nvi', 'Gênesis', 'gn', 7, 9, 'vieram a Noé e entraram na arca, como Deus tinha ordenado a Noé.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (169, 'nvi', 'Gênesis', 'gn', 7, 10, 'E depois dos sete dias, as águas do Dilúvio vieram sobre a terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (170, 'nvi', 'Gênesis', 'gn', 7, 11, 'No dia em que Noé completou seiscentos anos, um mês e dezessete dias, nesse mesmo dia todas as fontes das grandes profundezas jorraram, e as comportas do céu se abriram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (171, 'nvi', 'Gênesis', 'gn', 7, 12, 'E a chuva caiu sobre a terra quarenta dias e quarenta noites.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (172, 'nvi', 'Gênesis', 'gn', 7, 13, 'Naquele mesmo dia, Noé e seus filhos, Sem, Cam e Jafé, com sua mulher e com as mulheres de seus três filhos, entraram na arca.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (173, 'nvi', 'Gênesis', 'gn', 7, 14, 'Com eles entraram todos os animais de acordo com as suas espécies: todos os animais selvagens, todos os rebanhos domésticos, todos os demais seres vivos que se movem rente ao chão e todas as criaturas que têm asas: todas as aves e todos os outros animais que voam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (174, 'nvi', 'Gênesis', 'gn', 7, 15, 'Casais de todas as criaturas que tinham fôlego de vida vieram a Noé e entraram na arca.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (175, 'nvi', 'Gênesis', 'gn', 7, 16, 'Os animais que entraram foram um macho e uma fêmea de cada ser vivo, conforme Deus ordenara a Noé. Então o Senhor fechou a porta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (176, 'nvi', 'Gênesis', 'gn', 7, 17, 'Quarenta dias durou o Dilúvio sobre a terra, e as águas aumentaram e elevaram a arca acima da terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (177, 'nvi', 'Gênesis', 'gn', 7, 18, 'As águas prevaleceram, aumentando muito sobre a terra, e a arca flutuava na superfície das águas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (178, 'nvi', 'Gênesis', 'gn', 7, 19, 'As águas dominavam cada vez mais a terra, e foram cobertas todas as altas montanhas debaixo do céu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (179, 'nvi', 'Gênesis', 'gn', 7, 20, 'As águas subiram até quase sete metros acima das montanhas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (180, 'nvi', 'Gênesis', 'gn', 7, 21, 'Todos os seres vivos que se movem sobre a terra pereceram: aves, rebanhos domésticos, animais selvagens, todas as pequenas criaturas que povoam a terra e toda a humanidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (181, 'nvi', 'Gênesis', 'gn', 7, 22, 'Tudo o que havia em terra seca e tinha nas narinas o fôlego de vida morreu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (182, 'nvi', 'Gênesis', 'gn', 7, 23, 'Todos os seres vivos foram exterminados da face da terra; tanto os homens, como os animais grandes, os animais pequenos que se movem rente ao chão e as aves do céu foram exterminados da terra. Só restaram Noé e aqueles que com ele estavam na arca.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (183, 'nvi', 'Gênesis', 'gn', 7, 24, 'E as águas prevaleceram sobre a terra cento e cinqüenta dias.');
