INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (206, 'nvi', 'Gênesis', 'gn', 9, 1, 'Deus abençoou Noé e seus filhos, dizendo-lhes: "Sejam férteis, multipliquem-se e encham a terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (207, 'nvi', 'Gênesis', 'gn', 9, 2, 'Todos os animais da terra tremerão de medo diante de vocês: os animais selvagens, as aves do céu, as criaturas que se movem rente ao chão e os peixes do mar; eles estão entregues em suas mãos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (208, 'nvi', 'Gênesis', 'gn', 9, 3, 'Tudo o que vive e se move lhes servirá de alimento. Assim como lhes dei os vegetais, agora lhes dou todas as coisas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (209, 'nvi', 'Gênesis', 'gn', 9, 4, '"Mas não comam carne com sangue, que é vida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (210, 'nvi', 'Gênesis', 'gn', 9, 5, 'A todo que derramar sangue, tanto homem como animal, pedirei contas; a cada um pedirei contas da vida do seu próximo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (211, 'nvi', 'Gênesis', 'gn', 9, 6, '"Quem derramar sangue do homem, pelo homem seu sangue será derramado; porque à imagem de Deus foi o homem criado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (212, 'nvi', 'Gênesis', 'gn', 9, 7, '"Mas vocês, sejam férteis e multipliquem-se; espalhem-se pela terra e proliferem nela".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (213, 'nvi', 'Gênesis', 'gn', 9, 8, 'Então disse Deus a Noé e a seus filhos, que estavam com ele:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (214, 'nvi', 'Gênesis', 'gn', 9, 9, '"Vou estabelecer a minha aliança com vocês e com os seus futuros descendentes,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (215, 'nvi', 'Gênesis', 'gn', 9, 10, 'e com todo ser vivo que está com vocês: as aves, os rebanhos domésticos e os animais selvagens, todos os que saíram da arca com vocês, todos os seres vivos da terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (216, 'nvi', 'Gênesis', 'gn', 9, 11, 'Estabeleço uma aliança com vocês: Nunca mais será ceifada nenhuma forma de vida pelas águas de um dilúvio; nunca mais haverá dilúvio para destruir a terra".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (217, 'nvi', 'Gênesis', 'gn', 9, 12, 'E Deus prosseguiu: "Este é o sinal da aliança que estou fazendo entre mim e vocês e com todos os seres vivos que estão com vocês, para todas as gerações futuras:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (218, 'nvi', 'Gênesis', 'gn', 9, 13, 'o meu arco que coloquei nas nuvens. Será o sinal da minha aliança com a terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (219, 'nvi', 'Gênesis', 'gn', 9, 14, 'Quando eu trouxer nuvens sobre a terra e nelas aparecer o arco-íris,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (220, 'nvi', 'Gênesis', 'gn', 9, 15, 'então me lembrarei da minha aliança com vocês e com os seres vivos de todas as espécies. Nunca mais as águas se tornarão um dilúvio para destruir toda forma de vida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (221, 'nvi', 'Gênesis', 'gn', 9, 16, 'Toda vez que o arco-íris estiver nas nuvens, olharei para ele e me lembrarei da aliança eterna entre Deus e todos os seres vivos de todas as espécies que vivem na terra".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (222, 'nvi', 'Gênesis', 'gn', 9, 17, 'Concluindo, disse Deus a Noé: "Esse é o sinal da aliança que estabeleci entre mim e toda forma de vida que há sobre a terra".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (223, 'nvi', 'Gênesis', 'gn', 9, 18, 'Os filhos de Noé que saíram da arca foram Sem, Cam e Jafé. Cam é o pai de Canaã.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (224, 'nvi', 'Gênesis', 'gn', 9, 19, 'Esses foram os três filhos de Noé; a partir deles toda a terra foi povoada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (225, 'nvi', 'Gênesis', 'gn', 9, 20, 'Noé, que era agricultor, foi o primeiro a plantar uma vinha.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (226, 'nvi', 'Gênesis', 'gn', 9, 21, 'Bebeu do vinho, embriagou-se e ficou nu dentro da sua tenda.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (227, 'nvi', 'Gênesis', 'gn', 9, 22, 'Cam, pai de Canaã, viu a nudez do pai e foi contar aos dois irmãos que estavam do lado de fora.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (228, 'nvi', 'Gênesis', 'gn', 9, 23, 'Mas Sem e Jafé pegaram a capa, levantaram-na sobre os ombros e, andando de costas para não verem a nudez do pai, cobriram-no.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (229, 'nvi', 'Gênesis', 'gn', 9, 24, 'Quando Noé acordou do efeito do vinho e descobriu o que seu filho caçula lhe havia feito,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (230, 'nvi', 'Gênesis', 'gn', 9, 25, 'disse: "Maldito seja Canaã! Escravo de escravos será para os seus irmãos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (231, 'nvi', 'Gênesis', 'gn', 9, 26, 'Disse ainda: "Bendito seja o Senhor, o Deus de Sem! Seja Canaã seu escravo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (232, 'nvi', 'Gênesis', 'gn', 9, 27, 'Amplie Deus o território de Jafé; habite ele nas tendas de Sem, e seja Canaã seu escravo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (233, 'nvi', 'Gênesis', 'gn', 9, 28, 'Depois do Dilúvio Noé viveu trezentos e cinqüenta anos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (234, 'nvi', 'Gênesis', 'gn', 9, 29, 'Viveu ao todo novecentos e cinqüenta anos e morreu.');
