INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30135, 'nvi', 'Hebreus', 'hb', 10, 1, 'A Lei traz apenas uma sombra dos benefícios que hão de vir, e não a realidade dos mesmos. Por isso ela nunca consegue, mediante os mesmos sacrifícios repetidos ano após ano, aperfeiçoar os que se aproximam para adorar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30136, 'nvi', 'Hebreus', 'hb', 10, 2, 'Se pudesse fazê-lo, não deixariam de ser oferecidos? Pois os adoradores, tendo sido purificados uma vez por todas, não mais se sentiriam culpados de seus pecados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30137, 'nvi', 'Hebreus', 'hb', 10, 3, 'Contudo, esses sacrifícios são uma recordação anual dos pecados,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30138, 'nvi', 'Hebreus', 'hb', 10, 4, 'pois é impossível que o sangue de touros e bodes tire pecados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30139, 'nvi', 'Hebreus', 'hb', 10, 5, 'Por isso, quando Cristo veio ao mundo, disse: "Sacrifício e oferta não quiseste, mas um corpo me preparaste;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30140, 'nvi', 'Hebreus', 'hb', 10, 6, 'de holocaustos e ofertas pelo pecado não te agradaste".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30141, 'nvi', 'Hebreus', 'hb', 10, 7, 'Então eu disse: Aqui estou, no livro está escrito a meu respeito; vim para fazer a tua vontade, ó Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30142, 'nvi', 'Hebreus', 'hb', 10, 8, 'Primeiro ele disse: "Sacrifícios, ofertas, holocaustos e ofertas pelo pecado não quiseste, nem deles te agradaste" ( os quais eram feitos conforme a Lei ).');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30143, 'nvi', 'Hebreus', 'hb', 10, 9, 'Então acrescentou: "Aqui estou; vim para fazer a tua vontade". Ele cancela o primeiro para estabelecer o segundo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30144, 'nvi', 'Hebreus', 'hb', 10, 10, 'Pelo cumprimento dessa vontade fomos santificados, por meio do sacrifício do corpo de Jesus Cristo, oferecido uma vez por todas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30145, 'nvi', 'Hebreus', 'hb', 10, 11, 'Dia após dia, todo sacerdote apresenta-se e exerce os seus deveres religiosos; repetidamente oferece os mesmos sacrifícios, que nunca podem remover os pecados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30146, 'nvi', 'Hebreus', 'hb', 10, 12, 'Mas quando este sacerdote acabou de oferecer, para sempre, um único sacrifício pelos pecados, assentou-se à direita de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30147, 'nvi', 'Hebreus', 'hb', 10, 13, 'Daí em diante, ele está esperando até que os seus inimigos sejam colocados como estrado dos seus pés;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30148, 'nvi', 'Hebreus', 'hb', 10, 14, 'porque, por meio de um único sacrifício, ele aperfeiçoou para sempre os que estão sendo santificados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30149, 'nvi', 'Hebreus', 'hb', 10, 15, 'O Espírito Santo também nos testifica a este respeito. Primeiro ele diz:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30150, 'nvi', 'Hebreus', 'hb', 10, 16, '"Esta é a aliança que farei com eles, depois daqueles dias, diz o Senhor. Porei as minhas leis em seus corações e as escreverei em suas mentes";');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30151, 'nvi', 'Hebreus', 'hb', 10, 17, 'e acrescenta: "Dos seus pecados e iniqüidades não me lembrarei mais".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30152, 'nvi', 'Hebreus', 'hb', 10, 18, 'Onde essas coisas foram perdoadas, não há mais necessidade de sacrifício pelo pecado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30153, 'nvi', 'Hebreus', 'hb', 10, 19, 'Portanto, irmãos, temos plena confiança para entrar no Santo dos Santos pelo sangue de Jesus,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30154, 'nvi', 'Hebreus', 'hb', 10, 20, 'por um novo e vivo caminho que ele nos abriu por meio do véu, isto é, do seu corpo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30155, 'nvi', 'Hebreus', 'hb', 10, 21, 'Temos, pois, um grande sacerdote sobre a casa de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30156, 'nvi', 'Hebreus', 'hb', 10, 22, 'Sendo assim, aproximemo-nos de Deus com um coração sincero e com plena convicção de fé, tendo os corações aspergidos para nos purificar de uma consciência culpada e tendo os nossos corpos lavados com água pura.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30157, 'nvi', 'Hebreus', 'hb', 10, 23, 'Apeguemo-nos com firmeza à esperança que professamos, pois aquele que prometeu é fiel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30158, 'nvi', 'Hebreus', 'hb', 10, 24, 'E consideremo-nos uns aos outros para incentivar-nos ao amor e às boas obras.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30159, 'nvi', 'Hebreus', 'hb', 10, 25, 'Não deixemos de reunir-nos como igreja, segundo o costume de alguns, mas encorajemo-nos uns aos outros, ainda mais quando vocês vêem que se aproxima o Dia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30160, 'nvi', 'Hebreus', 'hb', 10, 26, 'Se continuarmos a pecar deliberadamente depois que recebemos o conhecimento da verdade, já não resta sacrifício pelos pecados,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30161, 'nvi', 'Hebreus', 'hb', 10, 27, 'mas tão-somente uma terrível expectativa de juízo e de fogo intenso que consumirá os inimigos de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30162, 'nvi', 'Hebreus', 'hb', 10, 28, 'Quem rejeitava a lei de Moisés morria sem misericórdia pelo depoimento de duas ou três testemunhas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30163, 'nvi', 'Hebreus', 'hb', 10, 29, 'Quão mais severo castigo, julgam vocês, merece aquele que pisou aos pés o Filho de Deus, que profanou o sangue da aliança pelo qual ele foi santificado, e insultou o Espírito da graça?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30164, 'nvi', 'Hebreus', 'hb', 10, 30, 'Pois conhecemos aquele que disse: "A mim pertence a vingança; eu retribuirei"; e outra vez: "O Senhor julgará o seu povo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30165, 'nvi', 'Hebreus', 'hb', 10, 31, 'Terrível coisa é cair nas mãos do Deus vivo!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30166, 'nvi', 'Hebreus', 'hb', 10, 32, 'Lembrem-se dos primeiros dias, depois que vocês foram iluminados, quando suportaram muita luta e muito sofrimento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30167, 'nvi', 'Hebreus', 'hb', 10, 33, 'Algumas vezes vocês foram expostos a insultos e tribulações; em outras ocasiões fizeram-se solidários com os que assim foram tratados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30168, 'nvi', 'Hebreus', 'hb', 10, 34, 'Vocês se compadeceram dos que estavam na prisão e aceitaram alegremente o confisco dos próprios bens, pois sabiam que possuíam bens superiores e permanentes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30169, 'nvi', 'Hebreus', 'hb', 10, 35, 'Por isso, não abram mão da confiança que vocês têm; ela será ricamente recompensada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30170, 'nvi', 'Hebreus', 'hb', 10, 36, 'Vocês precisam perseverar, de modo que, quando tiverem feito a vontade de Deus, recebam o que ele prometeu;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30171, 'nvi', 'Hebreus', 'hb', 10, 37, 'pois em breve, muito em breve "Aquele que vem virá, e não demorará.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30172, 'nvi', 'Hebreus', 'hb', 10, 38, 'Mas o meu justo viverá pela fé. E, se retroceder, não me agradarei dele".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30173, 'nvi', 'Hebreus', 'hb', 10, 39, 'Nós, porém, não somos dos que retrocedem e são destruídos, mas dos que crêem e são salvos.');
