INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30243, 'nvi', 'Hebreus', 'hb', 13, 1, 'Seja constante o amor fraternal.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30244, 'nvi', 'Hebreus', 'hb', 13, 2, 'Não se esqueçam da hospitalidade; foi praticando-a que, sem o saber alguns acolheram anjos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30245, 'nvi', 'Hebreus', 'hb', 13, 3, 'Lembrem-se dos que estão na prisão, como se aprisionados com eles; dos que estão sendo maltratados, como se fossem vocês mesmos que o estivessem sofrendo no corpo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30246, 'nvi', 'Hebreus', 'hb', 13, 4, 'O casamento deve ser honrado por todos; o leito conjugal, conservado puro; pois Deus julgará os imorais e os adúlteros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30247, 'nvi', 'Hebreus', 'hb', 13, 5, 'Conservem-se livres do amor ao dinheiro e contentem-se com o que vocês têm, porque Deus mesmo disse: "Nunca o deixarei, nunca o abandonarei".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30248, 'nvi', 'Hebreus', 'hb', 13, 6, 'Podemos, pois, dizer com confiança: "O Senhor é o meu ajudador, não temerei. O que me podem fazer os homens? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30249, 'nvi', 'Hebreus', 'hb', 13, 7, 'Lembrem-se dos seus líderes, que lhes falaram a palavra de Deus. Observem bem o resultado da vida que tiveram e imitem a sua fé.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30250, 'nvi', 'Hebreus', 'hb', 13, 8, 'Jesus Cristo é o mesmo, ontem, hoje e para sempre.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30251, 'nvi', 'Hebreus', 'hb', 13, 9, 'Não se deixem levar pelos diversos ensinos estranhos. É bom que o nosso coração seja fortalecido pela graça, e não por alimentos cerimoniais, os quais não têm valor para aqueles que os comem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30252, 'nvi', 'Hebreus', 'hb', 13, 10, 'Nós temos um altar do qual não têm direito de comer os que ministram no tabernáculo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30253, 'nvi', 'Hebreus', 'hb', 13, 11, 'O sumo sacerdote leva sangue de animais até o Santo dos Santos, como oferta pelo pecado, mas os corpos dos animais são queimados fora do acampamento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30254, 'nvi', 'Hebreus', 'hb', 13, 12, 'Assim, Jesus também sofreu fora das portas da cidade, para santificar o povo por meio do seu próprio sangue.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30255, 'nvi', 'Hebreus', 'hb', 13, 13, 'Portanto, saiamos até ele, fora do acampamento, suportando a desonra que ele suportou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30256, 'nvi', 'Hebreus', 'hb', 13, 14, 'Pois não temos aqui nenhuma cidade permanente, mas buscamos a que há de vir.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30257, 'nvi', 'Hebreus', 'hb', 13, 15, 'Por meio de Jesus, portanto, ofereçamos continuamente a Deus um sacrifício de louvor, que é fruto de lábios que confessam o seu nome.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30258, 'nvi', 'Hebreus', 'hb', 13, 16, 'Não se esqueçam de fazer o bem e de repartir com os outros o que vocês têm, pois de tais sacrifícios Deus se agrada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30259, 'nvi', 'Hebreus', 'hb', 13, 17, 'Obedeçam aos seus líderes e submetam-se à autoridade deles. Eles cuidam de vocês como quem deve prestar contas. Obedeçam-lhes, para que o trabalho deles seja uma alegria e não um peso, pois isso não seria proveitoso para vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30260, 'nvi', 'Hebreus', 'hb', 13, 18, 'Orem por nós. Estamos certos de que temos consciência limpa, e desejamos viver de maneira honrosa em tudo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30261, 'nvi', 'Hebreus', 'hb', 13, 19, 'Particularmente recomendo-lhes que orem para que eu lhes seja restituído em breve.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30262, 'nvi', 'Hebreus', 'hb', 13, 20, 'O Deus da paz, que pelo sangue da aliança eterna trouxe de volta dentre os mortos a nosso Senhor Jesus, o grande Pastor das ovelhas,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30263, 'nvi', 'Hebreus', 'hb', 13, 21, 'os aperfeiçoe em todo o bem para fazerem a vontade dele, e opere em nós o que lhe é agradável, mediante Jesus Cristo, a quem seja a glória para todo o sempre. Amém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30264, 'nvi', 'Hebreus', 'hb', 13, 22, 'Irmãos, peço-lhes que suportem a minha palavra de exortação; na verdade o que eu lhes escrevi é pouco.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30265, 'nvi', 'Hebreus', 'hb', 13, 23, 'Quero que saibam que o nosso irmão Timóteo foi posto em liberdade. Se ele chegar logo, irei vê-los com ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30266, 'nvi', 'Hebreus', 'hb', 13, 24, 'Saúdem a todos os seus líderes e a todos os santos. Os da Itália lhes enviam saudações.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30267, 'nvi', 'Hebreus', 'hb', 13, 25, 'A graça seja com todos vocês.');
