INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29979, 'nvi', 'Hebreus', 'hb', 2, 1, 'Por isso é preciso que prestemos maior atenção ao que temos ouvido, para que jamais nos desviemos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29980, 'nvi', 'Hebreus', 'hb', 2, 2, 'Porque se a mensagem transmitida por anjos provou a sua firmeza, e toda transgressão e desobediência recebeu a devida punição,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29981, 'nvi', 'Hebreus', 'hb', 2, 3, 'como escaparemos nós, se negligenciarmos tão grande salvação? Esta salvação, primeiramente anunciada pelo Senhor, foi-nos confirmada pelos que a ouviram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29982, 'nvi', 'Hebreus', 'hb', 2, 4, 'Deus também deu testemunho dela por meio de sinais, maravilhas, diversos milagres e dons do Espírito Santo distribuídos de acordo com a sua vontade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29983, 'nvi', 'Hebreus', 'hb', 2, 5, 'Não foi a anjos que ele sujeitou o mundo que há de vir, a respeito do qual estamos falando,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29984, 'nvi', 'Hebreus', 'hb', 2, 6, 'mas alguém em certo lugar testemunhou, dizendo: "Que é o homem, para que com ele te importes? E o filho do homem, para que com ele te preocupes?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29985, 'nvi', 'Hebreus', 'hb', 2, 7, 'Tu o fizeste um pouco menor do que os anjos e o coroaste de glória e de honra;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29986, 'nvi', 'Hebreus', 'hb', 2, 8, 'tudo sujeitaste debaixo dos seus pés". Ao lhe sujeitar todas as coisas, nada deixou que não lhe estivesse sujeito. Agora, porém, ainda não vemos que todas as coisas lhe estejam sujeitas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29987, 'nvi', 'Hebreus', 'hb', 2, 9, 'Vemos, todavia, aquele que por um pouco foi feito menor do que os anjos, Jesus, coroado de honra e glória por ter sofrido a morte, para que, pela graça de Deus, em favor de todos, experimentasse a morte.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29988, 'nvi', 'Hebreus', 'hb', 2, 10, 'Ao levar muitos filhos à glória, convinha que Deus, por causa de quem e por meio de quem tudo existe, tornasse perfeito, mediante o sofrimento, o autor da salvação deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29989, 'nvi', 'Hebreus', 'hb', 2, 11, 'Ora, tanto o que santifica quanto os que são santificados provêm de um só. Por isso Jesus não se envergonha de chamá-los irmãos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29990, 'nvi', 'Hebreus', 'hb', 2, 12, 'Ele diz: "Proclamarei o teu nome a meus irmãos; na assembléia te louvarei".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29991, 'nvi', 'Hebreus', 'hb', 2, 13, 'E também: "Nele porei a minha confiança". Novamente ele diz: "Aqui estou eu com os filhos que Deus me deu".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29992, 'nvi', 'Hebreus', 'hb', 2, 14, 'Portanto, visto que os filhos são pessoas de carne e sangue, ele também participou dessa condição humana, para que, por sua morte, derrotasse aquele que tem o poder da morte, isto é, o diabo,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29993, 'nvi', 'Hebreus', 'hb', 2, 15, 'e libertasse aqueles que durante toda a vida estiveram escravizados pelo medo da morte.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29994, 'nvi', 'Hebreus', 'hb', 2, 16, 'Pois é claro que não é a anjos que ele ajuda, mas aos descendentes de Abraão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29995, 'nvi', 'Hebreus', 'hb', 2, 17, 'Por essa razão era necessário que ele se tornasse semelhante a seus irmãos em todos os aspectos, para se tornar sumo sacerdote misericordioso e fiel com relação a Deus e fazer propiciação pelos pecados do povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29996, 'nvi', 'Hebreus', 'hb', 2, 18, 'Porque, tendo em vista o que ele mesmo sofreu quando tentado, ele é capaz de socorrer aqueles que também estão sendo tentados.');
