INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29997, 'nvi', 'Hebreus', 'hb', 3, 1, 'Portanto, santos irmãos, participantes do chamado celestial, fixem os seus pensamentos em Jesus, apóstolo e sumo sacerdote que confessamos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29998, 'nvi', 'Hebreus', 'hb', 3, 2, 'Ele foi fiel àquele que o havia constituído, assim como Moisés foi fiel em toda a casa de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29999, 'nvi', 'Hebreus', 'hb', 3, 3, 'Jesus foi considerado digno de maior glória do que Moisés, da mesma forma que o construtor de uma casa tem mais honra do que a própria casa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30000, 'nvi', 'Hebreus', 'hb', 3, 4, 'Pois toda casa é construída por alguém, mas Deus é o edificador de tudo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30001, 'nvi', 'Hebreus', 'hb', 3, 5, 'Moisés foi fiel como servo em toda a casa de Deus, dando testemunho do que haveria de ser dito no futuro,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30002, 'nvi', 'Hebreus', 'hb', 3, 6, 'mas Cristo é fiel como Filho sobre a casa de Deus; e esta casa somos nós, se é que nos apegamos firmemente à confiança e à esperança da qual nos gloriamos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30003, 'nvi', 'Hebreus', 'hb', 3, 7, 'Assim, como diz o Espírito Santo: "Hoje, se vocês ouvirem a sua voz,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30004, 'nvi', 'Hebreus', 'hb', 3, 8, 'não endureçam o coração, como na rebelião, durante o tempo de provação no deserto,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30005, 'nvi', 'Hebreus', 'hb', 3, 9, 'onde os seus antepassados me tentaram, pondo-me à prova, apesar de, durante quarenta anos, terem visto o que eu fiz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30006, 'nvi', 'Hebreus', 'hb', 3, 10, 'Por isso fiquei irado contra aquela geração e disse: Os seus corações estão sempre se desviando, e eles não reconheceram os meus caminhos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30007, 'nvi', 'Hebreus', 'hb', 3, 11, 'Assim jurei na minha ira: Jamais entrarão no meu descanso".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30008, 'nvi', 'Hebreus', 'hb', 3, 12, 'Cuidado, irmãos, para que nenhum de vocês tenha coração perverso e incrédulo, que se afaste do Deus vivo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30009, 'nvi', 'Hebreus', 'hb', 3, 13, 'Pelo contrário, encorajem-se uns aos outros todos os dias, durante o tempo que se chama "hoje", de modo que nenhum de vocês seja endurecido pelo engano do pecado,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30010, 'nvi', 'Hebreus', 'hb', 3, 14, 'pois passamos a ser participantes de Cristo, desde que, de fato, nos apeguemos até o fim à confiança que tivemos no princípio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30011, 'nvi', 'Hebreus', 'hb', 3, 15, 'Por isso é que se diz: "Se hoje vocês ouvirem a sua voz, não endureçam o coração, como na rebelião".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30012, 'nvi', 'Hebreus', 'hb', 3, 16, 'Quem foram os que ouviram e se rebelaram? Não foram todos os que Moisés tirou do Egito?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30013, 'nvi', 'Hebreus', 'hb', 3, 17, 'Contra quem Deus esteve irado durante quarenta anos? Não foi contra aqueles que pecaram, cujos corpos caíram no deserto?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30014, 'nvi', 'Hebreus', 'hb', 3, 18, 'E a quem jurou que nunca haveriam de entrar no seu descanso? Não foi àqueles que foram desobedientes?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30015, 'nvi', 'Hebreus', 'hb', 3, 19, 'Vemos, assim, que foi por causa da incredulidade que não puderam entrar.');
