INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30046, 'nvi', 'Hebreus', 'hb', 6, 1, 'Portanto, deixemos os ensinos elementares a respeito de Cristo e avancemos para a maturidade, sem lançar novamente o fundamento do arrependimento de atos que conduzem à morte, da fé em Deus,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30047, 'nvi', 'Hebreus', 'hb', 6, 2, 'da instrução a respeito de batismos, da imposição de mãos, da ressurreição dos mortos e do juízo eterno.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30048, 'nvi', 'Hebreus', 'hb', 6, 3, 'Assim faremos, se Deus o permitir.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30049, 'nvi', 'Hebreus', 'hb', 6, 4, 'Ora para aqueles que uma vez foram iluminados, provaram o dom celestial, tornaram-se participantes do Espírito Santo,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30050, 'nvi', 'Hebreus', 'hb', 6, 5, 'experimentaram a bondade da palavra de Deus e os poderes da era que há de vir,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30051, 'nvi', 'Hebreus', 'hb', 6, 6, 'e caíram, é impossível que sejam reconduzidos ao arrependimento; pois para si mesmos estão crucificando de novo o Filho de Deus, sujeitando-o à desonra pública.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30052, 'nvi', 'Hebreus', 'hb', 6, 7, 'Pois a terra que absorve a chuva, que cai freqüentemente e dá colheita proveitosa àqueles que a cultivam, recebe a bênção de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30053, 'nvi', 'Hebreus', 'hb', 6, 8, 'Mas a terra que produz espinhos e ervas daninhas, é inútil e logo será amaldiçoada. Seu fim é ser queimada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30054, 'nvi', 'Hebreus', 'hb', 6, 9, 'Amados, mesmo falando dessa forma, estamos convictos de coisas melhores em relação a vocês, coisas próprias da salvação.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30055, 'nvi', 'Hebreus', 'hb', 6, 10, 'Deus não é injusto; ele não se esquecerá do trabalho de vocês e do amor que demonstraram por ele, pois ajudaram os santos e continuam a ajudá-los.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30056, 'nvi', 'Hebreus', 'hb', 6, 11, 'Queremos que cada um de vocês mostre essa mesma prontidão até o fim, para que tenham a plena certeza da esperança,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30057, 'nvi', 'Hebreus', 'hb', 6, 12, 'de modo que vocês não se tornem negligentes, mas imitem aqueles que, por meio da fé e da paciência, recebem a herança prometida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30058, 'nvi', 'Hebreus', 'hb', 6, 13, 'Quando Deus fez a sua promessa a Abraão, por não haver ninguém superior por quem jurar, jurou por si mesmo,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30059, 'nvi', 'Hebreus', 'hb', 6, 14, 'dizendo: "Esteja certo de que o abençoarei e farei seus descendentes numerosos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30060, 'nvi', 'Hebreus', 'hb', 6, 15, 'E foi assim que, depois de esperar pacientemente, Abraão alcançou a promessa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30061, 'nvi', 'Hebreus', 'hb', 6, 16, 'Os homens juram por alguém superior a si mesmos, e o juramento confirma o que foi dito, pondo fim a toda discussão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30062, 'nvi', 'Hebreus', 'hb', 6, 17, 'Querendo mostrar de forma bem clara a natureza imutável do seu propósito para com os herdeiros da promessa, Deus o confirmou com juramento,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30063, 'nvi', 'Hebreus', 'hb', 6, 18, 'para que, por meio de duas coisas imutáveis nas quais é impossível que Deus minta, sejamos firmemente encorajados, nós, que nos refugiamos nele para tomar posse da esperança a nós proposta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30064, 'nvi', 'Hebreus', 'hb', 6, 19, 'Temos esta esperança como âncora da alma, firme e segura, a qual adentra o santuário interior, por trás do véu,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30065, 'nvi', 'Hebreus', 'hb', 6, 20, 'onde Jesus, que nos precedeu, entrou em nosso lugar, tornando-se sumo sacerdote para sempre, segundo a ordem de Melquisedeque.');
