INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30066, 'nvi', 'Hebreus', 'hb', 7, 1, 'Esse Melquisedeque, rei de Salém e sacerdote do Deus Altíssimo, encontrou-se com Abraão quando este voltava, depois de derrotar os reis, e o abençoou;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30067, 'nvi', 'Hebreus', 'hb', 7, 2, 'e Abraão lhe deu o dízimo de tudo. Em primeiro lugar, seu nome significa "rei de justiça"; depois, "rei de Salém" quer dizer "rei de paz".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30068, 'nvi', 'Hebreus', 'hb', 7, 3, 'Sem pai, sem mãe, sem genealogia, sem princípio de dias nem fim de vida, feito semelhante ao Filho de Deus, ele permanece sacerdote para sempre.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30069, 'nvi', 'Hebreus', 'hb', 7, 4, 'Considerem a grandeza desse homem: até mesmo o patriarca Abraão lhe deu o dízimo dos despojos!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30070, 'nvi', 'Hebreus', 'hb', 7, 5, 'A lei requer dos sacerdotes dentre os descendentes de Levi que recebam o dízimo do povo, isto é, dos seus irmãos, embora estes sejam descendentes de Abraão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30071, 'nvi', 'Hebreus', 'hb', 7, 6, 'Este homem, porém, que não pertencia à linhagem de Levi, recebeu os dízimos de Abraão e abençoou aquele que tinha as promessas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30072, 'nvi', 'Hebreus', 'hb', 7, 7, 'Sem dúvida alguma, o inferior é abençoado pelo superior.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30073, 'nvi', 'Hebreus', 'hb', 7, 8, 'No primeiro caso, quem recebe o dízimo são homens mortais; no outro caso é aquele de quem se declara que vive.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30074, 'nvi', 'Hebreus', 'hb', 7, 9, 'Pode-se até dizer que Levi, que recebe os dízimos, entregou-os por meio de Abraão,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30075, 'nvi', 'Hebreus', 'hb', 7, 10, 'pois, quando Melquisedeque se encontrou com Abraão, Levi ainda estava no corpo do seu antepassado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30076, 'nvi', 'Hebreus', 'hb', 7, 11, 'Se fosse possível alcançar a perfeição por meio do sacerdócio levítico ( pois em sua vigência o povo recebeu a lei ), por que haveria ainda necessidade de se levantar outro sacerdote, segundo a ordem de Melquisedeque e não de Arão?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30077, 'nvi', 'Hebreus', 'hb', 7, 12, 'Pois quando há mudança de sacerdócio, é necessário que haja mudança de lei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30078, 'nvi', 'Hebreus', 'hb', 7, 13, 'Ora, aquele de quem se dizem estas coisas pertencia a outra tribo, da qual ninguém jamais havia servido diante do altar,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30079, 'nvi', 'Hebreus', 'hb', 7, 14, 'pois é evidente que o nosso Senhor descende de Judá, tribo da qual Moisés nada fala quanto a sacerdócio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30080, 'nvi', 'Hebreus', 'hb', 7, 15, 'O que acabamos de dizer fica ainda mais claro, quando aparece outro sacerdote semelhante a Melquisedeque,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30081, 'nvi', 'Hebreus', 'hb', 7, 16, 'alguém que se tornou sacerdote, não por regras relativas à linhagem, mas segundo o poder de uma vida indestrutível.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30082, 'nvi', 'Hebreus', 'hb', 7, 17, 'Pois sobre ele é afirmado: "Tu és sacerdote para sempre, segundo a ordem de Melquisedeque".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30083, 'nvi', 'Hebreus', 'hb', 7, 18, 'A ordenança anterior é revogada, porquanto era fraca e inútil');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30084, 'nvi', 'Hebreus', 'hb', 7, 19, '( pois a lei não havia aperfeiçoado coisa alguma ), sendo introduzida uma esperança superior, pela qual nos aproximamos de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30085, 'nvi', 'Hebreus', 'hb', 7, 20, 'E isso não aconteceu sem juramento! Outros se tornaram sacerdotes sem qualquer juramento,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30086, 'nvi', 'Hebreus', 'hb', 7, 21, 'mas ele se tornou sacerdote com juramento, quando Deus lhe disse: "O Senhor jurou e não se arrependerá: ‘Tu és sacerdote para sempre’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30087, 'nvi', 'Hebreus', 'hb', 7, 22, 'Jesus tornou-se, por isso mesmo, a garantia de uma aliança superior.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30088, 'nvi', 'Hebreus', 'hb', 7, 23, 'Ora, daqueles sacerdotes tem havido muitos, porque a morte os impede de continuar em seu ofício;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30089, 'nvi', 'Hebreus', 'hb', 7, 24, 'mas, visto que vive para sempre, Jesus tem um sacerdócio permanente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30090, 'nvi', 'Hebreus', 'hb', 7, 25, 'Portanto ele é capaz de salvar definitivamente aqueles que, por meio dele, aproximam-se de Deus, pois vive sempre para interceder por eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30091, 'nvi', 'Hebreus', 'hb', 7, 26, 'É de um sumo sacerdote como este que precisávamos: santo, inculpável, puro, separado dos pecadores, exaltado acima dos céus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30092, 'nvi', 'Hebreus', 'hb', 7, 27, 'Ao contrário dos outros sumos sacerdotes, ele não tem necessidade de oferecer sacrifícios dia após dia, primeiro por seus próprios pecados e, depois, pelos pecados do povo. E ele fez isso de uma vez por todas quando a si mesmo se ofereceu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30093, 'nvi', 'Hebreus', 'hb', 7, 28, 'Pois a Lei constitui sumos sacerdotes a homens que têm fraquezas; mas o juramento, que veio depois da Lei, constitui o Filho, perfeito para sempre.');
