INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22750, 'nvi', 'Habacuque', 'hc', 2, 1, 'Ficarei no meu posto de sentinela e tomarei posição sobre a muralha; aguardarei para ver o que ele me dirá e que resposta terei à minha queixa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22751, 'nvi', 'Habacuque', 'hc', 2, 2, 'Então o Senhor respondeu: "Escreva claramente a visão em tabuinhas, para que se leia facilmente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22752, 'nvi', 'Habacuque', 'hc', 2, 3, 'Pois a visão aguarda um tempo designado; ela fala do fim, e não falhará. Ainda que se demore, espere-a; porque ela certamente virá e não se atrasará".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22753, 'nvi', 'Habacuque', 'hc', 2, 4, 'Escreva: "O ímpio está envaidecido; seus desejos não são bons; mas o justo viverá pela sua fidelidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22754, 'nvi', 'Habacuque', 'hc', 2, 5, 'De fato, a riqueza é ilusória, e o ímpio é arrogante e não descansa; ele é voraz como a sepultura e como a morte. Nunca se satisfaz; apanha para si todas as nações e ajunta para si todos os povos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22755, 'nvi', 'Habacuque', 'hc', 2, 6, '"Todos estes povos um dia rirão dele com canções de zombaria e dirão: " ‘Ai daquele que amontoa bens roubados e se enriquece mediante extorsão! Até quando isto continuará assim? ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22756, 'nvi', 'Habacuque', 'hc', 2, 7, 'Seus credores não se levantarão de repente? Não despertarão os que o fazem tremer? Agora você se tornará vítima deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22757, 'nvi', 'Habacuque', 'hc', 2, 8, 'Porque você saqueou muitas nações, todos os povos que restaram o saquearão. Pois você derramou muito sangue, e cometeu violência contra terras, cidades e seus habitantes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22758, 'nvi', 'Habacuque', 'hc', 2, 9, '"Ai daquele que obtém lucros injustos para a sua casa, para pôr seu ninho no alto e escapar das garras do mal!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22759, 'nvi', 'Habacuque', 'hc', 2, 10, 'Você tramou a ruína de muitos povos, envergonhando a sua própria casa e pecando contra a sua própria vida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22760, 'nvi', 'Habacuque', 'hc', 2, 11, 'Pois as pedras clamarão da parede, e as vigas responderão do madeiramento contra você.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22761, 'nvi', 'Habacuque', 'hc', 2, 12, '"Ai daquele que edifica uma cidade com sangue e a estabelece com crime!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22762, 'nvi', 'Habacuque', 'hc', 2, 13, 'Acaso não vem do Senhor dos Exércitos, que o trabalho dos povos seja só para satisfazer o fogo, e que as nações se afadiguem em vão?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22763, 'nvi', 'Habacuque', 'hc', 2, 14, 'E a terra se encherá do conhecimento da glória do Senhor, como as águas enchem o mar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22764, 'nvi', 'Habacuque', 'hc', 2, 15, '"Ai daquele que dá bebida ao seu próximo, misturando-a com o seu furor, até que ele fique bêbado, para lhe contemplar a nudez.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22765, 'nvi', 'Habacuque', 'hc', 2, 16, 'Beba bastante vergonha, em vez de glória! Sim! Beba você também e se exponha! A taça da mão direita do Senhor lhe é dada. Muita vergonha cobrirá a sua glória.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22766, 'nvi', 'Habacuque', 'hc', 2, 17, 'A violência que você cometeu contra o Líbano o alcançará, e você ficará apavorado com a destruição de animais, que você fez. Pois você derramou muito sangue, e cometeu violência contra terras, cidades e seus habitantes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22767, 'nvi', 'Habacuque', 'hc', 2, 18, '"De que vale uma imagem feita por um escultor? Ou um ídolo de metal que ensina mentiras? Pois aquele que o faz confia em sua própria criação, fazendo ídolos incapazes de falar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22768, 'nvi', 'Habacuque', 'hc', 2, 19, 'Ai daquele que diz à madeira: "Desperte! Ou à pedra sem vida: Acorde! " Poderá isso dar orientação? Está coberta de ouro e prata, mas não respira.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22769, 'nvi', 'Habacuque', 'hc', 2, 20, 'Mas o Senhor está em seu santo templo; diante dele fique em silêncio toda a terra".');
