INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22770, 'nvi', 'Habacuque', 'hc', 3, 1, 'Oração do profeta Habacuque. Uma confissão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22771, 'nvi', 'Habacuque', 'hc', 3, 2, 'Senhor, ouvi falar da tua fama; temo diante dos teus atos, Senhor. Realiza de novo, em nossa época, as mesmas obras, faze-as conhecidas em nosso tempo; em tua ira, lembra-te da misericórdia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22772, 'nvi', 'Habacuque', 'hc', 3, 3, 'Deus veio de Temã, o Santo veio do monte Parã. Pausa Sua glória cobriu os céus e seu louvor encheu a terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22773, 'nvi', 'Habacuque', 'hc', 3, 4, 'Seu esplendor era como a luz do sol; raios lampejavam de sua mão, onde se escondia o seu poder.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22774, 'nvi', 'Habacuque', 'hc', 3, 5, 'Pragas iam adiante dele; doenças terríveis seguiam os seus passos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22775, 'nvi', 'Habacuque', 'hc', 3, 6, 'Ele parou, e a terra tremeu; olhou, e fez estremecer as nações. Montes antigos se desmancharam; colinas antiqüíssimas se desfizeram. Os caminhos dele são eternos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22776, 'nvi', 'Habacuque', 'hc', 3, 7, 'Vi a aflição das tendas de Cuchã; tremiam as cortinas das tendas de Midiã.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22777, 'nvi', 'Habacuque', 'hc', 3, 8, 'Era com os rios que estavas irado, Senhor? Era contra os riachos o teu furor? Foi contra o mar que a tua fúria transbordou quando cavalgaste com os teus cavalos e com os teus carros vitoriosos?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22778, 'nvi', 'Habacuque', 'hc', 3, 9, 'Preparaste o teu arco; pediste muitas flechas. Pausa Fendeste a terra com rios;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22779, 'nvi', 'Habacuque', 'hc', 3, 10, 'os montes te viram e se contorceram. Torrentes de água desceram com violência; o abismo estrondou erguendo as suas ondas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22780, 'nvi', 'Habacuque', 'hc', 3, 11, 'O sol e lua pararam em suas moradas, diante do reflexo de tuas flechas voadoras, diante do lampejo da tua lança reluzente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22781, 'nvi', 'Habacuque', 'hc', 3, 12, 'Com ira andaste a passos largos por toda a terra e com indignação pisoteaste as nações.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22782, 'nvi', 'Habacuque', 'hc', 3, 13, 'Saíste para salvar o teu povo, para libertar o teu ungido. Esmagaste o líder da nação ímpia, tu o desnudaste da cabeça aos pés. Pausa');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22783, 'nvi', 'Habacuque', 'hc', 3, 14, 'Com as suas próprias flechas lhe atravessaste a cabeça, quando os seus guerreiros saíram como um furacão para nos espalhar, com maldoso prazer, como se estivessem para devorar o necessitado em seu esconderijo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22784, 'nvi', 'Habacuque', 'hc', 3, 15, 'Pisaste o mar com teus cavalos, agitando as grandes águas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22785, 'nvi', 'Habacuque', 'hc', 3, 16, 'Ouvi isso, e o meu íntimo estremeceu, meus lábios tremeram; os meus ossos desfaleceram; minhas pernas vacilavam. Tranqüilo esperarei o dia da desgraça que virá sobre o povo que nos ataca.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22786, 'nvi', 'Habacuque', 'hc', 3, 17, 'Mesmo não florescendo a figueira, não havendo uvas nas videiras; mesmo falhando a safra de azeitonas, não havendo produção de alimento nas lavouras, nem ovelhas no curral nem bois nos estábulos,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22787, 'nvi', 'Habacuque', 'hc', 3, 18, 'ainda assim eu exultarei no Senhor e me alegrarei no Deus da minha salvação.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22788, 'nvi', 'Habacuque', 'hc', 3, 19, 'O Senhor Soberano é a minha força; ele faz os meus pés como os do cervo; ele me habilita a andar em lugares altos. Para o mestre de música. Para os meus instrumentos de cordas.');
