INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17656, 'nvi', 'Isaías', 'is', 1, 1, 'Visão que Isaías, filho de Amoz, teve a respeito de Judá e Jerusalém durante os reinados de Uzias, Jotão, Acaz e Ezequias, reis de Judá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17657, 'nvi', 'Isaías', 'is', 1, 2, 'Ouçam, ó céus! Escute, ó terra! Pois o Senhor falou: "Criei filhos e os fiz crescer, mas eles se revoltaram contra mim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17658, 'nvi', 'Isaías', 'is', 1, 3, 'O boi reconhece o seu dono, e o jumento conhece a manjedoura do seu proprietário, mas Israel nada sabe, o meu povo nada compreende".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17659, 'nvi', 'Isaías', 'is', 1, 4, 'Ah, nação pecadora, povo carregado de iniqüidade! Raça de malfeitores, filhos dados à corrupção! Abandonaram o Senhor; desprezaram o Santo de Israel e o rejeitaram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17660, 'nvi', 'Isaías', 'is', 1, 5, 'Por que continuarão sendo castigados? Por que insistem na revolta? A cabeça toda está ferida, todo o coração está sofrendo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17661, 'nvi', 'Isaías', 'is', 1, 6, 'Da sola do pé ao alto da cabeça não há nada são; somente machucados, vergões e ferimentos abertos, que não foram limpos nem enfaixados nem tratados com azeite.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17662, 'nvi', 'Isaías', 'is', 1, 7, 'A terra de vocês está devastada, suas cidades foram destruídas a fogo; os seus campos estão sendo tomados por estrangeiros, diante de vocês, e devastados como a ruína que os estrangeiros costumam causar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17663, 'nvi', 'Isaías', 'is', 1, 8, 'Só restou a cidade de Sião como tenda numa vinha, como abrigo numa plantação de melões, como uma cidade sitiada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17664, 'nvi', 'Isaías', 'is', 1, 9, 'Se o Senhor dos Exércitos não tivesse poupado alguns de nós, já estaríamos como Sodoma e semelhantes a Gomorra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17665, 'nvi', 'Isaías', 'is', 1, 10, 'Governantes de Sodoma, ouçam a palavra do Senhor! Vocês, povo de Gomorra, escutem a instrução de nosso Deus!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17666, 'nvi', 'Isaías', 'is', 1, 11, '"Para que me oferecem tantos sacrifícios? ", pergunta o Senhor. Para mim, chega de holocaustos de carneiros e da gordura de novilhos gordos; não tenho nenhum prazer no sangue de novilhos, de cordeiros e de bodes!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17667, 'nvi', 'Isaías', 'is', 1, 12, 'Quando lhes pediu que viessem à minha presença, quem lhes pediu que pusessem os pés em meus átrios?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17668, 'nvi', 'Isaías', 'is', 1, 13, 'Parem de trazer ofertas inúteis! O incenso de vocês é repugnante para mim. Luas novas, sábados e reuniões! Não consigo suportar suas assembléias cheias de iniqüidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17669, 'nvi', 'Isaías', 'is', 1, 14, 'Suas festas da lua nova e suas festas fixas, eu as odeio. Tornaram-se um fardo para mim; não as suporto mais!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17670, 'nvi', 'Isaías', 'is', 1, 15, 'Quando vocês estenderem as mãos em oração, esconderei de vocês os meus olhos; mesmo que multipliquem as suas orações, não as escutarei! As suas mãos estão cheias de sangue!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17671, 'nvi', 'Isaías', 'is', 1, 16, 'Lavem-se! Limpem-se! Removam suas más obras para longe da minha vista! Parem de fazer o mal,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17672, 'nvi', 'Isaías', 'is', 1, 17, 'aprendam a fazer o bem! Busquem a justiça, acabem com a opressão. Lutem pelos direitos do órfão, defendam a causa da viúva.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17673, 'nvi', 'Isaías', 'is', 1, 18, '"Venham, vamos refletir juntos", diz o Senhor. "Embora os seus pecados sejam vermelhos como escarlate, eles se tornarão brancos como a neve; embora sejam rubros como púrpura, como a lã se tornarão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17674, 'nvi', 'Isaías', 'is', 1, 19, 'Se vocês estiverem dispostos a obedecer, comerão os melhores frutos desta terra;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17675, 'nvi', 'Isaías', 'is', 1, 20, 'mas, se resistirem e se rebelarem, serão devorados pela espada". Pois o Senhor é quem fala!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17676, 'nvi', 'Isaías', 'is', 1, 21, 'Vejam como a cidade fiel se tornou prostituta! Antes cheia de justiça e habitada pela retidão, agora está cheia de assassinos!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17677, 'nvi', 'Isaías', 'is', 1, 22, 'Sua prata tornou-se escória, seu licor ficou aguado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17678, 'nvi', 'Isaías', 'is', 1, 23, 'Seus líderes são rebeldes, amigos de ladrões; todos eles amam o suborno e andam atrás de presentes. Eles não defendem os direitos do órfão, e não tomam conhecimento da causa da viúva.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17679, 'nvi', 'Isaías', 'is', 1, 24, 'Por isso o Soberano, o Senhor dos Exércitos, o Poderoso de Israel, anuncia: "Ah! Derramarei minha ira sobre os meus adversários e me vingarei dos meus inimigos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17680, 'nvi', 'Isaías', 'is', 1, 25, 'Voltarei minha mão contra você; tirarei toda a sua escória e removerei todas as suas impurezas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17681, 'nvi', 'Isaías', 'is', 1, 26, 'Restaurarei os seus juízes como no passado, os seus conselheiros, como no princípio. Depois disso você será chamada cidade de retidão, cidade fiel".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17682, 'nvi', 'Isaías', 'is', 1, 27, 'Sião será redimida com justiça, com retidão os que se arrependerem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17683, 'nvi', 'Isaías', 'is', 1, 28, 'Mas os rebeldes e os pecadores serão destruídos, e os que abandonam o Senhor perecerão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17684, 'nvi', 'Isaías', 'is', 1, 29, '"Vocês se envergonharão dos carvalhos sagrados que tanto apreciam; ficarão decepcionados com os jardins sagrados que escolheram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17685, 'nvi', 'Isaías', 'is', 1, 30, 'Vocês serão como um terebinto cujas folhas estão caindo, como um jardim sem água.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17686, 'nvi', 'Isaías', 'is', 1, 31, 'O poderoso se tornará como estopa e sua obra, como fagulha; ambos serão queimados juntos sem que ninguém apague o fogo".');
