INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17908, 'nvi', 'Isaías', 'is', 13, 1, 'Advertência contra a Babilônia, que Isaías, filho de Amoz, recebeu em visão:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17909, 'nvi', 'Isaías', 'is', 13, 2, 'Levantem uma bandeira no topo de uma colina desnuda, gritem a eles; chamem-nos com um aceno, para que entrem pelas portas dos nobres.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17910, 'nvi', 'Isaías', 'is', 13, 3, 'Eu mesmo ordenei aos meus santos; para executarem a minha ira já convoquei os meus guerreiros, os que se regozijam com o meu triunfo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17911, 'nvi', 'Isaías', 'is', 13, 4, 'Escutem! Há um barulho nos montes como o de uma grande multidão! Escutem! É uma gritaria entre os reinos, como nações formando uma imensa multidão! O Senhor dos Exércitos está reunindo um exército para a guerra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17912, 'nvi', 'Isaías', 'is', 13, 5, 'Eles vêm de terras distantes, lá dos confins dos céus; o Senhor e as armas da sua ira, para destruírem todo o país.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17913, 'nvi', 'Isaías', 'is', 13, 6, 'Chorem, pois o dia do Senhor está perto; virá como destruição da parte do Todo-poderoso.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17914, 'nvi', 'Isaías', 'is', 13, 7, 'Por isso, todas as mãos ficarão trêmulas, o coração de todos os homens se derreterá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17915, 'nvi', 'Isaías', 'is', 13, 8, 'Ficarão apavorados, dores e aflições os dominarão; eles se contorcerão como a mulher em trabalho de parto. Olharão chocados uns para os outros, com os rostos em fogo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17916, 'nvi', 'Isaías', 'is', 13, 9, 'Vejam! O dia do Senhor está perto, dia cruel, de ira e grande furor, para devastar a terra e destruir os seus pecadores.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17917, 'nvi', 'Isaías', 'is', 13, 10, 'As estrelas do céu e as suas constelações não mostrarão a sua luz. O sol nascente escurecerá, e a lua não fará brilhar a sua luz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17918, 'nvi', 'Isaías', 'is', 13, 11, 'Castigarei o mundo por causa da sua maldade, os ímpios pela sua iniqüidade. Darei fim à arrogância dos altivos e humilharei o orgulho dos cruéis.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17919, 'nvi', 'Isaías', 'is', 13, 12, 'Tornarei o homem mais escasso do que o ouro puro, mais raro do que o ouro de Ofir.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17920, 'nvi', 'Isaías', 'is', 13, 13, 'Por isso farei o céu tremer; e a terra se moverá do seu lugar diante da ira do Senhor dos Exércitos, no dia do furor da sua ira.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17921, 'nvi', 'Isaías', 'is', 13, 14, 'Como a gazela perseguida, como a ovelha que ninguém recolhe, cada um voltará para o seu povo, cada um fugirá para a sua terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17922, 'nvi', 'Isaías', 'is', 13, 15, 'Todo o que for capturado será traspassado; todos os que forem apanhados cairão à espada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17923, 'nvi', 'Isaías', 'is', 13, 16, 'Seus bebês serão despedaçados diante dos seus olhos; suas casas serão saqueadas e suas mulheres violentadas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17924, 'nvi', 'Isaías', 'is', 13, 17, 'Vejam! Eu despertarei contra eles os medos, que não se interessam pela prata nem se deleitam com o ouro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17925, 'nvi', 'Isaías', 'is', 13, 18, 'Seus arcos ferirão os jovens, e eles não terão misericórdia dos bebês, nem olharão com compaixão para as crianças.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17926, 'nvi', 'Isaías', 'is', 13, 19, 'Babilônia, a jóia dos reinos, o esplendor do orgulho dos babilônios. será destruída por Deus à semelhança de Sodoma e Gomorra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17927, 'nvi', 'Isaías', 'is', 13, 20, 'Nunca mais será repovoada nem habitada, de geração em geração; o árabe não armará ali a sua tenda e o pastor não fará descansar ali o seu rebanho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17928, 'nvi', 'Isaías', 'is', 13, 21, 'Mas as criaturas do deserto lá estarão, e as suas casas se encherão de chacais; nela habitarão corujas e saltarão bodes selvagens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17929, 'nvi', 'Isaías', 'is', 13, 22, 'As hienas uivarão em suas fortalezas, e os chacais em seus luxuosos palácios. O tempo dela está terminando, e os seus dias não serão prolongados.');
