INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17930, 'nvi', 'Isaías', 'is', 14, 1, 'O Senhor terá compaixão de Jacó; tornará a escolher Israel e os estabelecerá em sua própria terra. Os estrangeiros se juntarão a eles e farão parte da descendência de Jacó.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17931, 'nvi', 'Isaías', 'is', 14, 2, 'Povos os apanharão e os levarão ao seu próprio lugar. E a descendência de Israel possuirá os povos como servos e servas na terra do Senhor. Farão prisioneiros os seus captores e dominarão sobre os seus opressores.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17932, 'nvi', 'Isaías', 'is', 14, 3, 'No dia em que o Senhor lhe der descanso do sofrimento, da perturbação e da cruel escravidão que sobre você foi imposta,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17933, 'nvi', 'Isaías', 'is', 14, 4, 'assim você zombará do rei da Babilônia: Como chegou ao fim o opressor! Sua arrogância acabou-se!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17934, 'nvi', 'Isaías', 'is', 14, 5, 'O Senhor quebrou a vara dos ímpios, o cetro dos governantes,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17935, 'nvi', 'Isaías', 'is', 14, 6, 'que irados feriram os povos com golpes incessantes, e enfurecidos subjugaram as nações com perseguição implacável.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17936, 'nvi', 'Isaías', 'is', 14, 7, 'Toda a terra descansa tranqüila, todos irrompem em gritos de alegria.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17937, 'nvi', 'Isaías', 'is', 14, 8, 'Até os pinheiros e os cedros do Líbano alegram-se por sua causa e dizem: "Agora que você foi derrubado, nenhum lenhador vem derrubar-nos! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17938, 'nvi', 'Isaías', 'is', 14, 9, 'Nas profundezas o Sheol está todo agitado para recebê-lo quando chegar. Por sua causa ele desperta os espíritos dos mortos, todos os governantes da terra. Ele os faz levantar-se dos seus tronos, todos os reis dos povos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17939, 'nvi', 'Isaías', 'is', 14, 10, 'Todos responderão e lhe dirão: "Você também perdeu as forças como nós, e tornou-se como um de nós".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17940, 'nvi', 'Isaías', 'is', 14, 11, 'Sua soberba foi lançada na sepultura, junto com o som das suas liras; sua cama é de larvas, sua coberta, de vermes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17941, 'nvi', 'Isaías', 'is', 14, 12, 'Como você caiu dos céus, ó estrela da manhã, filho da alvorada! Como foi atirado à terra, você, que derrubava as nações!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17942, 'nvi', 'Isaías', 'is', 14, 13, 'Você que dizia no seu coração: "Subirei aos céus; erguerei o meu trono acima das estrelas de Deus; eu me assentarei no monte da assembléia, no ponto mais elevado do monte santo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17943, 'nvi', 'Isaías', 'is', 14, 14, 'Subirei mais alto que as mais altas nuvens; serei como o Altíssimo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17944, 'nvi', 'Isaías', 'is', 14, 15, 'Mas às profundezas do Sheol você será levado, irá ao fundo do abismo!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17945, 'nvi', 'Isaías', 'is', 14, 16, 'Os que olham para você admiram-se da sua situação, e a seu respeito ponderam: "É esse o homem que fazia tremer a terra e abalava os reinos,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17946, 'nvi', 'Isaías', 'is', 14, 17, 'e fez do mundo um deserto, conquistou cidades e não deixou que os seus prisioneiros voltassem para casa? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17947, 'nvi', 'Isaías', 'is', 14, 18, 'Todos os reis das nações jazem honrosamente, cada um em seu próprio túmulo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17948, 'nvi', 'Isaías', 'is', 14, 19, 'Mas você é atirado fora do seu túmulo, como um galho rejeitado; como as roupas dos mortos que foram feridos pela espada; como os que descem às pedras da cova; como um cadáver pisoteado,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17949, 'nvi', 'Isaías', 'is', 14, 20, 'você não se unirá a eles num sepultamento, pois destruiu a sua própria terra, e matou o seu próprio povo. Nunca se mencione a descendência dos malfeitores!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17950, 'nvi', 'Isaías', 'is', 14, 21, 'Preparem um local para matar os filhos dele por causa da iniqüidade dos seus antepassados; para que eles não se levantem para herdar a terra e a cobrirem de cidades.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17951, 'nvi', 'Isaías', 'is', 14, 22, '"Eu me levantarei contra eles", diz o Senhor dos Exércitos. "Eliminarei da Babilônia o seu nome e os seus sobreviventes, sua prole e os seus descendentes", diz o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17952, 'nvi', 'Isaías', 'is', 14, 23, '"Farei dela um lugar para corujas e uma terra pantanosa; vou varrê-la com a vassoura da destruição", diz o Senhor dos Exércitos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17953, 'nvi', 'Isaías', 'is', 14, 24, 'O Senhor dos Exércitos jurou: "Certamente, como planejei, assim acontecerá, e, como pensei, assim será.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17954, 'nvi', 'Isaías', 'is', 14, 25, 'Esmagarei a Assíria na minha terra; nos meus montes a pisotearei. O seu jugo será tirado do meu povo, e o seu fardo dos ombros deles".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17955, 'nvi', 'Isaías', 'is', 14, 26, 'Esse é o plano estabelecido para toda a terra; essa é a mão estendida sobre todas as nações.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17956, 'nvi', 'Isaías', 'is', 14, 27, 'Pois esse é o propósito do Senhor dos Exércitos; quem pode impedi-lo? Sua mão está estendida; quem pode fazê-la recuar?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17957, 'nvi', 'Isaías', 'is', 14, 28, 'Esta advertência veio no ano em que o rei Acaz morreu:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17958, 'nvi', 'Isaías', 'is', 14, 29, 'Vocês, filisteus, todos vocês, não se alegrem porque a vara que os feria está quebrada! Da raiz da cobra brotará uma víbora, e o seu fruto será uma serpente veloz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17959, 'nvi', 'Isaías', 'is', 14, 30, 'O mais pobre dos pobres achará pastagem, e os necessitados descansarão em segurança. Mas eu matarei de fome a raiz de vocês, e ela matará os seus sobreviventes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17960, 'nvi', 'Isaías', 'is', 14, 31, 'Lamente, ó porta! Clame, ó cidade! Derretam-se todos vocês, filisteus! Do norte vem um exército, e ninguém desertou de suas fileiras.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17961, 'nvi', 'Isaías', 'is', 14, 32, 'Que resposta se dará aos emissários daquela nação? Esta: "O Senhor estabeleceu Sião, e nela encontrarão refúgio os aflitos do seu povo".');
