INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17999, 'nvi', 'Isaías', 'is', 18, 1, 'Ai da terra do zumbido de insetos ao longo dos rios da Etiópia,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18000, 'nvi', 'Isaías', 'is', 18, 2, 'que manda emissários pelo mar em barcos de papiro sobre as águas. Vão, ágeis mensageiros, a um povo alto e de pele macia, a um povo temido pelos que estão perto e pelos que estão longe, nação agressiva e de fala estranha, cuja terra é dividida por rios.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18001, 'nvi', 'Isaías', 'is', 18, 3, 'Todos vocês, habitantes do mundo, vocês que vivem na terra, quando a bandeira for erguida sobre os montes, vocês a verão, e, quando soar a trombeta, vocês a ouvirão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18002, 'nvi', 'Isaías', 'is', 18, 4, 'Assim diz o Senhor: "Do lugar onde moro ficarei olhando, quieto como o ardor do sol reluzente, como a nuvem de orvalho no calor do tempo da colheita".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18003, 'nvi', 'Isaías', 'is', 18, 5, 'Pois, antes da colheita, quando a floração der lugar ao fruto e as uvas amadurecerem, ele cortará os brotos com a podadeira e tirará os ramos longos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18004, 'nvi', 'Isaías', 'is', 18, 6, 'Serão todos entregues aos abutres das montanhas e aos animais selvagens; as aves se alimentarão delas todo o verão, e os animais selvagens, todo o inverno.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18005, 'nvi', 'Isaías', 'is', 18, 7, 'Naquela ocasião dádivas serão trazidas ao Senhor dos Exércitos da parte de um povo alto e de pele macia, da parte de um povo temido pelos que estão perto e pelos que estão longe, nação agressiva e de fala estranha, cuja terra é dividida por rios. As dádivas serão trazidas ao monte Sião, ao local do nome do Senhor dos Exércitos.');
