INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18006, 'nvi', 'Isaías', 'is', 19, 1, 'Advertência contra o Egito: Vejam! O Senhor cavalga numa nuvem veloz que vai para o Egito. Os ídolos do Egito tremem diante dele, e os corações dos egípcios se derretem no íntimo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18007, 'nvi', 'Isaías', 'is', 19, 2, '"Incitarei egípcio contra egípcio; cada um lutará contra seu irmão, vizinho lutará contra vizinho, cidade contra cidade, reino contra reino.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18008, 'nvi', 'Isaías', 'is', 19, 3, 'Os egípcios ficarão desanimados, e farei que os seus planos resultem em nada. Depois eles consultarão os ídolos e os necromantes, os médiuns e os adivinhos,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18009, 'nvi', 'Isaías', 'is', 19, 4, 'então eu entregarei os egípcios nas mãos de um senhor cruel, e um rei feroz dominará sobre eles", anuncia o Soberano, o Senhor dos Exércitos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18010, 'nvi', 'Isaías', 'is', 19, 5, 'As águas do rio vão secar-se; o leito do rio ficará completamente seco.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18011, 'nvi', 'Isaías', 'is', 19, 6, 'Os canais terão mau cheiro; os riachos do Egito vão diminuir até secarem-se; os juncos e as canas murcharão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18012, 'nvi', 'Isaías', 'is', 19, 7, 'Haverá lugares secos ao longo do Nilo e na própria foz do rio. Tudo o que for semeado ao longo do Nilo se ressecará, será levado pelo vento e desaparecerá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18013, 'nvi', 'Isaías', 'is', 19, 8, 'Os pescadores gemerão e se lamentarão como também todos os que lançam anzóis no Nilo; os que lançam redes na água se desanimarão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18014, 'nvi', 'Isaías', 'is', 19, 9, 'Os que trabalham com linho e os tecelões de algodão se desesperarão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18015, 'nvi', 'Isaías', 'is', 19, 10, 'Os nobres ficarão deprimidos, e todos os assalariados ficarão abatidos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18016, 'nvi', 'Isaías', 'is', 19, 11, 'Os líderes de Zoa não passam de insensatos; os sábios conselheiros do faraó dão conselhos tolos. Como, então, vocês podem dizer ao faraó: "Sou sábio, sou discípulo dos reis da antigüidade"?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18017, 'nvi', 'Isaías', 'is', 19, 12, 'Onde estão agora os seus sábios? Que lhe mostrem, se é que eles tem conhecimento do que o Senhor dos Exércitos tem planejado contra o Egito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18018, 'nvi', 'Isaías', 'is', 19, 13, 'Tornaram-se tolos os líderes de Zoã, e os de Mênfis são enganados; os chefes dos seus clãs induziram o Egito ao erro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18019, 'nvi', 'Isaías', 'is', 19, 14, 'O Senhor derramou dentro deles um espírito que os deixou desorientados; eles levam o Egito a cambalear em tudo quanto faz, como cambaleia o bêbado em volta do seu vômito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18020, 'nvi', 'Isaías', 'is', 19, 15, 'Não há nada que o Egito possa fazer, nada que a cabeça ou a cauda, a palma ou o junco possam fazer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18021, 'nvi', 'Isaías', 'is', 19, 16, 'Naquele dia os egípcios serão como mulheres. Tremerão de medo diante do agitar da mão do Senhor dos Exércitos, que se levantará contra eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18022, 'nvi', 'Isaías', 'is', 19, 17, 'Judá trará pavor aos egípcios; todo aquele que mencionar o nome de Judá ficará apavorado, por causa do plano do Senhor dos Exércitos contra eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18023, 'nvi', 'Isaías', 'is', 19, 18, 'Naquele dia cinco cidades do Egito falarão a língua de Canaã e jurarão lealdade ao Senhor dos Exércitos. Uma delas será chamada cidade do Sol.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18024, 'nvi', 'Isaías', 'is', 19, 19, 'Naquele dia haverá um altar dedicado ao Senhor no centro do Egito, e em sua fronteira, um monumento ao Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18025, 'nvi', 'Isaías', 'is', 19, 20, 'Serão um sinal e um testemunho para o Senhor dos Exércitos na terra do Egito. Quando eles clamarem ao Senhor por causa dos seus opressores, ele lhes enviará um salvador e defensor que os libertará.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18026, 'nvi', 'Isaías', 'is', 19, 21, 'Assim o Senhor se dará a conhecer aos egípcios, e naquele dia eles saberão quem é o Senhor. A ele prestarão culto com sacrifícios e ofertas de cereal; farão votos ao Senhor e os cumprirão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18027, 'nvi', 'Isaías', 'is', 19, 22, 'O Senhor ferirá os egípcios; ele os ferirá e os curará. Eles se voltarão para o Senhor, e ele responderá às suas súplicas e os curará.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18028, 'nvi', 'Isaías', 'is', 19, 23, 'Naquele dia haverá uma estrada do Egito para a Assíria. Os assírios irão para o Egito, e os egípcios para a Assíria, e os egípcios e os assírios cultuarão juntos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18029, 'nvi', 'Isaías', 'is', 19, 24, 'Naquele dia Israel será um mediador entre o Egito e a Assíria, uma bênção na terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18030, 'nvi', 'Isaías', 'is', 19, 25, 'O Senhor dos Exércitos os abençoará, dizendo: "Bendito seja o Egito, meu povo, a Assíria, obra de minhas mãos, e Israel, minha herança".');
