INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17687, 'nvi', 'Isaías', 'is', 2, 1, 'Foi isto que Isaías, filho de Amoz, viu a respeito de Judá e de Jerusalém:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17688, 'nvi', 'Isaías', 'is', 2, 2, 'Nos últimos dias o monte do templo do Senhor será estabelecido como o principal; será elevado acima das colinas, e todas as nações correrão para ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17689, 'nvi', 'Isaías', 'is', 2, 3, 'Virão muitos povos e dirão: "Venham, subamos ao monte do Senhor, ao templo do Deus de Jacó, para que ele nos ensine os seus caminhos, e assim andemos em suas veredas". Pois, a lei sairá de Sião, de Jerusalém virá a palavra do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17690, 'nvi', 'Isaías', 'is', 2, 4, 'Ele julgará entre as nações e resolverá contendas de muitos povos. Eles farão de suas espadas arados, e de suas lanças foices. Uma nação não mais pegará em armas para atacar outra nação, elas jamais tornarão a preparar-se para a guerra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17691, 'nvi', 'Isaías', 'is', 2, 5, 'Venha, ó descendência de Jacó, andemos na luz do Senhor!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17692, 'nvi', 'Isaías', 'is', 2, 6, 'Certamente abandonaste o teu povo, os descendentes de Jacó, porque eles se encheram de superstições dos povos do leste, praticam adivinhações como os filisteus e fazem acordos com pagãos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17693, 'nvi', 'Isaías', 'is', 2, 7, 'Sua terra está cheia de prata e ouro; seus tesouros são incontáveis. Sua terra está cheia de cavalos; seus carros não têm fim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17694, 'nvi', 'Isaías', 'is', 2, 8, 'Sua terra está cheia de ídolos. Eles se inclinam diante da obra das suas mãos, diante do que os seus dedos fizeram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17695, 'nvi', 'Isaías', 'is', 2, 9, 'Por isso a humanidade será abatida e o homem será humilhado; não os perdoes!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17696, 'nvi', 'Isaías', 'is', 2, 10, 'Entre no meio das rochas, esconda-se no pó, por causa do terror que vem do Senhor e do esplendor da sua majestade!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17697, 'nvi', 'Isaías', 'is', 2, 11, 'O olhos do arrogante serão humilhados e o orgulho dos homens será abatido; somente o Senhor será exaltado naquele dia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17698, 'nvi', 'Isaías', 'is', 2, 12, 'O Senhor dos Exércitos tem um dia reservado para todos os orgulhosos e altivos, para tudo o que é exaltado para que eles sejam humilhados;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17699, 'nvi', 'Isaías', 'is', 2, 13, 'para todos os cedros do Líbano, altos e altivos, e todos os carvalhos de Basã,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17700, 'nvi', 'Isaías', 'is', 2, 14, 'para todos os montes elevados e todas as colinas altas,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17701, 'nvi', 'Isaías', 'is', 2, 15, 'para toda torre imponente e todo muro fortificado,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17702, 'nvi', 'Isaías', 'is', 2, 16, 'para todo navio mercante e todo barco de luxo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17703, 'nvi', 'Isaías', 'is', 2, 17, 'A arrogância dos homens será abatida, e o seu orgulho será humilhado. Somente o Senhor será exaltado naquele dia,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17704, 'nvi', 'Isaías', 'is', 2, 18, 'e os ídolos desaparecerão por completo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17705, 'nvi', 'Isaías', 'is', 2, 19, 'Os homens fugirão para as cavernas das rochas e para os buracos da terra, por causa do terror que vem do Senhor e do esplendor da sua majestade, quando ele se levantar para sacudir a terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17706, 'nvi', 'Isaías', 'is', 2, 20, 'Naquele dia os homens atirarão aos ratos e aos morcegos os ídolos de prata e os ídolos de ouro, que fizeram para adorar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17707, 'nvi', 'Isaías', 'is', 2, 21, 'Fugirão para as cavernas das rochas e para as brechas dos penhascos, por causa do terror que vem do Senhor e do esplendor da sua majestade, quando ele se levantar para sacudir a terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17708, 'nvi', 'Isaías', 'is', 2, 22, 'Parem de confiar no homem, cuja vida não passa de um sopro em suas narinas. Que valor ele tem?');
