INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18097, 'nvi', 'Isaías', 'is', 24, 1, 'Vejam! O Senhor vai arrasar a terra e devastá-la; arruinará sua superfície e espalhará seus habitantes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18098, 'nvi', 'Isaías', 'is', 24, 2, 'Será o mesmo para o sacerdote e o povo, para o senhor e o servo, para a senhora e a serva, para o vendedor e o comprador, para quem toma emprestado e quem empresta, para o devedor e o credor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18099, 'nvi', 'Isaías', 'is', 24, 3, 'A terra será completamente arrasada e totalmente saqueada. Quem falou esta palavra foi o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18100, 'nvi', 'Isaías', 'is', 24, 4, 'A terra seca-se e murcha, o mundo definha e murcha, definham os nobres da terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18101, 'nvi', 'Isaías', 'is', 24, 5, 'A terra está contaminada pelos seus habitantes, porque desobedeceram às leis, violaram os decretos e quebraram a aliança eterna.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18102, 'nvi', 'Isaías', 'is', 24, 6, 'Por isso a maldição consome a terra, e seu povo é culpado. Por isso os habitantes da terra são consumidos pelo fogo, ao ponto de sobrarem pouquíssimos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18103, 'nvi', 'Isaías', 'is', 24, 7, 'O vinho novo vai-se, e a videira murcha; todos os que se divertiam gemem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18104, 'nvi', 'Isaías', 'is', 24, 8, 'O som festivo dos tamborins foi silenciado, o barulho dos que se alegram parou, a harpa cheia de júbilo está muda.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18105, 'nvi', 'Isaías', 'is', 24, 9, 'Já não bebem vinho entoando canções; a bebida fermentada é amarga para os que a bebem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18106, 'nvi', 'Isaías', 'is', 24, 10, 'A cidade vã está em ruínas; a entrada de cada casa está fechada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18107, 'nvi', 'Isaías', 'is', 24, 11, 'Nas ruas clamam por vinho; toda a alegria chegou ao fim, toda celebração foi eliminada da terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18108, 'nvi', 'Isaías', 'is', 24, 12, 'A cidade foi deixada em ruínas, sua porta feita em pedaços.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18109, 'nvi', 'Isaías', 'is', 24, 13, 'Assim será na terra, entre as nações, como quando se usa a vara na oliveira ou se buscam os restos das uvas após a colheita.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18110, 'nvi', 'Isaías', 'is', 24, 14, 'Erguem as vozes, cantam de alegria; desde o Ocidente aclamam a majestade do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18111, 'nvi', 'Isaías', 'is', 24, 15, 'Dêem glória, pois, ao Senhor no Oriente, e nas ilhas do mar exaltem o nome do Senhor, o Deus de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18112, 'nvi', 'Isaías', 'is', 24, 16, 'Desde os confins da terra ouvimos cantarem: "Glória seja dada ao Justo! " Mas eu disse: "Que desgraça! Que desgraça! Ai de mim! Os traidores traem! Os traidores agem traiçoeiramente! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18113, 'nvi', 'Isaías', 'is', 24, 17, 'Pavor, cova e laço o aguardam, ó habitantes da terra!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18114, 'nvi', 'Isaías', 'is', 24, 18, 'Quem fugir ao grito de terror cairá na cova; quem sair da cova será pego no laço. Abertas estão as comportas dos céus; tremem os alicerces da terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18115, 'nvi', 'Isaías', 'is', 24, 19, 'A terra foi despedaçada, está destruída, totalmente abalada!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18116, 'nvi', 'Isaías', 'is', 24, 20, 'A terra cambaleia como um bêbado, balança como uma cabana ao vento; tão pesada sobre ela é a culpa de sua rebelião que cai para nunca mais se levantar!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18117, 'nvi', 'Isaías', 'is', 24, 21, 'Naquele dia o Senhor castigará os poderes em cima nos céus e os reis em baixo na terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18118, 'nvi', 'Isaías', 'is', 24, 22, 'Eles serão arrebanhados como prisioneiros numa masmorra, trancados numa prisão e castigados depois de muitos dias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18119, 'nvi', 'Isaías', 'is', 24, 23, 'A lua ficará humilhada, e o sol, envergonhado; pois o Senhor dos Exércitos reinará no monte Sião e em Jerusalém, glorioso na presença dos seus líderes!');
