INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18132, 'nvi', 'Isaías', 'is', 26, 1, 'Naquele dia este cântico será entoado em Judá: Temos uma cidade forte; Deus estabelece salvação como muros e trincheiras.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18133, 'nvi', 'Isaías', 'is', 26, 2, 'Abram as portas para que entre a nação justa, a nação que se mantém fiel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18134, 'nvi', 'Isaías', 'is', 26, 3, 'Tu guardarás em perfeita paz aquele cujo propósito está firme, porque em ti confia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18135, 'nvi', 'Isaías', 'is', 26, 4, 'Confiem para sempre no Senhor, pois o Senhor, somente o Senhor, é a Rocha eterna.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18136, 'nvi', 'Isaías', 'is', 26, 5, 'Ele humilha os que habitam nas alturas, rebaixa e arrasa a cidade altiva, e a lança ao pó.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18137, 'nvi', 'Isaías', 'is', 26, 6, 'Pés as pisoteiam, os pés dos necessitados, os passos dos pobres.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18138, 'nvi', 'Isaías', 'is', 26, 7, 'A vereda do justo é plana; tu, que és reto, torna suave o caminho do justo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18139, 'nvi', 'Isaías', 'is', 26, 8, 'Também no caminho das tuas ordenanças esperamos em ti, Senhor. O teu nome e a tua lembrança são o desejo do nosso coração.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18140, 'nvi', 'Isaías', 'is', 26, 9, 'A minha alma suspira por ti durante a noite; e logo cedo o meu espírito por ti anseia, pois, quando se vêem na terra as tuas ordenanças, os habitantes do mundo aprendem justiça.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18141, 'nvi', 'Isaías', 'is', 26, 10, 'Ainda que se tenha compaixão do ímpio, ele não aprenderá a justiça; na terra da retidão ele age perversamente, e não vê a majestade do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18142, 'nvi', 'Isaías', 'is', 26, 11, 'Erguida está a tua mão, Senhor, mas eles não a vêem! Que vejam o teu zelo para com o teu povo e se envergonhem; que o fogo reservado para os teus adversários os consuma.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18143, 'nvi', 'Isaías', 'is', 26, 12, 'Senhor, tu estabeleces a paz para nós; tudo o que alcançamos, fizeste-o para nós.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18144, 'nvi', 'Isaías', 'is', 26, 13, 'Ó Senhor nosso Deus, outros senhores além de ti nos tem dominado, mas só ao teu nome honramos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18145, 'nvi', 'Isaías', 'is', 26, 14, 'Agora eles estão mortos, não viverão; são sombras, não ressuscitarão. Tu os castigaste e os levaste à ruína; apagaste por completo a lembrança deles!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18146, 'nvi', 'Isaías', 'is', 26, 15, 'Fizeste crescer a nação, Senhor; sim, fizeste crescer a nação. De glória te revestiste; alargaste todas as fronteiras da nossa terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18147, 'nvi', 'Isaías', 'is', 26, 16, 'Senhor, no meio de aflição te buscaram; quando os disciplinaste sussurraram uma oração.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18148, 'nvi', 'Isaías', 'is', 26, 17, 'Como a mulher grávida prestes a dar à luz se contorce e grita de dor, assim estamos nós na tua presença, ó Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18149, 'nvi', 'Isaías', 'is', 26, 18, 'Nós engravidamos e nos contorcemos de dor, mas demos à luz o vento. Não trouxemos salvação à terra; não demos à luz os habitantes do mundo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18150, 'nvi', 'Isaías', 'is', 26, 19, 'Mas os teus mortos viverão; seus corpos ressuscitarão. Vocês, que voltaram ao pó, acordem e cantem de alegria. O teu orvalho é orvalho de luz; a terra dará à luz os seus mortos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18151, 'nvi', 'Isaías', 'is', 26, 20, 'Vá, meu povo, entre em seus quartos e tranque as portas; esconda-se por um momento, até que tenha passado a ira dele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18152, 'nvi', 'Isaías', 'is', 26, 21, 'Vejam! O Senhor está saindo da sua habitação para castigar os moradores da terra por suas iniqüidades. A terra mostrará o sangue derramado sobre ela; não mais encobrirá os seus mortos.');
