INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18166, 'nvi', 'Isaías', 'is', 28, 1, 'Ai daquela coroa, orgulho dos bêbados de Efraim, e de sua magnífica beleza, situada nos altos de um vale fértil e que agora é como uma flor murcha, Ai dos que são dominados pelo vinho!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18167, 'nvi', 'Isaías', 'is', 28, 2, 'Vejam! O Senhor envia alguém que é poderoso e forte. Como chuva de granizo e vento destruidor, como violento aguaceiro e tromba d’água inundante, ele a lançará com força ao chão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18168, 'nvi', 'Isaías', 'is', 28, 3, 'A coroa orgulhosa dos bêbados de Efraim será pisoteada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18169, 'nvi', 'Isaías', 'is', 28, 4, 'Sua magnífica beleza, localizada na cabeça de um vale fértil, é agora uma flor que murcha. Ela será como figo maduro antes da colheita, quem o vê, logo o apanha e o come.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18170, 'nvi', 'Isaías', 'is', 28, 5, 'Naquele dia o Senhor dos Exércitos será uma coroa gloriosa, um belo diadema para o remanescente do seu povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18171, 'nvi', 'Isaías', 'is', 28, 6, 'Ele será um espírito de justiça para aquele que se assenta para julgar, e força para os que fazem recuar das portas a guerra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18172, 'nvi', 'Isaías', 'is', 28, 7, 'E estes também cambaleiam pelo efeito do vinho, e não ficam de pé por causa da bebida fermentada: Os sacerdotes e os profetas cambaleiam por causa da bebida fermentada e estão desorientados devido ao vinho; eles não conseguem ficar de pé por causa da bebida fermentada, confundem-se quando têm visões, tropeçam quando devem dar um veredicto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18173, 'nvi', 'Isaías', 'is', 28, 8, 'Todas as mesas estão cobertas de vômito e não há um só lugar limpo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18174, 'nvi', 'Isaías', 'is', 28, 9, '"Quem é que ele está tentando ensinar? A quem está explicando a sua mensagem? A crianças desmamadas e a bebês recém-tirados do seio materno?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18175, 'nvi', 'Isaías', 'is', 28, 10, 'Pois o que se diz é: ‘Ordem sobre ordem, ordem sobre ordem, regra e mais regra, regra e mais regra; um pouco aqui, um pouco ali’. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18176, 'nvi', 'Isaías', 'is', 28, 11, 'Pois bem, com lábios trôpegos e língua estranha Deus falará a este povo,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18177, 'nvi', 'Isaías', 'is', 28, 12, 'ao qual dissera: "Este é o lugar de descanso. Deixem descansar o exausto. Este é o lugar de repouso! " Mas eles não quiseram ouvir.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18178, 'nvi', 'Isaías', 'is', 28, 13, 'Por isso o Senhor lhes dirá: "Ordem sobre ordem, ordem sobre ordem, regra e mais regra, regra e mais regra; um pouco aqui, um pouco ali", para que saiam, caiam de costas, firam-se, fiquem presos no laço e sejam capturados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18179, 'nvi', 'Isaías', 'is', 28, 14, 'Portanto, ouçam a palavra do Senhor, zombadores, vocês, que dominam este povo em Jerusalém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18180, 'nvi', 'Isaías', 'is', 28, 15, 'Vocês se vangloriam, dizendo: "Fizemos um pacto com a morte, com a sepultura fizemos um acordo. Quando vier a calamidade destruidora, não nos atingirá, pois da mentira fizemos o nosso refúgio e na falsidade temos o nosso esconderijo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18181, 'nvi', 'Isaías', 'is', 28, 16, 'Por isso diz o Soberano Senhor: "Eis que ponho em Sião uma pedra, uma pedra já experimentada, uma preciosa pedra angular para alicerce seguro; aquele que confia, jamais será abalado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18182, 'nvi', 'Isaías', 'is', 28, 17, 'Farei do juízo a linha de medir e da justiça o fio de prumo; o granizo varrerá o seu falso refúgio, e as águas inundarão o seu abrigo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18183, 'nvi', 'Isaías', 'is', 28, 18, 'Seu pacto com a morte será anulado; seu acordo com a sepultura não subsistirá. Quando vier a calamidade destruidora, vocês serão arrastados por ela.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18184, 'nvi', 'Isaías', 'is', 28, 19, 'Todas as vezes que vier, os arrastará; passará manhã após manhã, de dia e de noite". A compreensão desta mensagem trará pavor total.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18185, 'nvi', 'Isaías', 'is', 28, 20, 'A cama é curta demais para alguém deitar-se, e o cobertor é estreito demais para cobrir-se.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18186, 'nvi', 'Isaías', 'is', 28, 21, 'O Senhor se levantará como fez no monte Perazim, mostrará sua ira como no vale de Gibeom, para realizar sua obra, obra muito estranha, e cumprir sua tarefa, tarefa misteriosa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18187, 'nvi', 'Isaías', 'is', 28, 22, 'Agora, parem com a zombaria; caso contrário, as suas correntes ficarão mais pesadas; o Senhor, o Senhor dos Exércitos falou-me da destruição decretada contra o território inteiro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18188, 'nvi', 'Isaías', 'is', 28, 23, 'Ouçam, escutem a minha voz; prestem atenção, ouçam o que eu digo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18189, 'nvi', 'Isaías', 'is', 28, 24, 'Quando o agricultor ara a terra para o plantio, só faz isso o tempo todo? Só fica abrindo sulcos e gradeando o solo?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18190, 'nvi', 'Isaías', 'is', 28, 25, 'Depois de nivelado o solo, ele não semeia o endro e não espalha as sementes do cominho? Não planta o trigo no lugar certo, a cevada no terreno próprio e o trigo duro nas bordas?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18191, 'nvi', 'Isaías', 'is', 28, 26, 'O seu Deus o instrui e lhe ensina o caminho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18192, 'nvi', 'Isaías', 'is', 28, 27, 'Não se debulha o endro com trilhadeira, e sobre o cominho não se faz passar roda de carro; tira-se o endro com vara, e o cominho com um pedaço de pau.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18193, 'nvi', 'Isaías', 'is', 28, 28, 'É preciso moer o cereal para fazer pão; por isso ninguém o fica trilhando para sempre. Fazem passar as rodas da trilhadeira sobre o trigo, mas os seus cavalos não o trituram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18194, 'nvi', 'Isaías', 'is', 28, 29, 'Isso tudo vem da parte do Senhor dos Exércitos, maravilhoso em conselhos e magnífico em sabedoria.');
