INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17709, 'nvi', 'Isaías', 'is', 3, 1, 'Vejam! O Soberano, o Senhor dos Exércitos, logo irá retirar de Jerusalém e de Judá todo o seu sustento, tanto o suprimento de comida como o suprimento de água,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17710, 'nvi', 'Isaías', 'is', 3, 2, 'e também o herói e o guerreiro, o juiz e o profeta, o adivinho e a autoridade,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17711, 'nvi', 'Isaías', 'is', 3, 3, 'o capitão e o nobre, o conselheiro, o conhecedor de magia e o perito em maldições.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17712, 'nvi', 'Isaías', 'is', 3, 4, 'Porei jovens no governo; irresponsáveis dominarão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17713, 'nvi', 'Isaías', 'is', 3, 5, 'O povo oprimirá a si mesmo: homem contra homem, cada um contra o seu próximo. O jovem se levantará contra o idoso, o desprezível contra o nobre.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17714, 'nvi', 'Isaías', 'is', 3, 6, 'Um homem agarrará seu irmão; um da família de seu pai, e lhe dirá: "Você pelo menos tem um manto, seja o nosso governante; assuma o poder sobre este monte de ruínas! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17715, 'nvi', 'Isaías', 'is', 3, 7, 'Mas naquele dia ele exclamará: "Não tenho remédios, não há comida nem roupa em minha casa; não me nomeiem governante do povo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17716, 'nvi', 'Isaías', 'is', 3, 8, 'Jerusalém está em ruínas, e o povo de Judá está caído; suas palavras e suas ações são contra o Senhor, desafiando a sua presença gloriosa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17717, 'nvi', 'Isaías', 'is', 3, 9, 'O jeito que olham testifica contra eles; mostram seu pecado como Sodoma, sem nada esconder. Ai deles! Pois trouxeram desgraça sobre si mesmos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17718, 'nvi', 'Isaías', 'is', 3, 10, 'Digam aos justos que tudo lhes irá bem, pois comerão do fruto de suas ações.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17719, 'nvi', 'Isaías', 'is', 3, 11, 'Mas, ai dos ímpios! Pois tudo lhes irá mal! Terão a retribuição pelo que fizeram as suas mãos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17720, 'nvi', 'Isaías', 'is', 3, 12, 'Meu povo é oprimido por uma criança; mulheres dominam sobre ele. Meu povo, os seus guias o enganam, e o desviam do caminho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17721, 'nvi', 'Isaías', 'is', 3, 13, 'O Senhor toma o seu lugar no tribunal; levanta-se para julgar os povos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17722, 'nvi', 'Isaías', 'is', 3, 14, 'O Senhor entra em juízo contra as autoridades e contra os líderes do seu povo. "Vocês arruinaram a vinha, e o que foi roubado dos necessitados está nas suas casas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17723, 'nvi', 'Isaías', 'is', 3, 15, 'Que pretendem vocês, ao esmagarem o meu povo, e ao moerem o rosto dos necessitados? " Quem pergunta é o Senhor, o Senhor dos Exércitos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17724, 'nvi', 'Isaías', 'is', 3, 16, 'O Senhor diz: "Por causa da arrogância das mulheres de Sião, que caminham de cabeça erguida, flertando com os olhos, desfilando com passos curtos, com enfeites tinindo em seus calcanhares,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17725, 'nvi', 'Isaías', 'is', 3, 17, 'o Senhor rapará a cabeça das mulheres de Sião; o Senhor porá a descoberto as suas vergonhas".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17726, 'nvi', 'Isaías', 'is', 3, 18, 'Naquele dia o Senhor arrancará os enfeites delas: as pulseiras, as testeiras e os colares;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17727, 'nvi', 'Isaías', 'is', 3, 19, 'os pendentes, os braceletes e os véus,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17728, 'nvi', 'Isaías', 'is', 3, 20, 'os enfeites de cabeça, as correntinhas de tornozelo, os cintos, os talismãs e os amuletos;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17729, 'nvi', 'Isaías', 'is', 3, 21, 'os anéis e os enfeites para o nariz;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17730, 'nvi', 'Isaías', 'is', 3, 22, 'as roupas caras, as capas, as mantilhas, e as bolsas;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17731, 'nvi', 'Isaías', 'is', 3, 23, 'os espelhos, as roupas de linho, as tiaras e os xales.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17732, 'nvi', 'Isaías', 'is', 3, 24, 'Em vez de perfume haverá mau cheiro, em vez de cintos, corda, em vez de belos penteados, calvície, em vez de roupas finas, vestes de lamento, em vez de beleza, cicatrizes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17733, 'nvi', 'Isaías', 'is', 3, 25, 'Seus homens cairão ao fio da espada; seus guerreiros morrerão no combate.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17734, 'nvi', 'Isaías', 'is', 3, 26, 'As portas de Sião lamentarão e prantearão por causa disso; e sem nada, ela se assentará no chão.');
