INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18261, 'nvi', 'Isaías', 'is', 32, 1, 'Vejam! Um rei reinará com retidão, e príncipes governarão com justiça.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18262, 'nvi', 'Isaías', 'is', 32, 2, 'Cada homem será como um esconderijo contra o vento e um abrigo contra a tempestade, como correntes de água numa terra seca e como a sombra de uma grande rocha no deserto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18263, 'nvi', 'Isaías', 'is', 32, 3, 'Então os olhos dos que vêem não estarão mais fechados, e os ouvidos dos que ouvem escutarão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18264, 'nvi', 'Isaías', 'is', 32, 4, 'A mente do precipitado saberá julgar, e a língua gaguejante falará com facilidade e clareza.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18265, 'nvi', 'Isaías', 'is', 32, 5, 'O tolo já não será chamado nobre e o homem sem caráter não será tido em alta estima.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18266, 'nvi', 'Isaías', 'is', 32, 6, 'Pois o insensato fala com insensatez e só pensa no mal: Ele pratica a maldade e espalha mentiras sobre o Senhor; deixa o faminto sem nada e priva de água o sedento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18267, 'nvi', 'Isaías', 'is', 32, 7, 'As artimanhas do homem sem caráter são perversas; ele inventa planos maldosos para destruir com mentiras o pobre, mesmo quando a súplica deste é justa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18268, 'nvi', 'Isaías', 'is', 32, 8, 'Mas o homem nobre faz planos nobres, e graças aos seus feitos nobres permanece firme.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18269, 'nvi', 'Isaías', 'is', 32, 9, 'Vocês, mulheres tão complacentes, levantem-se e escutem-me! Vocês, filhas que se sentem seguras, ouçam o que lhes vou dizer!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18270, 'nvi', 'Isaías', 'is', 32, 10, 'Daqui a pouco mais de um ano, vocês, que se sentem seguras, ficarão apavoradas; a colheita de uvas falhará, e a colheita de frutas não virá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18271, 'nvi', 'Isaías', 'is', 32, 11, 'Tremam, vocês, mulheres tranqüilas! Estremeçam, vocês, que se sentem seguras! Arranquem suas vestes, e vistam roupas de lamento na cintura.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18272, 'nvi', 'Isaías', 'is', 32, 12, 'Batam no peito e chorem pelos campos agradáveis, pelas videiras frutíferas');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18273, 'nvi', 'Isaías', 'is', 32, 13, 'e pela terra do meu povo, terra infestada de espinhos e roseiras bravas; sim, pranteiem por todas as casas cheias de júbilo e por esta cidade exultante.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18274, 'nvi', 'Isaías', 'is', 32, 14, 'A fortaleza será abandonada, a cidade barulhenta ficará deserta, a cidadela e a torre de sentinela se tornarão covis, uma delícia para os jumentos, uma pastagem para os rebanhos,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18275, 'nvi', 'Isaías', 'is', 32, 15, 'até que sobre nós o Espírito seja derramado do alto, e o deserto se transforme em campo fértil, e o campo fértil pareça uma floresta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18276, 'nvi', 'Isaías', 'is', 32, 16, 'A justiça habitará no deserto, e a retidão viverá no campo fértil.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18277, 'nvi', 'Isaías', 'is', 32, 17, 'O fruto da justiça será paz; o resultado da justiça será tranqüilidade e confiança para sempre.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18278, 'nvi', 'Isaías', 'is', 32, 18, 'O meu povo viverá em locais pacíficos, em casas seguras, em tranqüilos lugares de descanso,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18279, 'nvi', 'Isaías', 'is', 32, 19, 'mesmo que a saraiva arrase a floresta e a cidade seja nivelada ao pó.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18280, 'nvi', 'Isaías', 'is', 32, 20, 'Como vocês serão felizes, semeando perto das águas, e deixando soltos os bois e os jumentos!');
