INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18281, 'nvi', 'Isaías', 'is', 33, 1, 'Ai de você, destruidor, que ainda não foi destruído! Ai de você, traidor, que não foi traído! Quando você parar de destruir, será destruído; quando parar de trair, será traído.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18282, 'nvi', 'Isaías', 'is', 33, 2, 'Senhor, tem misericórdia de nós; pois por ti esperamos! Sê tu a nossa força cada manhã, nossa salvação na hora do perigo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18283, 'nvi', 'Isaías', 'is', 33, 3, 'Diante do trovão da tua voz, os povos fogem; quando te levantas, dispersam-se as nações.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18284, 'nvi', 'Isaías', 'is', 33, 4, 'O seu despojo, ó nações, será reunido como fazem os gafanhotos novos; como gafanhotos em nuvem, dele se apoderam os homens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18285, 'nvi', 'Isaías', 'is', 33, 5, 'O Senhor é exaltado, pois habita no alto; ele encherá Sião de retidão e justiça.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18286, 'nvi', 'Isaías', 'is', 33, 6, 'Ele será o firme fundamento nos tempos a que você pertence, uma grande riqueza de salvação, sabedoria e conhecimento; o temor do Senhor é a chave desse tesouro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18287, 'nvi', 'Isaías', 'is', 33, 7, 'Vejam! Os seus heróis gritam nas ruas; os embaixadores da paz choram amargamente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18288, 'nvi', 'Isaías', 'is', 33, 8, 'As estradas estão abandonadas, ninguém viaja por elas. Rompeu-se o acordo, suas testemunhas são desprezadas, não se respeita ninguém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18289, 'nvi', 'Isaías', 'is', 33, 9, 'A terra pranteia e fraqueja, o Líbano murcha, envergonhado; Sarom é como a Arabá, e Basã e o Carmelo perdem sua folhagem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18290, 'nvi', 'Isaías', 'is', 33, 10, '"Agora me levantarei", diz o Senhor. "Agora eu me erguerei; agora serei exaltado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18291, 'nvi', 'Isaías', 'is', 33, 11, 'Vocês concebem palha, e dão à luz restolho; seu sopro é um fogo que o consome.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18292, 'nvi', 'Isaías', 'is', 33, 12, 'Os povos serão queimados como se faz com a cal; como espinheiros cortados, serão postos no fogo. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18293, 'nvi', 'Isaías', 'is', 33, 13, 'Vocês que estão longe, atentem para o que eu fiz! Vocês, que estão perto, reconheçam o meu poder!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18294, 'nvi', 'Isaías', 'is', 33, 14, 'Em Sião os pecadores estão aterrorizados; o tremor se apodera dos ímpios: "Quem de nós pode conviver com o fogo consumidor? Quem de nós pode conviver com a chama eterna? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18295, 'nvi', 'Isaías', 'is', 33, 15, 'Aquele que anda corretamente e fala o que é reto, que recusa o lucro injusto, cuja mão não aceita suborno, que tapa os ouvidos para as tramas de assassinatos e fecha os olhos para não contemplar o mal,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18296, 'nvi', 'Isaías', 'is', 33, 16, 'é esse o homem que habitará nas alturas; seu refúgio será a fortaleza das rochas; terá suprimento de pão, e água não lhe faltará.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18297, 'nvi', 'Isaías', 'is', 33, 17, 'Seus olhos verão o rei em seu esplendor e vislumbrarão o território em toda a sua extensão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18298, 'nvi', 'Isaías', 'is', 33, 18, 'Em seus pensamentos você lembrará terrores passados: "Onde está o oficial maior? Onde está o que recebia tributos? Onde o encarregado das torres? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18299, 'nvi', 'Isaías', 'is', 33, 19, 'Você não tornará a ver aquele povo arrogante, aquele povo de fala obscura, com sua língua estranha, incompreensível.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18300, 'nvi', 'Isaías', 'is', 33, 20, 'Olhe para Sião, a cidade das nossas festas; seus olhos verão Jerusalém, morada pacífica, tenda que não será removida; suas estacas jamais serão arrancadas, nem se romperá nenhuma de suas cordas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18301, 'nvi', 'Isaías', 'is', 33, 21, 'Ali o Senhor será o Poderoso para nós. Será como uma região de rios e canais largos, mas nenhum navio a remo os percorrerá, e nenhuma nau poderosa velejará neles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18302, 'nvi', 'Isaías', 'is', 33, 22, 'Pois o Senhor é o nosso juiz, o Senhor é o nosso legislador, o Senhor é o nosso rei; é ele que nos salvará.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18303, 'nvi', 'Isaías', 'is', 33, 23, 'Suas cordas se afrouxam: O mastro não está firme, as velas não estão estendidas. Então será dividida grande quantidade de despojos, e até o aleijado levará sua presa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18304, 'nvi', 'Isaías', 'is', 33, 24, 'Nenhum morador de Sião dirá: "Estou doente! " E os pecados dos que ali habitam serão perdoados.');
