INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18332, 'nvi', 'Isaías', 'is', 36, 1, 'No décimo quarto ano do reinado de Ezequias, Senaqueribe, rei da Assíria, atacou todas as cidades fortificadas de Judá e se apossou delas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18333, 'nvi', 'Isaías', 'is', 36, 2, 'Então o rei da Assíria enviou de Láquis ao rei Ezequias, em Jerusalém, seu comandante com um grande exército. Quando o comandante parou no aqueduto do açude superior, na estrada que leva ao campo do Lavandeiro,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18334, 'nvi', 'Isaías', 'is', 36, 3, 'o administrador do palácio, Eliaquim, filho de Hilquias, o secretário Sebna e o arquivista real Joá, filho de Asafe, foram ao encontro dele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18335, 'nvi', 'Isaías', 'is', 36, 4, 'E o comandante de campo falou: "Digam a Ezequias: "Assim diz o grande rei, o rei da Assíria: ‘Em que você está baseando essa sua confiança?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18336, 'nvi', 'Isaías', 'is', 36, 5, 'Você diz que tem estratégia e força militar; mas só palavras vãs. Em quem você confia, para rebelar-se contra mim?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18337, 'nvi', 'Isaías', 'is', 36, 6, 'Pois veja! Agora você está confiando no Egito, aquela cana esmagada, que fura a mão de quem nela se apóia! Assim é o faraó, o rei do Egito, para todos os que dele dependem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18338, 'nvi', 'Isaías', 'is', 36, 7, 'E se você me disser: "No Senhor, o nosso Deus, confiamos"; não são dele os altos e os altares que Ezequias removeu, dizendo a Judá e a Jerusalém: "Vocês devem adorar aqui, diante deste altar"? ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18339, 'nvi', 'Isaías', 'is', 36, 8, '"Façamos agora um tratado com o meu senhor, o rei da Assíria: Eu lhe darei dois mil cavalos — se você puder pôr cavaleiros neles!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18340, 'nvi', 'Isaías', 'is', 36, 9, 'Como então você poderá repelir um só dos menores oficiais do meu senhor, confiando que o Egito lhe dará carros e cavaleiros?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18341, 'nvi', 'Isaías', 'is', 36, 10, 'Além disso, você pensa que vim atacar e destruir esta nação sem o Senhor? O próprio Senhor me mandou marchar contra esta nação e destruí-la".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18342, 'nvi', 'Isaías', 'is', 36, 11, 'Então Eliaquim, Sebna e Joá disseram ao comandante: "Por favor, fala com os seus servos em aramaico, pois entendemos essa língua. Não fales em hebraico, pois assim o povo que está sobre os muros entenderá".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18343, 'nvi', 'Isaías', 'is', 36, 12, 'O comandante, porém, respondeu: "Pensam que o meu senhor mandou-me dizer estas coisas só a vocês e ao seu senhor, e não aos homens que estão sentados no muro? Pois, como vocês, eles terão que comer as próprias fezes e beber a própria urina! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18344, 'nvi', 'Isaías', 'is', 36, 13, 'E o comandante se pôs de pé e falou alto, em hebraico: "Ouçam as palavras do grande rei, do rei da Assíria!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18345, 'nvi', 'Isaías', 'is', 36, 14, 'Não deixem que Ezequias os engane. Ele não poderá livrá-los!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18346, 'nvi', 'Isaías', 'is', 36, 15, 'Não deixem Ezequias convencê-los a confiar no Senhor, quando diz: ‘Certamente o Senhor nos livrará; esta cidade não será entregue nas mãos do rei da Assíria’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18347, 'nvi', 'Isaías', 'is', 36, 16, '"Não dêem atenção a Ezequias. Assim diz o rei da Assíria: ‘Venham fazer as pazes comigo. Então cada um de vocês comerá de sua própria videira e de sua própria figueira, e beberá água de sua própria cisterna,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18348, 'nvi', 'Isaías', 'is', 36, 17, 'até que eu os leve a uma terra como a de vocês: terra de cereal e de vinho, terra de pão e de vinhas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18349, 'nvi', 'Isaías', 'is', 36, 18, '" ‘Não deixem que Ezequias os engane quando diz que o Senhor os livrará. Alguma vez o deus de qualquer nação livrou sua terra das mãos do rei da Assíria?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18350, 'nvi', 'Isaías', 'is', 36, 19, 'Onde estão os deuses de Hamate e de Arpade? Onde estão os deuses de Sefarvaim? Eles livraram Samaria das minhas mãos?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18351, 'nvi', 'Isaías', 'is', 36, 20, 'Quem dentre todos os deuses dessas nações conseguiu livrar a sua terra? Como então o Senhor poderá livrar Jerusalém das minhas mãos? ’ "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18352, 'nvi', 'Isaías', 'is', 36, 21, 'Mas o povo ficou em silêncio e nada respondeu, porque o rei dera esta ordem: "Não lhe respondam".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18353, 'nvi', 'Isaías', 'is', 36, 22, 'Então o administrador do palácio, Eliaquim, filho de Hilquias, o secretário Sebna e o arquivista Joá, filho de Asafe, com as vestes rasgadas, foram contar a Ezequias o que dissera o comandante.');
