INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18354, 'nvi', 'Isaías', 'is', 37, 1, 'Quando o rei Ezequias soube disso, rasgou suas vestes, vestiu pano de saco e entrou no templo do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18355, 'nvi', 'Isaías', 'is', 37, 2, 'Depois enviou o administrador do palácio, Eliaquim, o secretário Sebna e os chefes dos sacerdotes, todos vestidos de pano de saco, ao profeta Isaías, filho de Amoz,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18356, 'nvi', 'Isaías', 'is', 37, 3, 'com esta mensagem: "Assim diz Ezequias: Hoje é dia de angústia, de repreensão e de vergonha, como quando uma criança está a ponto de nascer e não há forças para dá-la à luz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18357, 'nvi', 'Isaías', 'is', 37, 4, 'Talvez o Senhor, o seu Deus, ouça as palavras do comandante de campo, a quem o seu senhor, o rei da Assíria, enviou para zombar do Deus vivo. E que o Senhor, o seu Deus, o repreenda pelas palavras que ouviu. Portanto, ore pelo remanescente que ainda sobrevive".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18358, 'nvi', 'Isaías', 'is', 37, 5, 'Quando os oficiais do rei Ezequias vieram a Isaías,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18359, 'nvi', 'Isaías', 'is', 37, 6, 'este lhes respondeu: "Digam a seu senhor: Assim diz o Senhor: ‘Não tenha medo das palavras que você ouviu, das blasfêmias que os servos do rei da Assíria falaram contra mim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18360, 'nvi', 'Isaías', 'is', 37, 7, 'Porei nele um espírito para que, quando ouvir uma certa notícia, volte à sua própria terra, e ali farei com que seja morto à espada’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18361, 'nvi', 'Isaías', 'is', 37, 8, 'Quando o comandante de campo soube que o rei da Assíria havia partido de Láquis, retirou-se e encontrou o rei lutando contra Libna.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18362, 'nvi', 'Isaías', 'is', 37, 9, 'Ora, Senaqueribe foi informado de que Tiraca, o rei da Etiópia, saíra para lutar contra ele. Quando soube disso, enviou mensageiros a Ezequias com esta mensagem:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18363, 'nvi', 'Isaías', 'is', 37, 10, '"Digam a Ezequias, rei de Judá: Não deixe que o Deus no qual você confia o engane quando diz: ‘Jerusalém não será entregue nas mãos do rei da Assíria’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18364, 'nvi', 'Isaías', 'is', 37, 11, 'Com certeza você ouviu o que os reis da Assíria têm feito a todas as nações, e como as destruíram por completo. E você acha que se livrará?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18365, 'nvi', 'Isaías', 'is', 37, 12, 'Acaso os deuses das nações que foram destruídas pelos meus antepassados os livraram: os deuses de Gozã, de Harã, de Rezefe e dos descendentes de Éden, que estavam em Telassar?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18366, 'nvi', 'Isaías', 'is', 37, 13, 'Onde estão o rei de Hamate, o rei de Arpade, o rei da cidade de Sefarvaim, de Hena e de Iva? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18367, 'nvi', 'Isaías', 'is', 37, 14, 'Ezequias recebeu a carta das mãos dos mensageiros e a leu. Então subiu ao templo do Senhor, apresentou-a diante do Senhor');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18368, 'nvi', 'Isaías', 'is', 37, 15, 'e orou:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18369, 'nvi', 'Isaías', 'is', 37, 16, '"Senhor dos Exércitos, Deus de Israel, cujo trono está entre os querubins, só tu és Deus sobre todos os reinos da terra. Tu fizeste os céus e a terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18370, 'nvi', 'Isaías', 'is', 37, 17, 'Dá ouvidos, Senhor, e ouve; abre os teus olhos, Senhor, e vê; escuta todas as palavras que Senaqueribe enviou para insultar o Deus vivo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18371, 'nvi', 'Isaías', 'is', 37, 18, '"É verdade, Senhor, que os reis assírios fizeram de todas essas nações e de seus territórios um deserto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18372, 'nvi', 'Isaías', 'is', 37, 19, 'Atiraram os deuses delas no fogo e os destruíram, pois em vez de deuses, não passam de madeira e pedra, moldados por mãos humanas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18373, 'nvi', 'Isaías', 'is', 37, 20, 'Agora, Senhor nosso Deus, salva-nos das mãos dele, para que todos os reinos da terra saibam que só tu, Senhor, és Deus".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18374, 'nvi', 'Isaías', 'is', 37, 21, 'Então Isaías, filho de Amoz, enviou uma mensagem a Ezequias: "Assim diz o Senhor, Deus de Israel: ‘Ouvi a sua oração acerca de Senaqueribe, rei da Assíria.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18375, 'nvi', 'Isaías', 'is', 37, 22, 'Esta é a palavra que o Senhor falou contra ele: " ‘A virgem cidade de Sião despreza e zomba de você. A cidade de Jerusalém meneia a cabeça enquanto você foge.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18376, 'nvi', 'Isaías', 'is', 37, 23, 'De quem você zombou e contra quem blasfemou? Contra quem você ergueu a voz e, contra quem levantou seu olhar arrogante? Contra o Santo de Israel!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18377, 'nvi', 'Isaías', 'is', 37, 24, 'Sim, você insultou ao Senhor por meio dos seus mensageiros, dizendo: "Com carros sem conta subi aos mais elevados e inacessíveis cumes do Líbano. Derrubei os seus cedros mais altos, os seus melhores pinheiros. Entrei em suas regiões mais remotas, ao melhor de suas florestas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18378, 'nvi', 'Isaías', 'is', 37, 25, 'Em terras estrangeiras cavei poços e bebi água. Com as solas dos meus pés sequei todos os riachos do Egito".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18379, 'nvi', 'Isaías', 'is', 37, 26, '" ‘Você não soube que há muito eu já o havia ordenado que desde os dias da antigüidade eu o havia planejado? Agora eu o executo, e faço você transformar cidades fortificadas em montões de pedra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18380, 'nvi', 'Isaías', 'is', 37, 27, 'Os seus habitantes, já sem forças, desanimam envergonhados. São como pastagens, como brotos tenros e verdes, como capim no terraço, queimado antes de crescer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18381, 'nvi', 'Isaías', 'is', 37, 28, '" ‘Eu, porém, sei onde você está quando sai e quando retorna, e como você se enfurece contra mim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18382, 'nvi', 'Isaías', 'is', 37, 29, 'Sim, contra mim você se enfurece, o seu atrevimento chegou aos meus ouvidos; por isso, porei o meu anzol em seu nariz e o meu freio em sua boca, e o farei voltar pelo caminho por onde veio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18383, 'nvi', 'Isaías', 'is', 37, 30, '" ‘A você, Ezequias, darei este sinal: " ‘Neste ano vocês comerão do que crescer por si, e no próximo o que daquilo brotar. Mas no terceiro ano semeiem e colham, plantem vinhas e comam o seu fruto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18384, 'nvi', 'Isaías', 'is', 37, 31, 'Mais uma vez um remanescente da tribo de Judá lançará raízes na terra e encherão de frutos os seus ramos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18385, 'nvi', 'Isaías', 'is', 37, 32, 'De Jerusalém sairão sobreviventes e um remanescente do monte Sião. O zelo do Senhor dos Exércitos realizará isso’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18386, 'nvi', 'Isaías', 'is', 37, 33, '"Por isso assim diz o Senhor acerca do rei da Assíria: " ‘Ele não entrará nesta cidade e não atirará aqui uma flecha sequer. Não virá diante dela com escudo nem construirá rampas de cerco contra ela.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18387, 'nvi', 'Isaías', 'is', 37, 34, 'Pelo caminho por onde veio voltará; não entrará nesta cidade’, declara o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18388, 'nvi', 'Isaías', 'is', 37, 35, '" ‘Eu defenderei esta cidade e a salvarei, por amor de mim e por amor de Davi, meu servo! ’ "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18389, 'nvi', 'Isaías', 'is', 37, 36, 'Então o anjo do Senhor saiu e matou cento e oitenta e cinco mil homens no acampamento assírio. Quando o povo se levantou na manhã seguinte, só havia cadáveres!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18390, 'nvi', 'Isaías', 'is', 37, 37, 'Assim Senaqueribe, rei da Assíria, fugiu do acampamento, voltou para Nínive e lá ficou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18391, 'nvi', 'Isaías', 'is', 37, 38, 'Certo dia, quando adorava no templo de seu deus Nisroque, seus filhos Adrameleque e Sarezer o feriram à espada, e fugiram para a terra de Ararate. E seu filho Esar-Hadom foi o seu sucessor.');
