INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18392, 'nvi', 'Isaías', 'is', 38, 1, 'Naqueles dias Ezequias ficou doente, à beira da morte. O profeta Isaías, filho de Amoz, foi visitá-lo e lhe disse: "Assim diz o Senhor: Ponha a casa em ordem, porque você vai morrer; você não se recuperará".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18393, 'nvi', 'Isaías', 'is', 38, 2, 'Ezequias virou o rosto para a parede e orou ao Senhor:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18394, 'nvi', 'Isaías', 'is', 38, 3, '"Lembra-te, Senhor, de como tenho te servido com fidelidade e com devoção sincera, e tenho feito o que tu aprovas". E Ezequias chorou amargamente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18395, 'nvi', 'Isaías', 'is', 38, 4, 'Então a palavra do Senhor veio a Isaías:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18396, 'nvi', 'Isaías', 'is', 38, 5, '"Vá dizer a Ezequias: ‘Assim diz o Senhor, o Deus de seu antepassado Davi: Ouvi sua oração e vi suas lágrimas; acrescentarei quinze anos à sua vida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18397, 'nvi', 'Isaías', 'is', 38, 6, 'E eu livrarei você e esta cidade das mãos do rei da Assíria. Eu defenderei esta cidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18398, 'nvi', 'Isaías', 'is', 38, 7, '" ‘Este é o sinal de que o Senhor fará o que prometeu:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18399, 'nvi', 'Isaías', 'is', 38, 8, 'Farei a sombra do sol retroceder os dez degraus que ela já cobriu na escadaria de Acaz’ ". E a luz do sol retrocedeu os dez degraus que tinha avançado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18400, 'nvi', 'Isaías', 'is', 38, 9, 'Depois de recuperar-se dessa doença, Ezequias, rei de Judá, escreveu o seguinte:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18401, 'nvi', 'Isaías', 'is', 38, 10, 'Eu disse: "No vigor da minha vida tenho que passar pelas portas da sepultura e ser roubado do restante dos meus anos? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18402, 'nvi', 'Isaías', 'is', 38, 11, 'Eu disse: "Não tornarei a ver o Senhor, o Senhor, na terra dos viventes; não olharei mais para a humanidade nem estarei mais com os que agora habitam neste mundo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18403, 'nvi', 'Isaías', 'is', 38, 12, 'A minha casa foi derrubada e tirada de mim, como se fosse uma tenda de pastor. A minha vida foi enovelada, como faz o tecelão, e ele me cortou como um pedaço de tecido; dia e noite foste acabando comigo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18404, 'nvi', 'Isaías', 'is', 38, 13, 'Esperei pacientemente até o alvorecer, mas como um leão ele quebrou todos os meus ossos; dia e noite foste acabando comigo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18405, 'nvi', 'Isaías', 'is', 38, 14, 'Gritei como um andorinhão, como um tordo; gemi como uma pomba chorosa. Olhando para os céus, enfraqueceram-se os meus olhos. Estou aflito, ó Senhor, vem em meu auxílio! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18406, 'nvi', 'Isaías', 'is', 38, 15, 'Mas, que posso dizer? Ele falou comigo, e ele mesmo fez isso. Andarei humildemente toda a minha vida, por causa dessa aflição da minha alma.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18407, 'nvi', 'Isaías', 'is', 38, 16, 'Senhor, por tais coisas os homens vivem, e por elas também vive o meu espírito. Tu me restauraste a saúde e deixaste-me viver.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18408, 'nvi', 'Isaías', 'is', 38, 17, 'Foi para o meu benefício que tanto sofri. Em teu amor me guardaste da cova da destruição; lançaste para trás de ti todos os meus pecados,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18409, 'nvi', 'Isaías', 'is', 38, 18, 'pois a sepultura não pode louvar-te, a morte não pode cantar o teu louvor. Aqueles que descem à cova não podem esperar pela tua fidelidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18410, 'nvi', 'Isaías', 'is', 38, 19, 'Os vivos, somente os vivos, te louvam, como hoje estou fazendo; os pais contam a tua fidelidade a seus filhos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18411, 'nvi', 'Isaías', 'is', 38, 20, 'O Senhor me salvou. Cantaremos com instrumentos de corda todos os dias de nossa vida no templo do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18412, 'nvi', 'Isaías', 'is', 38, 21, 'Isaías dissera: "Apliquem um emplastro de figos no furúnculo, e ele se recuperará".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18413, 'nvi', 'Isaías', 'is', 38, 22, 'Ezequias tinha perguntado: "Qual será o sinal de que subirei ao templo do Senhor? "');
