INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17735, 'nvi', 'Isaías', 'is', 4, 1, 'Naquele dia sete mulheres agarrarão um homem e dirão: "Nós mesmas providenciaremos nossa comida e nossas roupas; apenas case-se conosco e livre-nos da vergonha de sermos solteiras! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17736, 'nvi', 'Isaías', 'is', 4, 2, 'Naquele dia o Renovo do Senhor será belo e glorioso, e o fruto da terra será o orgulho e a glória dos sobreviventes de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17737, 'nvi', 'Isaías', 'is', 4, 3, 'Os que forem deixados em Sião e ficarem em Jerusalém serão chamados santos: todos os inscritos para viverem em Jerusalém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17738, 'nvi', 'Isaías', 'is', 4, 4, 'Quando o Senhor tiver lavado a impureza das mulheres de Sião, e tiver limpado por meio de um espírito de julgamento e de um espírito de fogo o sangue derramado em Jerusalém,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17739, 'nvi', 'Isaías', 'is', 4, 5, 'o Senhor criará sobre todo o monte Sião e sobre aqueles que se reunirem ali uma nuvem de dia e um clarão de fogo de noite. A glória tudo cobrirá');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17740, 'nvi', 'Isaías', 'is', 4, 6, 'e será um abrigo e sombra para o calor do dia, refúgio e esconderijo contra a tempestade e a chuva.');
