INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18422, 'nvi', 'Isaías', 'is', 40, 1, 'Consolem, consolem o meu povo, diz o Deus de vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18423, 'nvi', 'Isaías', 'is', 40, 2, 'Encoragem a Jerusalém e anunciem que ela já cumpriu o trabalho que lhe foi imposto, pagou por sua iniqüidade, e recebeu da mão do Senhor em dobro por todos os seus pecados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18424, 'nvi', 'Isaías', 'is', 40, 3, 'Uma voz clama: "No deserto preparem o caminho para o Senhor; façam no deserto um caminho reto para o nosso Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18425, 'nvi', 'Isaías', 'is', 40, 4, 'Todos os vales serão levantados, todos os montes e colinas serão aplanados; os terrenos acidentados se tornarão planos; as escarpas, serão niveladas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18426, 'nvi', 'Isaías', 'is', 40, 5, 'A glória do Senhor será revelada, e, juntos, todos a verão. Pois é o Senhor quem fala".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18427, 'nvi', 'Isaías', 'is', 40, 6, 'Uma voz ordena: "Clame". E eu pergunto: "O que clamarei? " "Que toda a humanidade é como a relva, e toda a sua glória como as flores do campo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18428, 'nvi', 'Isaías', 'is', 40, 7, 'A relva murcha e cai a sua flor, quando o vento do Senhor sopra sobre eles; o povo não passa de relva.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18429, 'nvi', 'Isaías', 'is', 40, 8, 'A relva murcha, e as flores caem, mas a palavra de nosso Deus permanece para sempre. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18430, 'nvi', 'Isaías', 'is', 40, 9, 'Você, que traz boas novas a Sião, suba num alto monte. Você, que traz boas novas a Jerusalém, erga a sua voz com fortes gritos, erga-a, não tenha medo; diga às cidades de Judá: "Aqui está o seu Deus! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18431, 'nvi', 'Isaías', 'is', 40, 10, 'O Soberano Senhor vem com poder! Com seu braço forte ele governa. A sua recompensa com ele está, e seu galardão o acompanha.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18432, 'nvi', 'Isaías', 'is', 40, 11, 'Como pastor ele cuida de seu rebanho, com o braço ajunta os cordeiros e os carrega no colo; conduz com cuidado as ovelhas que amamentas suas crias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18433, 'nvi', 'Isaías', 'is', 40, 12, 'Quem mediu as águas na concha da mão, ou com o palmo definiu os limites dos céus? Quem jamais calculou o peso da terra, ou pesou os montes na balança e as colinas nos seus pratos?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18434, 'nvi', 'Isaías', 'is', 40, 13, 'Quem definiu limites para o Espírito do Senhor, ou o instruiu como seu conselheiro?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18435, 'nvi', 'Isaías', 'is', 40, 14, 'A quem o Senhor consultou que pudesse esclarecê-lo, e que lhe ensinasse a julgar com justiça? Quem lhe ensinou o conhecimento ou lhe aponta o caminho da sabedoria?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18436, 'nvi', 'Isaías', 'is', 40, 15, 'Na verdade as nações são como a gota que sobra do balde; para ele são como o pó que resta na balança; para ele as ilhas não passam de um grão de areia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18437, 'nvi', 'Isaías', 'is', 40, 16, 'Nem as florestas do Líbano seriam suficientes para o fogo do altar, nem os animais de lá bastariam para o holocausto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18438, 'nvi', 'Isaías', 'is', 40, 17, 'Diante dele todas as nações são como nada; para ele são sem valor e menos que nada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18439, 'nvi', 'Isaías', 'is', 40, 18, 'A quem vocês compararão Deus? Como poderão representá-lo?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18440, 'nvi', 'Isaías', 'is', 40, 19, 'Com uma imagem que funde o artesão, e que o ourives cobre de ouro e lhe modela correntes de prata?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18441, 'nvi', 'Isaías', 'is', 40, 20, 'Ou com o ídolo do pobre que pode apenas escolher um bom pedaço de madeira e procurar um marceneiro para fazer uma imagem que não caia?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18442, 'nvi', 'Isaías', 'is', 40, 21, 'Será que vocês não sabem? Nunca ouviram falar? Não lhes contaram desde a antigüidade? Vocês não compreenderam como a terra foi fundada?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18443, 'nvi', 'Isaías', 'is', 40, 22, 'Ele se assenta no seu trono, acima da cúpula da terra, cujos habitantes são pequenos como gafanhotos. Ele estende os céus como um forro, e os arma como uma tenda para neles habitar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18444, 'nvi', 'Isaías', 'is', 40, 23, 'Ele aniquila os príncipes e reduz a nada os juízes deste mundo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18445, 'nvi', 'Isaías', 'is', 40, 24, 'Mal eles são plantados ou semeados, mal lançam raízes na terra, Deus sopra sobre eles, e eles murcham; um redemoinho os leva como palha.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18446, 'nvi', 'Isaías', 'is', 40, 25, '"Com quem vocês me compararão? Quem se assemelha a mim? ", pergunta o Santo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18447, 'nvi', 'Isaías', 'is', 40, 26, 'Ergam os olhos e olhem para as alturas. Quem criou tudo isso? Aquele que põe em marcha cada estrela do seu exército celestial, e a todas chama pelo nome. Tão grande é o seu poder e tão imensa a sua força, que nenhuma delas deixa de comparecer!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18448, 'nvi', 'Isaías', 'is', 40, 27, 'Por que você reclama, ó Jacó, e por que se queixa, ó Israel: "O Senhor não se interessa pela minha situação; o meu Deus não considera a minha causa"?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18449, 'nvi', 'Isaías', 'is', 40, 28, 'Será que você não sabe? Nunca ouviu falar? O Senhor é o Deus eterno, o Criador de toda a terra. Ele não se cansa nem fica exausto, sua sabedoria é insondável.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18450, 'nvi', 'Isaías', 'is', 40, 29, 'Ele fortalece ao cansado e dá grande vigor ao que está sem forças.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18451, 'nvi', 'Isaías', 'is', 40, 30, 'Até os jovens se cansam e ficam exaustos, e os moços tropeçam e caem;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18452, 'nvi', 'Isaías', 'is', 40, 31, 'mas aqueles que esperam no Senhor renovam as suas forças. Voam bem alto como águias; correm e não ficam exaustos, andam e não se cansam.');
