INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18453, 'nvi', 'Isaías', 'is', 41, 1, '"Calem-se diante de mim, ó ilhas! Que as nações renovem as suas forças! Que elas se apresentem para se defender; vamos nos encontrar para decidir a questão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18454, 'nvi', 'Isaías', 'is', 41, 2, '"Quem despertou o que vem do oriente, e chamou-o em retidão ao seu serviço, entregando-lhe nações e subjugando reis diante dele? Com a espada ele os reduz a pó, com o arco os dispersa como palha.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18455, 'nvi', 'Isaías', 'is', 41, 3, 'Ele os persegue e avança em segurança, por um caminho que seus pés jamais percorreram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18456, 'nvi', 'Isaías', 'is', 41, 4, 'Quem fez tudo isso? Quem chama as gerações à existência desde o princípio? Fui eu mesmo, o Senhor, o primeiro, que continuarei sendo, até mesmo com os últimos. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18457, 'nvi', 'Isaías', 'is', 41, 5, 'As ilhas viram isso e temem; os confins da terra tremem. Eles se aproximam e vêm à frente;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18458, 'nvi', 'Isaías', 'is', 41, 6, 'cada um ajuda o outro e diz a seu irmão: "Seja forte! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18459, 'nvi', 'Isaías', 'is', 41, 7, 'O artesão encoraja o ourives, e aquele que alisa com o martelo incentiva o que bate na bigorna. Ele diz acerca da soldagem: "Está boa. " E fixa o ídolo com prego para que não tombe.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18460, 'nvi', 'Isaías', 'is', 41, 8, '"Você, porém, ó Israel, meu servo, Jacó, a quem escolhi, vocês, descendentes de Abraão, meu amigo,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18461, 'nvi', 'Isaías', 'is', 41, 9, 'eu os tirei dos confins da terra, de seus recantos mais distantes eu os chamei. Eu disse: "Você é meu servo"; eu o escolhi e não o rejeitei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18462, 'nvi', 'Isaías', 'is', 41, 10, 'Por isso não tema, pois estou com você; não tenha medo, pois sou o seu Deus. Eu o fortalecerei e o ajudarei; Eu o segurarei com a minha mão direita vitoriosa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18463, 'nvi', 'Isaías', 'is', 41, 11, '"Todos os que o odeiam certamente serão humilhados e constrangidos; aqueles que se opõem a você serão como nada e perecerão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18464, 'nvi', 'Isaías', 'is', 41, 12, 'Embora procure os seus inimigos, você não os encontrará. Os que guerreiam contra você serão reduzidos a nada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18465, 'nvi', 'Isaías', 'is', 41, 13, 'Pois eu sou o Senhor, o seu Deus, que o segura pela mão direita e lhe diz: Não tema; eu o ajudarei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18466, 'nvi', 'Isaías', 'is', 41, 14, 'Não tenha medo, ó verme Jacó, ó pequeno Israel, pois eu mesmo o ajudarei", declara o Senhor, seu Redentor, o Santo de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18467, 'nvi', 'Isaías', 'is', 41, 15, '"Veja, eu o tornarei um debulhador, novo e cortante, com muitos dentes. Você debulhará os montes e os esmagará, e reduzirá as colinas a palha.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18468, 'nvi', 'Isaías', 'is', 41, 16, 'Você irá peneirá-los, o vento os levará, e uma ventania os espalhará. Mas você se regozijará no Senhor e no Santo de Israel se gloriará.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18469, 'nvi', 'Isaías', 'is', 41, 17, '"O pobre e o necessitado buscam água, e não encontram! Suas línguas estão ressequidas de sede. Mas eu, o Senhor, lhes responderei; eu, o Deus de Israel, não os abandonarei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18470, 'nvi', 'Isaías', 'is', 41, 18, 'Abrirei rios nas colinas estéreis, e fontes nos vales. Transformarei o deserto num lago, e o chão ressequido em mananciais.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18471, 'nvi', 'Isaías', 'is', 41, 19, 'Porei no deserto o cedro, a acácia, a murta e a oliveira. Colocarei juntos no ermo o cipreste, o abeto e o pinheiro,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18472, 'nvi', 'Isaías', 'is', 41, 20, 'para que o povo veja e saiba, e todos vejam e saibam, que a mão do Senhor fez isso, que o Santo de Israel o criou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18473, 'nvi', 'Isaías', 'is', 41, 21, '"Exponham a sua causa", diz o Senhor. "Apresentem as suas provas", diz o rei de Jacó.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18474, 'nvi', 'Isaías', 'is', 41, 22, '"Tragam os seus ídolos para nos dizerem o que vai acontecer. Que eles nos contem como eram as coisas anteriores, para que as consideremos e saibamos o seu resultado final; ou que nos declarem as coisas vindouras,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18475, 'nvi', 'Isaías', 'is', 41, 23, 'revelem-nos o futuro, para que saibamos que vocês são deuses. Façam alguma coisa, boa ou má, para que nos rendamos, cheios de temor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18476, 'nvi', 'Isaías', 'is', 41, 24, 'Mas vejam só! Vocês não são nada, e as suas obras são totalmente nulas; detestável é aquele que os escolhe!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18477, 'nvi', 'Isaías', 'is', 41, 25, '"Despertei um homem, e do norte ele vem; desde o nascente proclamará o meu nome. Pisa em governantes como em argamassa, como o oleiro amassa o barro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18478, 'nvi', 'Isaías', 'is', 41, 26, 'Quem falou disso desde o princípio, para que o soubéssemos, ou antecipadamente, para que pudéssemos dizer: "Ele estava certo"? Ninguém o revelou, ninguém o fez ouvir, ninguém ouviu palavra alguma de vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18479, 'nvi', 'Isaías', 'is', 41, 27, 'Desde o princípio eu disse a Sião: "Veja, estas coisas acontecendo! " A Jerusalém eu darei um mensageiro de boas novas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18480, 'nvi', 'Isaías', 'is', 41, 28, 'Olho, e não há ninguém entre eles, nenhum conselheiro que dê resposta quando pergunto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18481, 'nvi', 'Isaías', 'is', 41, 29, 'Veja, são todos falsos! Seus feitos são nulos; suas imagens fundidas não passam de um sopro e nulidade!');
