INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18482, 'nvi', 'Isaías', 'is', 42, 1, '"Eis o meu servo, a quem sustento, o meu escolhido, em quem tenho prazer. Porei nele o meu Espírito, e ele trará justiça às nações.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18483, 'nvi', 'Isaías', 'is', 42, 2, 'Não gritará nem clamará, nem erguerá a voz nas ruas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18484, 'nvi', 'Isaías', 'is', 42, 3, 'Não quebrará o caniço rachado, e não apagará o pavio fumegante. Com fidelidade fará justiça;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18485, 'nvi', 'Isaías', 'is', 42, 4, 'não mostrará fraqueza nem se deixará ferir, até que estabeleça a justiça sobre a terra. Em sua lei as ilhas porão sua esperança. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18486, 'nvi', 'Isaías', 'is', 42, 5, 'É o que diz Deus, o Senhor, aquele que criou o céu e o estendeu, que espalhou a terra e tudo o que dela procede, que dá fôlego aos seus moradores e vida aos que andam nela:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18487, 'nvi', 'Isaías', 'is', 42, 6, '"Eu, o Senhor, o chamei em retidão; segurarei firme a sua mão. Eu o guardarei e farei de você um mediador para o povo e uma luz para os gentios,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18488, 'nvi', 'Isaías', 'is', 42, 7, 'para abrir os olhos aos cegos, para libertar da prisão os cativos e para livrar do calabouço os que habitam na escuridão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18489, 'nvi', 'Isaías', 'is', 42, 8, '"Eu sou o Senhor; esse é o meu nome! Não darei a outro a minha glória nem a imagens o meu louvor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18490, 'nvi', 'Isaías', 'is', 42, 9, 'Vejam! As profecias antigas aconteceram, e novas eu anuncio; antes de surgirem, eu as declaro a vocês".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18491, 'nvi', 'Isaías', 'is', 42, 10, 'Cantem ao Senhor um novo cântico, seu louvor desde os confins da terra, vocês, que navegam no mar, e tudo o que nele existe, vocês, ilhas, e todos os seus habitantes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18492, 'nvi', 'Isaías', 'is', 42, 11, 'Que o deserto e as suas cidades ergam a sua voz; regozijem-se os povoados habitados por Quedar. Cante de alegria o povo de Selá, gritem pelos altos dos montes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18493, 'nvi', 'Isaías', 'is', 42, 12, 'Dêem glória ao Senhor e nas ilhas proclamem seu louvor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18494, 'nvi', 'Isaías', 'is', 42, 13, 'O Senhor sairá como homem poderoso, como guerreiro despertará o seu zelo; com forte brado e o grito de guerra triunfará sobre os seus inimigos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18495, 'nvi', 'Isaías', 'is', 42, 14, '"Fiquei muito tempo em silêncio, e me contive, calado. Mas agora, como mulher em trabalho de parto, eu grito, gemo e respiro ofegante.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18496, 'nvi', 'Isaías', 'is', 42, 15, 'Arrasarei os montes e as colinas e secarei toda sua vegetação; tornarei rios em ilhas e secarei os açudes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18497, 'nvi', 'Isaías', 'is', 42, 16, 'Conduzirei os cegos por caminhos que eles não conheceram, por veredas desconhecidas eu os guiarei; transformarei as trevas em luz diante deles e tornarei retos os lugares acidentados. Essas são as coisas que farei; não os abandonarei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18498, 'nvi', 'Isaías', 'is', 42, 17, 'Mas retrocederão em vergonha total aqueles que confiam em imagens esculpidas, que dizem aos ídolos fundidos: ‘Vocês são nossos deuses’. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18499, 'nvi', 'Isaías', 'is', 42, 18, '"Ouçam, surdos; olhem, cegos, e vejam!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18500, 'nvi', 'Isaías', 'is', 42, 19, 'Quem é cego senão o meu servo e surdo senão o mensageiro que enviei? Quem é cego como aquele Que é consagrado a mim, cego como o servo do Senhor?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18501, 'nvi', 'Isaías', 'is', 42, 20, 'Você viu muitas coisas, mas não deu qualquer atenção; seus ouvidos estão abertos, mas você não ouve nada. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18502, 'nvi', 'Isaías', 'is', 42, 21, 'Foi do agrado do Senhor, por amor de sua retidão, tornar grande e gloriosa a sua lei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18503, 'nvi', 'Isaías', 'is', 42, 22, 'Mas este é um povo saqueado e roubado; foram apanhados em cavernas e escondidos em prisões. Eles se tornaram presa, sem ninguém para resgatá-los; eles se tornaram despojo, sem que ninguém reclamasse: "Devolvam".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18504, 'nvi', 'Isaías', 'is', 42, 23, 'Qual de vocês escutará isso ou prestará muita atenção no tempo vindouro?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18505, 'nvi', 'Isaías', 'is', 42, 24, 'Quem entregou Jacó para tornar-se despojo, e Israel aos saqueadores? Não foi o Senhor, contra quem temos pecado? Pois eles não quiseram seguir os seus caminhos; não obedeceram à sua lei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18506, 'nvi', 'Isaías', 'is', 42, 25, 'De modo que ele lançou sobre eles o seu furor, a violência da guerra. Ele os envolveu em chamas, contudo nada aprenderam; ela os consumiu, e ainda assim, não levaram isso a sério.');
