INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18507, 'nvi', 'Isaías', 'is', 43, 1, 'Mas agora assim diz o Senhor, aquele que o criou, ó Jacó, aquele que o formou, ó Israel: "Não tema, pois eu o resgatei; eu o chamei pelo nome; você é meu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18508, 'nvi', 'Isaías', 'is', 43, 2, 'Quando você atravessar as águas, eu estarei com você; e, quando você atravessar os rios, eles não o encobrirão. Quando você andar através do fogo, você não se queimará; as chamas não o deixarão em brasas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18509, 'nvi', 'Isaías', 'is', 43, 3, 'Pois eu sou o Senhor, o seu Deus, o Santo de Israel, o seu Salvador; dou o Egito como resgate por você, a Etiópia e Sebá em troca de você.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18510, 'nvi', 'Isaías', 'is', 43, 4, 'Visto que você é precioso e honrado à minha vista, e porque eu o amo, darei homens em seu lugar, e nações em troca de sua vida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18511, 'nvi', 'Isaías', 'is', 43, 5, 'Não tenha medo, pois eu estou com você, do oriente trarei seus filhos e do ocidente ajuntarei você.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18512, 'nvi', 'Isaías', 'is', 43, 6, 'Direi ao norte ‘Entregue-os! ’ e ao sul ‘Não os retenha’. De longe tragam os meus filhos, e dos confins da terra as minhas filhas;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18513, 'nvi', 'Isaías', 'is', 43, 7, 'todo o que é chamado pelo meu nome, a quem criei para a minha glória, a quem formei e fiz".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18514, 'nvi', 'Isaías', 'is', 43, 8, 'Traga o povo que tem olhos, mas é cego, que tem ouvidos, mas é surdo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18515, 'nvi', 'Isaías', 'is', 43, 9, 'Todas as nações se reúnem, e os povos se ajuntam. Qual deles predisse isto e anunciou as coisas passadas? Que eles façam entrar suas testemunhas, para provarem que estavam certos, para que outros ouçam e digam: "É verdade".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18516, 'nvi', 'Isaías', 'is', 43, 10, '"Vocês são minhas testemunhas", declara o Senhor, "e meu servo, a quem escolhi, para que vocês saibam e creiam em mim e entendam que eu sou Deus. Antes de mim nenhum deus se formou, nem haverá algum depois de mim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18517, 'nvi', 'Isaías', 'is', 43, 11, 'Eu, eu mesmo, sou o Senhor, e além de mim não há salvador algum.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18518, 'nvi', 'Isaías', 'is', 43, 12, 'Eu revelei, salvei e anunciei; eu, e não um deus estrangeiro entre vocês. Vocês são testemunhas de que eu sou Deus", declara o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18519, 'nvi', 'Isaías', 'is', 43, 13, '" Desde os dias mais antigos eu o sou. Não há quem possa livrar alguém de minha mão. Agindo eu quem pode desfazer? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18520, 'nvi', 'Isaías', 'is', 43, 14, 'Assim diz o Senhor, o seu Redentor, o Santo de Israel: "Por amor de vocês mandarei inimigos para a Babilônia e farei todos os babilônios descerem como fugitivos, nos navios de que se orgulhavam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18521, 'nvi', 'Isaías', 'is', 43, 15, 'Eu sou o Senhor, o Santo de vocês, o Criador de Israel e o seu Rei".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18522, 'nvi', 'Isaías', 'is', 43, 16, 'Assim diz o Senhor, aquele que fez um caminho pelo mar, uma vereda pelas águas violentas,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18523, 'nvi', 'Isaías', 'is', 43, 17, 'que fez saírem juntos os carros e cavalos, o exército e seus reforços, e eles jazem ali, para nunca mais se levantarem, exterminados, apagados como um pavio:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18524, 'nvi', 'Isaías', 'is', 43, 18, '"Esqueçam o que se foi; não vivam no passado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18525, 'nvi', 'Isaías', 'is', 43, 19, 'Vejam, estou fazendo uma coisa nova! Ela já está surgindo! Vocês não o percebem? Até no deserto vou abrir um caminho e riachos no ermo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18526, 'nvi', 'Isaías', 'is', 43, 20, 'Os animais do campo me honrarão, os chacais e as corujas, porque fornecerei água no deserto e riachos no ermo, para dar de beber a meu povo, meu escolhido,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18527, 'nvi', 'Isaías', 'is', 43, 21, 'ao povo que formei para mim mesmo a fim de que proclamasse o meu louvor".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18528, 'nvi', 'Isaías', 'is', 43, 22, '"Contudo você não me invocou, ó Jacó, embora você tenha ficado exausto por minha causa, ó Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18529, 'nvi', 'Isaías', 'is', 43, 23, 'Não foi para mim que você trouxe ovelhas para holocaustos, nem me honrou com seus sacrifícios. Não o sobrecarreguei com ofertas de cereal nem o deixei exausto com exigências de incenso.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18530, 'nvi', 'Isaías', 'is', 43, 24, 'Você não me comprou qualquer cana aromática, nem me encheu com a gordura de seus sacrifícios. Mas você me sobrecarregou com os seus pecados e me deixou exausto com suas ofensas. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18531, 'nvi', 'Isaías', 'is', 43, 25, '"Sou eu, eu mesmo, aquele que apaga suas transgressões, por amor de mim, e que não se lembra mais de seus pecados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18532, 'nvi', 'Isaías', 'is', 43, 26, 'Relembre o passado para mim, vamos discutir a sua causa; apresente o caso para provar sua inocência.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18533, 'nvi', 'Isaías', 'is', 43, 27, 'Seu primeiro pai pecou; seus porta-vozes se rebelaram contra mim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18534, 'nvi', 'Isaías', 'is', 43, 28, 'Por isso envergonharei os líderes do templo, e entregarei Jacó à destruição e Israel à zombaria.');
