INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18616, 'nvi', 'Isaías', 'is', 48, 1, '"Escute isto, ó comunidade de Jacó, vocês que são chamados pelo nome de Israel e vêm da linhagem de Judá, vocês que fazem juramentos pelo nome do Senhor e invocam o Deus de Israel, mas não em verdade ou retidão;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18617, 'nvi', 'Isaías', 'is', 48, 2, 'vocês que chamam a si mesmos cidadãos da cidade santa e dependem do Deus de Israel; o Senhor dos Exércitos é o seu nome:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18618, 'nvi', 'Isaías', 'is', 48, 3, 'Eu predisse há muito as coisas passadas, minha boca as anunciou, e eu as fiz conhecidas; então repentinamente agi, e elas aconteceram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18619, 'nvi', 'Isaías', 'is', 48, 4, 'Pois eu sabia o quão obstinado você era; os tendões de seu pescoço eram ferro, a sua testa era bronze.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18620, 'nvi', 'Isaías', 'is', 48, 5, 'Por isso há muito lhe contei essas coisas; antes que acontecessem eu as anunciei a você para que você não pudesse dizer: ‘Meus ídolos as fizeram; minha imagem de madeira e meu deus de metal as determinaram’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18621, 'nvi', 'Isaías', 'is', 48, 6, 'Você tem ouvido essas coisas; olhe para todas elas. Você não irá admiti-las? De agora em diante eu lhe contarei coisas novas, coisas ocultas, que você desconhece.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18622, 'nvi', 'Isaías', 'is', 48, 7, 'Elas foram criadas agora, e não há muito tempo; você nunca os conheceu antes. Por isso você não pode dizer: ‘Sim, eu as conhecia’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18623, 'nvi', 'Isaías', 'is', 48, 8, 'Você não tinha conhecimento nem entendimento; desde a antigüidade, o seu ouvido tem se fechado. Sei quão traiçoeiro você é; desde o nascimento você foi chamado rebelde.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18624, 'nvi', 'Isaías', 'is', 48, 9, 'Por amor do meu próprio nome eu adio a minha ira; por amor de meu louvor eu a contive, para que você não fosse eliminado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18625, 'nvi', 'Isaías', 'is', 48, 10, 'Veja, eu refinei você, embora não como prata; eu o provei na fornalha da aflição.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18626, 'nvi', 'Isaías', 'is', 48, 11, 'Por amor de mim mesmo, por amor de mim mesmo, eu faço isso. Como posso permitir que eu mesmo seja difamado? Não darei minha glória a um outro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18627, 'nvi', 'Isaías', 'is', 48, 12, '"Escute-me, ó Jacó, Israel, a quem chamei: Eu sou sempre o mesmo; eu sou o primeiro e eu sou o último.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18628, 'nvi', 'Isaías', 'is', 48, 13, 'Minha própria mão lançou os alicerces da terra, e a minha mão direita estendeu os céus; quando eu os convoco, todos juntos se põem de pé.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18629, 'nvi', 'Isaías', 'is', 48, 14, '"Reúnam-se, todos vocês, e escutem: Qual dos ídolos predisse essas coisas? O amado do Senhor cumprirá seu propósito contra a Babilônia; seu braço será contra os babilônios.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18630, 'nvi', 'Isaías', 'is', 48, 15, 'Eu, eu mesmo, falei; sim, eu o chamei. Eu o trarei, e ele será bem sucedido em sua missão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18631, 'nvi', 'Isaías', 'is', 48, 16, '"Aproximem-se de mim e escutem isto: "Desde o primeiro anúncio não falei secretamente; na hora em que acontecer, estarei ali. " E agora o Soberano Senhor me enviou, com seu Espírito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18632, 'nvi', 'Isaías', 'is', 48, 17, 'Assim diz o Senhor, o seu redentor, o Santo de Israel: "Eu sou o Senhor, o seu Deus, que lhe ensina o que é melhor para você, que o dirige no caminho em que você deve ir.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18633, 'nvi', 'Isaías', 'is', 48, 18, 'Se tão-somente você tivesse prestado atenção às minhas ordens, sua paz seria como um rio, sua retidão, como as ondas do mar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18634, 'nvi', 'Isaías', 'is', 48, 19, 'Seus descendentes seriam como a areia, seus filhos, como seus inúmeros grãos; o nome deles jamais seria eliminado nem destruído de diante de mim".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18635, 'nvi', 'Isaías', 'is', 48, 20, 'Deixem a Babilônia, fujam do meio dos babilônios! Anunciem isso com gritos de alegria e o proclamem. Enviem-no aos confins da terra; digam: "O Senhor resgatou seu servo Jacó".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18636, 'nvi', 'Isaías', 'is', 48, 21, 'Não tiveram sede quando ele os conduziu através dos desertos; ele fez água fluir da rocha para eles; fendeu a rocha, e a água jorrou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18637, 'nvi', 'Isaías', 'is', 48, 22, '"Não há paz alguma para os ímpios", diz o Senhor.');
