INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18638, 'nvi', 'Isaías', 'is', 49, 1, 'Escutem-me, vocês, ilhas; ouçam, vocês, nações distantes: Antes de eu nascer o Senhor me chamou; desde o meu nascimento ele fez menção de meu nome.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18639, 'nvi', 'Isaías', 'is', 49, 2, 'Ele fez de minha boca uma espada afiada, na sombra de sua mão ele me escondeu; ele me tornou uma flecha polida e escondeu-me na sua aljava.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18640, 'nvi', 'Isaías', 'is', 49, 3, 'Ele me disse: "Você é meu servo, Israel, em quem mostrarei o meu esplendor".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18641, 'nvi', 'Isaías', 'is', 49, 4, 'Mas eu disse: "Tenho me afadigado sem qualquer propósito; tenho gasto minha força em vão e para nada. Contudo, o que me é devido está na mão do Senhor, e a minha recompensa está com o meu Deus".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18642, 'nvi', 'Isaías', 'is', 49, 5, 'E agora o Senhor diz, aquele que me formou no ventre para ser o seu servo para trazer de volta Jacó e reunir Israel a ele mesmo, pois sou honrado aos olhos do Senhor, e o meu Deus tem sido a minha força;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18643, 'nvi', 'Isaías', 'is', 49, 6, 'ele diz: "É coisa pequena demais para você ser meu servo para restaurar as tribos de Jacó e trazer de volta aqueles de Israel que eu guardei. Também farei de você uma luz para os gentios, para que você leve a minha salvação até aos confins da terra".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18644, 'nvi', 'Isaías', 'is', 49, 7, 'Assim diz o Senhor, o Redentor e o Santo de Israel, àquele que foi desprezado e detestado pela nação, ao servo de governantes: "Reis o verão e se levantarão, líderes verão e se encurvarão, por causa do Senhor, que é fiel, o Santo de Israel, que o escolheu".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18645, 'nvi', 'Isaías', 'is', 49, 8, 'Assim diz o Senhor: "No tempo favorável eu lhe responderei, e no dia da salvação eu o ajudarei; eu o guardarei e farei que você seja uma aliança para o povo, para restaurar a terra e distribuir suas propriedades abandonadas,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18646, 'nvi', 'Isaías', 'is', 49, 9, 'para dizer aos cativos: "Saiam", e para aqueles que estão nas trevas: "Apareçam! " "Eles se apascentarão junto aos caminhos e acharão pastagem em toda colina estéril.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18647, 'nvi', 'Isaías', 'is', 49, 10, 'Não terão fome nem sede, o calor do deserto e o sol os atingirá. Aquele que tem compaixão deles os guiará e os conduzirá para as fontes de água.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18648, 'nvi', 'Isaías', 'is', 49, 11, 'Transformarei todos os meus montes em estradas, e os meus caminhos serão erguidos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18649, 'nvi', 'Isaías', 'is', 49, 12, 'Veja, eles virão de bem longe alguns do norte, alguns do oeste, alguns de Assuã".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18650, 'nvi', 'Isaías', 'is', 49, 13, 'Gritem de alegria, ó céus, regozije-se, ó terra; irrompam em canção, ó montes! Pois o Senhor consola o seu povo e terá compaixão de seus afligidos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18651, 'nvi', 'Isaías', 'is', 49, 14, 'Sião, porém, disse: "O Senhor me abandonou, o Senhor me desamparou".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18652, 'nvi', 'Isaías', 'is', 49, 15, '"Será que uma mãe pode esquecer do seu bebê que ainda mama e não ter compaixão do filho que gerou? Embora ela possa se esquecer, eu não me esquecerei de você!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18653, 'nvi', 'Isaías', 'is', 49, 16, 'Veja, eu gravei você nas palmas das minhas mãos; seus muros estão sempre diante de mim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18654, 'nvi', 'Isaías', 'is', 49, 17, 'Seus filhos apressam-se em voltar, e aqueles que a despojaram afastam-se de você.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18655, 'nvi', 'Isaías', 'is', 49, 18, 'Erga os olhos e olhe ao redor; todos os seus filhos se ajuntam e vêm até você. Juro pela minha vida Que você vestirá a todos como ornamento; você os vestirá como uma noiva", declara o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18656, 'nvi', 'Isaías', 'is', 49, 19, '"Embora você estivesse arruinada e fosse abandonada e sua terra fosse arrasada, agora você será pequena demais para o seu povo, e aqueles que a devoraram estarão bem distantes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18657, 'nvi', 'Isaías', 'is', 49, 20, 'Os filhos nascidos durante seu luto ainda dirão ao alcance de seus ouvidos: ‘Este lugar é pequeno demais para nós; dê-nos mais espaço para nele vivermos’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18658, 'nvi', 'Isaías', 'is', 49, 21, 'Então você dirá em seu coração: ‘Quem me gerou estes filhos? Eu estava enlutada e estéril; estava exilada e rejeitada. Quem os criou? Fui deixada totalmente só, mas estes... de onde vieram? ’ "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18659, 'nvi', 'Isaías', 'is', 49, 22, 'Assim diz o Soberano Senhor: "Veja, eu acenarei para os gentios, erguerei minha bandeira para os povos; eles trarão nos braços os seus filhos e carregarão nos ombros as suas filhas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18660, 'nvi', 'Isaías', 'is', 49, 23, 'Reis serão os seus padrastos, e suas rainhas serão as suas amas de leite. Eles se inclinarão diante de você, com o rosto em terra; lamberão o pó dos seus pés. Então você saberá que eu sou o Senhor; aqueles que esperam em mim não ficarão decepcionados".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18661, 'nvi', 'Isaías', 'is', 49, 24, 'Será que se pode tirar o despojo dos guerreiros, ou serem os prisioneiros resgatados do poder dos violentos?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18662, 'nvi', 'Isaías', 'is', 49, 25, 'Assim, porém, diz o Senhor:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18663, 'nvi', 'Isaías', 'is', 49, 26, '"Sim, prisioneiros serão tirados de guerreiros, e despojo será retomado dos violentos; brigarei com os que brigam com você, e seus filhos, eu os salvarei. Farei seus opressores comerem sua própria carne; ficarão bêbados com seu próprio sangue, como com vinho. Então todo mundo saberá que eu, o Senhor, sou o seu Salvador, seu Redentor, o Poderoso de Jacó".');
