INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18823, 'nvi', 'Isaías', 'is', 60, 1, '"Levante-se, refulja! Porque chegou a sua luz, e a glória do Senhor raia sobre você.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18824, 'nvi', 'Isaías', 'is', 60, 2, 'Olhe! A escuridão cobre a terra, dessas trevas envolvem os povos, mas sobre você raia o Senhor, e sobre você se vê a sua glória.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18825, 'nvi', 'Isaías', 'is', 60, 3, 'As nações virão à sua luz e os reis ao fulgor do sua alvorecer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18826, 'nvi', 'Isaías', 'is', 60, 4, '"Olhe ao redor, e veja: Todos se reúnem e vêm a você; de longe vêm os seus filhos, e as suas filhas vêm carregadas nos braços.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18827, 'nvi', 'Isaías', 'is', 60, 5, 'Então o verás e ficarás radiante; o seu coração pulsará forte e se encherá de alegria, porque a riqueza dos mares lhe será trazida, e a você virão as riquezas das nações.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18828, 'nvi', 'Isaías', 'is', 60, 6, 'Manadas de camelos cobrirão a sua terra, camelos novos de Midiã e de Efá. Virão todos os de Sabá carregando ouro e incenso e proclamando o louvor do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18829, 'nvi', 'Isaías', 'is', 60, 7, 'Todos os rebanhos de Quedar se reunirão junto de você, e os carneiros de Nebaiote a servirão; serão aceitos como ofertas em meu altar, e adornarei o meu glorioso templo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18830, 'nvi', 'Isaías', 'is', 60, 8, '"Quem são estes que voam como nuvens, que voam como pombas para os seus ninhos?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18831, 'nvi', 'Isaías', 'is', 60, 9, 'Pois as ilhas esperam em mim; à frente vêm os navios de Társis, trazendo de longe os seus filhos, com prata e ouro, para a honra do Senhor, o seu Deus, o Santo de Israel, porque ele se revestiu de esplendor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18832, 'nvi', 'Isaías', 'is', 60, 10, '"Estrangeiros reconstruirão os seus muros, e seus reis a servirão. Com ira eu a feri, mas com amor lhe mostrarei compaixão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18833, 'nvi', 'Isaías', 'is', 60, 11, 'As suas portas permanecerão abertas; jamais serão fechadas, dia e noite, para que lhe tragam as riquezas das nações, com seus reis e seu séquito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18834, 'nvi', 'Isaías', 'is', 60, 12, 'Pois a nação e o reino que não a servirem perecerão; serão totalmente exterminados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18835, 'nvi', 'Isaías', 'is', 60, 13, '"A glória do Líbano virá a você, juntos virão o pinheiro, o abeto e o cipreste, para adornarem o lugar do meu santuário; e eu glorificarei o local em que pisam os meus pés.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18836, 'nvi', 'Isaías', 'is', 60, 14, 'Os filhos dos seus opressores virão e se inclinarão diante de você; todos os que a desprezam se curvarão aos seus pés e a chamarão cidade do Senhor, Sião do Santo de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18837, 'nvi', 'Isaías', 'is', 60, 15, '"Em vez de abandonada e odiada, sem que ninguém a percorresse, farei de você um orgulho, uma alegria para todas as gerações.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18838, 'nvi', 'Isaías', 'is', 60, 16, 'Você beberá o leite das nações e será amamentada por mulheres nobres. Então você saberá que eu, o Senhor, sou o seu Salvador, o seu Redentor, o Poderoso de Jacó.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18839, 'nvi', 'Isaías', 'is', 60, 17, 'Em vez de bronze eu lhe trarei ouro, e em vez de ferro, prata. Em vez de madeira eu lhe trarei bronze, e em vez de pedras, ferro. Farei da paz o seu dominador, da justiça, o seu governador.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18840, 'nvi', 'Isaías', 'is', 60, 18, 'Não se ouvirá mais falar de violência em sua terra, nem de ruína e destruição dentro de suas fronteiras. Os seus muros você chamará salvação, e as suas portas, louvor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18841, 'nvi', 'Isaías', 'is', 60, 19, 'O sol não será mais a sua luz de dia, e você não terá mais o brilho do luar, pois o Senhor será a sua luz para sempre; o seu Deus será a sua glória.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18842, 'nvi', 'Isaías', 'is', 60, 20, 'O seu sol nunca se porá, e a sua lua nunca desaparecerá, porque o Senhor será a sua luz, para sempre e os seus dias de tristeza terão fim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18843, 'nvi', 'Isaías', 'is', 60, 21, 'Então todo o seu povo será justo, e possuirá a terra para sempre. Ele é o renovo que plantei, obra das minhas mãos, para manifestação da minha glória.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (18844, 'nvi', 'Isaías', 'is', 60, 22, 'O mais pequenino se tornará mil, o menor será uma nação poderosa. Eu sou o Senhor; na hora certa farei que isso aconteça depressa. "');
