INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17784, 'nvi', 'Isaías', 'is', 7, 1, 'Quando Acaz, filho de Jotão, e neto de Uzias, era rei de Judá, o rei Rezim, da Síria, e Peca, filho de Remalias, rei de Israel, atacaram Jerusalém, mas não puderam vencê-la.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17785, 'nvi', 'Isaías', 'is', 7, 2, 'Informaram ao rei: "A Síria montou acampamento em Efraim". Com isso o coração de Acaz e do seu povo agitou-se, como as árvores da floresta agitam-se com o vento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17786, 'nvi', 'Isaías', 'is', 7, 3, 'Então o Senhor disse a Isaías: "Saiam, você e seu filho Sear-Jasube, e vão encontrar-se com Acaz no final do aqueduto do açude Superior, na estrada que vai para o campo do Lavandeiro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17787, 'nvi', 'Isaías', 'is', 7, 4, 'Diga a ele: ‘Tenha cuidado, acalme-se e não tenha medo. Que o seu coração não se desanime por causa do furor destes restos de lenha fumegantes: Rezim, a Síria e o filho de Remalias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17788, 'nvi', 'Isaías', 'is', 7, 5, '" ‘Porque a Síria, Efraim e o filho de Remalias têm tramado a sua ruína, dizendo:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17789, 'nvi', 'Isaías', 'is', 7, 6, '"Vamos invadir o reino de Judá; vamos rasgá-lo e dividi-lo entre nós, e fazer o filho de Tabeel reinar sobre ele".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17790, 'nvi', 'Isaías', 'is', 7, 7, 'Assim diz o Soberano Senhor: " ‘Não será assim, isso não acontecerá,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17791, 'nvi', 'Isaías', 'is', 7, 8, 'pois a cabeça da Síria é Damasco, e a cabeça de Damasco é Rezim. Em sessenta e cinco anos Efraim estará destruído demais para ser um povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17792, 'nvi', 'Isaías', 'is', 7, 9, 'A cabeça de Efraim é Samaria, e a cabeça de Samaria é o filho de Remalias. Se vocês não ficarem firmes na fé, com certeza não resistirão! ’ "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17793, 'nvi', 'Isaías', 'is', 7, 10, 'Disse ainda o Senhor a Acaz:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17794, 'nvi', 'Isaías', 'is', 7, 11, '"Peça ao Senhor, ao seu Deus, um sinal miraculoso, seja das maiores profundezas, seja das alturas mais elevadas".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17795, 'nvi', 'Isaías', 'is', 7, 12, 'Mas Acaz disse: "Não pedirei; não porei o Senhor à prova".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17796, 'nvi', 'Isaías', 'is', 7, 13, 'Disse então Isaías: "Ouçam agora, descendentes de Davi! Não basta abusarem da paciência dos homens? Também vão abusar da paciência do meu Deus?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17797, 'nvi', 'Isaías', 'is', 7, 14, 'Por isso o Senhor mesmo lhes dará um sinal: a virgem ficará grávida e dará à luz um filho, e o chamará Emanuel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17798, 'nvi', 'Isaías', 'is', 7, 15, 'Ele comerá coalhada e mel até a idade em que saiba rejeitar o erro e escolher o que é certo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17799, 'nvi', 'Isaías', 'is', 7, 16, 'Mas antes que o menino saiba rejeitar o erro e escolher o que é certo, a terra dos dois reis que você teme ficará deserta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17800, 'nvi', 'Isaías', 'is', 7, 17, 'O Senhor trará o rei da Assíria sobre você e sobre o seu povo e sobre a descendência de seu pai. Serão dias como nunca houve, desde que Efraim se separou de Judá".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17801, 'nvi', 'Isaías', 'is', 7, 18, 'Naquele dia o Senhor assobiará para chamar as moscas dos distantes rios do Egito e as abelhas da Assíria.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17802, 'nvi', 'Isaías', 'is', 7, 19, 'Todas virão e pousarão nos vales íngremes e nas fendas das rochas, em todos os espinheiros e em todas as cisternas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17803, 'nvi', 'Isaías', 'is', 7, 20, 'Naquele dia o Senhor utilizará uma navalha alugada de além do Eufrates, o rei da Assíria, para rapar a sua cabeça e os pêlos de suas pernas e da sua barba.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17804, 'nvi', 'Isaías', 'is', 7, 21, 'Naquele dia o homem que tiver uma vaca e duas cabras');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17805, 'nvi', 'Isaías', 'is', 7, 22, 'terá coalhada para comer, graças à fartura de leite que elas darão. Todos os que ficarem na terra comerão coalhada e mel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17806, 'nvi', 'Isaías', 'is', 7, 23, 'Naquele dia, todo lugar onde havia mil videiras no valor de doze quilos de prata será deixado para as roseiras bravas e para os espinheiros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17807, 'nvi', 'Isaías', 'is', 7, 24, 'Os homens entrarão ali com arcos e flechas, pois todo o país estará coberto de roseiras bravas e de espinheiros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17808, 'nvi', 'Isaías', 'is', 7, 25, 'E às colinas antes lavradas com enxada você não irá mais, porque terá medo das roseiras bravas e dos espinheiros; nesses lugares os bois ficarão à solta e as ovelhas correrão livremente.');
