INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17809, 'nvi', 'Isaías', 'is', 8, 1, 'O Senhor me disse: "Tome uma placa de bom tamanho e nela escreva de forma legível: Maher-Shalal-Hash-Baz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17810, 'nvi', 'Isaías', 'is', 8, 2, 'E chame o sacerdote Urias, e Zacarias, filho de Jeberequias, como testemunhas de confiança".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17811, 'nvi', 'Isaías', 'is', 8, 3, 'Então deitei-me com a profetisa, minha mulher, e ela engravidou e deu à luz um filho. E o Senhor me disse: "Dê-lhe o nome de Maher-Shalal-Hash-Baz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17812, 'nvi', 'Isaías', 'is', 8, 4, 'Pois antes que o menino saiba dizer ‘papai’ ou ‘mamãe’, a riqueza de Damasco e os bens de Samaria serão levados pelo rei da Assíria".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17813, 'nvi', 'Isaías', 'is', 8, 5, 'O Senhor tornou a falar-me:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17814, 'nvi', 'Isaías', 'is', 8, 6, '"Já que este povo rejeitou as águas de Siloé, que fluem mansamente, e alegrou-se com Rezim e com o filho de Remalias,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17815, 'nvi', 'Isaías', 'is', 8, 7, 'o Senhor está trazendo contra eles as poderosas e devastadoras águas do Eufrates, o rei da Assíria com todo o seu poderio. Elas transbordarão em todos os seus canais, encobrirão todas as suas margens');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17816, 'nvi', 'Isaías', 'is', 8, 8, 'e inundarão Judá cobrindo tudo até o pescoço. Seus braços abertos se espalharão por toda a tua terra, ó Emanuel! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17817, 'nvi', 'Isaías', 'is', 8, 9, 'Continuem a fazer o mal, ó nações, e vocês serão destruídas! Escutem, terras distantes: Ainda que vocês se preparem para o combate, serão destruídas! Sim, mesmo que se preparem para o combate, vocês serão destruídas!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17818, 'nvi', 'Isaías', 'is', 8, 10, 'Mesmo que vocês criem estratégias, elas serão frustradas; mesmo que façam planos, não terão sucesso, pois Deus está conosco!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17819, 'nvi', 'Isaías', 'is', 8, 11, 'O Senhor falou comigo com veemência, advertindo-me a não seguir o caminho desse povo. Ele disse:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17820, 'nvi', 'Isaías', 'is', 8, 12, '"Não chamem conspiração tudo o que esse povo chama conspiração; não temam aquilo que eles temem, nem se apavorem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17821, 'nvi', 'Isaías', 'is', 8, 13, 'Ao Senhor dos Exércitos é que vocês devem considerar santo, a ele é que vocês devem temer, dele é que vocês devem ter pavor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17822, 'nvi', 'Isaías', 'is', 8, 14, 'Para os dois reinos de Israel ele será um santuário, mas também uma pedra de tropeço, uma rocha que faz cair. E para os habitantes de Jerusalém ele será uma armadilha e um laço.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17823, 'nvi', 'Isaías', 'is', 8, 15, 'Muitos deles tropeçarão, cairão e serão despedaçados, presos no laço e capturados".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17824, 'nvi', 'Isaías', 'is', 8, 16, 'Guarde o mandamento com cuidado e sele a lei entre os meus discípulos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17825, 'nvi', 'Isaías', 'is', 8, 17, 'Esperarei pelo Senhor, que está escondendo o seu rosto da descendência de Jacó. Nele porei a minha esperança.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17826, 'nvi', 'Isaías', 'is', 8, 18, 'Aqui estou eu com os filhos que o Senhor me deu. Em Israel somos sinais e símbolos da parte do Senhor dos Exércitos, que habita no monte Sião.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17827, 'nvi', 'Isaías', 'is', 8, 19, 'Quando disserem a vocês: "Consultem médiuns e espíritas que murmuram encantamentos, pois todos procuram seus deuses e os mortos em favor dos vivos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17828, 'nvi', 'Isaías', 'is', 8, 20, 'Respondam: "À lei e aos mandamentos! " Se eles não falarem conforme esta palavra, vocês jamais verão a luz!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17829, 'nvi', 'Isaías', 'is', 8, 21, 'Aflitos e famintos vaguearão pela terra; quando estiverem famintos, ficarão irados e, olhando para cima, amaldiçoarão o seu rei e o seu Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17830, 'nvi', 'Isaías', 'is', 8, 22, 'Depois olharão para a terra e só verão aflição, trevas e temível escuridão, e serão atirados em densas trevas.');
