INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17831, 'nvi', 'Isaías', 'is', 9, 1, 'Contudo, não haverá mais escuridão para os que estavam aflitos. No passado ele humilhou a terra de Zebulom e de Naftali, mas no futuro honrará a Galiléia dos gentios, o caminho do mar, junto ao Jordão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17832, 'nvi', 'Isaías', 'is', 9, 2, 'O povo que caminhava em trevas viu uma grande luz; sobre os que viviam na terra da sombra da morte raiou uma luz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17833, 'nvi', 'Isaías', 'is', 9, 3, 'Fizeste crescer a nação e aumentaste a sua alegria; eles se alegram diante de ti como os que se regozijam na colheita, como os que exultam quando dividem os bens tomados na batalha.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17834, 'nvi', 'Isaías', 'is', 9, 4, 'Pois, tu destruíste o jugo que os oprimia, a canga que estava sobre os seus ombros, e a vara de castigo do seu opressor, como no dia da derrota de Midiã.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17835, 'nvi', 'Isaías', 'is', 9, 5, 'Pois, toda bota de guerreiro usada em combate e toda veste revolvida em sangue serão queimadas, como lenha no fogo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17836, 'nvi', 'Isaías', 'is', 9, 6, 'Porque um menino nos nasceu, um filho nos foi dado, e o governo está sobre os seus ombros. E ele será chamado Maravilhoso Conselheiro, Deus Poderoso, Pai Eterno, Príncipe da Paz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17837, 'nvi', 'Isaías', 'is', 9, 7, 'Ele estenderá o seu domínio, e haverá paz sem fim sobre o trono de Davi e sobre o seu reino, estabelecido e mantido com justiça e retidão, desde agora e para sempre. O zelo do Senhor dos Exércitos fará isso.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17838, 'nvi', 'Isaías', 'is', 9, 8, 'O Senhor enviou uma mensagem contra Jacó, e ela atingiu Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17839, 'nvi', 'Isaías', 'is', 9, 9, 'Todo o povo ficará sabendo, tanto Efraim como os habitantes de Samaria, que dizem com orgulho e arrogância de coração:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17840, 'nvi', 'Isaías', 'is', 9, 10, '"Os tijolos caíram, mas nós reconstruiremos com pedras lavradas; as figueiras bravas foram derrubadas, mas nós as substituiremos por cedros".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17841, 'nvi', 'Isaías', 'is', 9, 11, 'Mas o Senhor fortaleceu os adversários de Rezim para atacá-los e incitou contra eles os seus inimigos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17842, 'nvi', 'Isaías', 'is', 9, 12, 'Os arameus do leste e os filisteus do oeste devoraram Israel, escancarando a boca. Apesar disso tudo, a ira divina não se desviou; sua mão continua erguida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17843, 'nvi', 'Isaías', 'is', 9, 13, 'Mas o povo não voltou para aquele que os feriu, nem buscou o Senhor dos Exércitos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17844, 'nvi', 'Isaías', 'is', 9, 14, 'Por essa razão o Senhor corta de Israel tanto a cabeça como a cauda, tanto a palma como o junco, num único dia;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17845, 'nvi', 'Isaías', 'is', 9, 15, 'as autoridades e os homens de destaque são a cabeça, os profetas que ensinam mentiras são a cauda.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17846, 'nvi', 'Isaías', 'is', 9, 16, 'Aqueles que guiam este povo o desorientam, e aqueles que são guiados deixam-se induzir ao erro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17847, 'nvi', 'Isaías', 'is', 9, 17, 'Por isso o Senhor não terá nos jovens motivo de alegria, nem terá piedade dos órfãos e das viúvas, pois todos são hipócritas e perversos, e todos falam loucuras. Apesar disso tudo, a ira dele não se desviou; sua mão continua erguida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17848, 'nvi', 'Isaías', 'is', 9, 18, 'Porque a impiedade queima como fogo; consome roseiras bravas e espinheiros, põe em chamas os matagais da floresta, fazendo nuvens de fumaça.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17849, 'nvi', 'Isaías', 'is', 9, 19, 'Pela ira do Senhor dos Exércitos a terra será ressecada, e o povo será como lenha no fogo; ninguém poupará seu irmão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17850, 'nvi', 'Isaías', 'is', 9, 20, 'À direita devorarão, mas ainda estarão com fome; à esquerda comerão, mas não ficarão satisfeitos. Cada um comerá a carne do seu próprio irmão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17851, 'nvi', 'Isaías', 'is', 9, 21, 'Manassés contra Efraim, Efraim contra Manassés, e juntos eles se voltarão contra Judá. Apesar disso tudo, a ira divina não se desviou; sua mão continua erguida.');
