INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22293, 'nvi', 'Joel', 'jl', 1, 1, 'A palavra do Senhor que veio a Joel, filho de Petuel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22294, 'nvi', 'Joel', 'jl', 1, 2, 'Ouçam isto, anciãos; escutem, todos os habitantes do país. Já aconteceu algo assim nos seus dias? Ou nos dias dos seus antepassados?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22295, 'nvi', 'Joel', 'jl', 1, 3, 'Contem o que aconteceu aos seus filhos, e eles aos seus netos, e os seus netos, à geração seguinte.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22296, 'nvi', 'Joel', 'jl', 1, 4, 'O que o gafanhoto cortador deixou o gafanhoto peregrino comeu; o que o gafanhoto peregrino deixou o gafanhoto devastador comeu; o que o gafanhoto devastador deixou o gafanhoto devorador comeu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22297, 'nvi', 'Joel', 'jl', 1, 5, 'Acordem, bêbados, e chorem! Lamentem todos vocês, bebedores de vinho; gritem por causa do vinho novo, pois ele foi tirado dos seus lábios.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22298, 'nvi', 'Joel', 'jl', 1, 6, 'Uma nação invadiu a minha terra, poderosa e inumerável; seus dentes são dentes de leão, suas presas são de leoa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22299, 'nvi', 'Joel', 'jl', 1, 7, 'Arrasou as minhas videiras e arruinou as minhas figueiras. Arrancou-lhes a casca, e derrubou-as, deixando brancos os seus galhos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22300, 'nvi', 'Joel', 'jl', 1, 8, 'Pranteiem como uma virgem em vestes de luto que lamenta pelo noivo da sua mocidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22301, 'nvi', 'Joel', 'jl', 1, 9, 'As ofertas de cereal e as ofertas derramadas foram eliminadas do templo do Senhor. Os sacerdotes, que ministram diante do Senhor, estão de luto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22302, 'nvi', 'Joel', 'jl', 1, 10, 'Os campos estão arruinados, a terra está seca; o trigo está destruído, o vinho novo acabou, o azeite está em falta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22303, 'nvi', 'Joel', 'jl', 1, 11, 'Desesperem-se, agricultores, chorem, produtores de vinho; fiquem aflitos pelo trigo e pela cevada, porque a colheita foi destruída.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22304, 'nvi', 'Joel', 'jl', 1, 12, 'A vinha está seca, e a figueira murchou; a romãzeira, a palmeira e a macieira, todas as árvores do campo secaram. Secou-se, mais ainda, a alegria dos homens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22305, 'nvi', 'Joel', 'jl', 1, 13, 'Ponham vestes de luto, ó sacerdotes, e pranteiem; chorem alto, vocês que ministram perante o altar. Venham, passem a noite vestidos de luto, vocês que ministram perante o meu Deus; pois as ofertas de cereal e as ofertas derramadas foram suprimidas do templo do seu Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22306, 'nvi', 'Joel', 'jl', 1, 14, 'Decretem um jejum santo; convoquem uma assembléia sagrada. Reúnam as autoridades e todos os habitantes do país no templo do Senhor, do seu Deus, e clamem ao Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22307, 'nvi', 'Joel', 'jl', 1, 15, 'Ah! Aquele dia! Sim, o dia do Senhor está próximo; como destruição poderosa da parte do Todo-poderoso, ele virá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22308, 'nvi', 'Joel', 'jl', 1, 16, 'Não é verdade que a comida foi eliminada diante dos nossos próprios olhos, e que a alegria e a satisfação foram suprimidas, do templo do nosso Deus?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22309, 'nvi', 'Joel', 'jl', 1, 17, 'As sementes estão murchas debaixo dos torrões de terra. Os celeiros estão em ruínas, os depósitos de cereal foram derrubados, pois a colheita se perdeu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22310, 'nvi', 'Joel', 'jl', 1, 18, 'Como está mugindo o gado! As manadas andam agitadas porque não têm pasto; até os rebanhos de ovelhas estão sendo castigados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22311, 'nvi', 'Joel', 'jl', 1, 19, 'A ti, Senhor, eu clamo, pois o fogo devorou as pastagens e as chamas consumiram todas as árvores do campo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22312, 'nvi', 'Joel', 'jl', 1, 20, 'Até os animais do campo clamam a ti, pois os canais de água se secaram e o fogo devorou as pastagens.');
