INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26046, 'nvi', 'João', 'jo', 1, 1, 'No princípio era aquele que é a Palavra. Ele estava com Deus, e era Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26047, 'nvi', 'João', 'jo', 1, 2, 'Ela estava com Deus no princípio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26048, 'nvi', 'João', 'jo', 1, 3, 'Todas as coisas foram feitas por intermédio dele; sem ele, nada do que existe teria sido feito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26049, 'nvi', 'João', 'jo', 1, 4, 'Nele estava a vida, e esta era a luz dos homens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26050, 'nvi', 'João', 'jo', 1, 5, 'A luz brilha nas trevas, e as trevas não a derrotaram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26051, 'nvi', 'João', 'jo', 1, 6, 'Surgiu um homem enviado por Deus, chamado João.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26052, 'nvi', 'João', 'jo', 1, 7, 'Ele veio como testemunha, para testificar acerca da luz, a fim de que por meio dele todos os homens cressem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26053, 'nvi', 'João', 'jo', 1, 8, 'Ele próprio não era a luz, mas veio como testemunha da luz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26054, 'nvi', 'João', 'jo', 1, 9, 'Estava chegando ao mundo a verdadeira luz, que ilumina todos os homens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26055, 'nvi', 'João', 'jo', 1, 10, 'Aquele que é a Palavra estava no mundo, e o mundo foi feito por intermédio dele, mas o mundo não o reconheceu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26056, 'nvi', 'João', 'jo', 1, 11, 'Veio para o que era seu, mas os seus não o receberam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26057, 'nvi', 'João', 'jo', 1, 12, 'Contudo, aos que o receberam, aos que creram em seu nome, deu-lhes o direito de se tornarem filhos de Deus,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26058, 'nvi', 'João', 'jo', 1, 13, 'os quais não nasceram por descendência natural, nem pela vontade da carne nem pela vontade de algum homem, mas nasceram de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26059, 'nvi', 'João', 'jo', 1, 14, 'Aquele que é a Palavra tornou-se carne e viveu entre nós. Vimos a sua glória, glória como do Unigênito vindo do Pai, cheio de graça e de verdade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26060, 'nvi', 'João', 'jo', 1, 15, 'João dá testemunho dele. Ele exclama: "Este é aquele de quem eu falei: Aquele que vem depois de mim é superior a mim, porque já existia antes de mim".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26061, 'nvi', 'João', 'jo', 1, 16, 'Todos recebemos da sua plenitude, graça sobre graça.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26062, 'nvi', 'João', 'jo', 1, 17, 'Pois a Lei foi dada por intermédio de Moisés; a graça e a verdade vieram por intermédio de Jesus Cristo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26063, 'nvi', 'João', 'jo', 1, 18, 'Ninguém jamais viu a Deus, mas o Deus Unigênito, que está junto do Pai, o tornou conhecido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26064, 'nvi', 'João', 'jo', 1, 19, 'Esse foi o testemunho de João, quando os judeus de Jerusalém enviaram sacerdotes e levitas para lhe perguntarem quem ele era.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26065, 'nvi', 'João', 'jo', 1, 20, 'Ele confessou e não negou; declarou abertamente: "Não sou o Cristo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26066, 'nvi', 'João', 'jo', 1, 21, 'Perguntaram-lhe: "E então, quem é você? É Elias? " Ele disse: "Não sou". "É o Profeta? " Ele respondeu: "Não".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26067, 'nvi', 'João', 'jo', 1, 22, 'Finalmente perguntaram: "Quem é você? Dê-nos uma resposta, para que a levemos àqueles que nos enviaram. Que diz você acerca de si próprio? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26068, 'nvi', 'João', 'jo', 1, 23, 'João respondeu com as palavras do profeta Isaías: "Eu sou a voz do que clama no deserto: ‘Façam um caminho reto para o Senhor’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26069, 'nvi', 'João', 'jo', 1, 24, 'Alguns fariseus que tinham sido enviados');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26070, 'nvi', 'João', 'jo', 1, 25, 'interrogaram-no: "Então, por que você batiza, se não é o Cristo, nem Elias, nem o Profeta? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26071, 'nvi', 'João', 'jo', 1, 26, 'Respondeu João: "Eu batizo com água, mas entre vocês está alguém que vocês não conhecem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26072, 'nvi', 'João', 'jo', 1, 27, 'Ele é aquele que vem depois de mim, cujas correias das sandálias não sou digno de desamarrar".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26073, 'nvi', 'João', 'jo', 1, 28, 'Tudo isso aconteceu em Betânia, do outro lado do Jordão, onde João estava batizando.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26074, 'nvi', 'João', 'jo', 1, 29, 'No dia seguinte João viu Jesus aproximando-se e disse: "Vejam! É o Cordeiro de Deus, que tira o pecado do mundo!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26075, 'nvi', 'João', 'jo', 1, 30, 'Este é aquele a quem eu me referi, quando disse: Vem depois de mim um homem que é superior a mim, porque já existia antes de mim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26076, 'nvi', 'João', 'jo', 1, 31, 'Eu mesmo não o conhecia, mas por isso é que vim batizando com água: para que ele viesse a ser revelado a Israel".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26077, 'nvi', 'João', 'jo', 1, 32, 'Então João deu o seguinte testemunho: "Eu vi o Espírito descer do céu como pomba e permanecer sobre ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26078, 'nvi', 'João', 'jo', 1, 33, 'Eu não o teria reconhecido, se aquele que me enviou para batizar com água não me tivesse dito: ‘Aquele sobre quem você vir o Espírito descer e permanecer, esse é o que batiza com o Espírito Santo’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26079, 'nvi', 'João', 'jo', 1, 34, 'Eu vi e testifico que este é o Filho de Deus".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26080, 'nvi', 'João', 'jo', 1, 35, 'No dia seguinte João estava ali novamente com dois dos seus discípulos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26081, 'nvi', 'João', 'jo', 1, 36, 'Quando viu Jesus passando, disse: "Vejam! É o Cordeiro de Deus! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26082, 'nvi', 'João', 'jo', 1, 37, 'Ouvindo-o dizer isso, os dois discípulos seguiram a Jesus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26083, 'nvi', 'João', 'jo', 1, 38, 'Voltando-se e vendo Jesus que os dois o seguiam, perguntou-lhes: "O que vocês querem? " Eles disseram: "Rabi", ( que significa Mestre ), "onde estás hospedado? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26084, 'nvi', 'João', 'jo', 1, 39, 'Respondeu ele: "Venham e verão". Então foram, por volta das quatro horas da tarde, viram onde ele estava hospedado e passaram com ele aquele dia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26085, 'nvi', 'João', 'jo', 1, 40, 'André, irmão de Simão Pedro, era um dos dois que tinham ouvido o que João dissera e que haviam seguido a Jesus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26086, 'nvi', 'João', 'jo', 1, 41, 'O primeiro que ele encontrou foi Simão, seu irmão, e lhe disse: "Achamos o Messias" ( isto é, o Cristo ).');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26087, 'nvi', 'João', 'jo', 1, 42, 'E o levou a Jesus. Jesus olhou para ele e disse: "Você é Simão, filho de João. Será chamado Cefas" ( que significa Pedro ).');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26088, 'nvi', 'João', 'jo', 1, 43, 'No dia seguinte Jesus decidiu partir para a Galiléia. Quando encontrou Filipe, disse-lhe: "Siga-me".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26089, 'nvi', 'João', 'jo', 1, 44, 'Filipe, como André e Pedro, era da cidade de Betsaida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26090, 'nvi', 'João', 'jo', 1, 45, 'Filipe encontrou Natanael e lhe disse: "Achamos aquele sobre quem Moisés escreveu na Lei, e a respeito de quem os profetas também escreveram: Jesus de Nazaré, filho de José".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26091, 'nvi', 'João', 'jo', 1, 46, 'Perguntou Natanael: "Nazaré? Pode vir alguma coisa boa de lá? " Disse Filipe: "Venha e veja".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26092, 'nvi', 'João', 'jo', 1, 47, 'Ao ver Natanael se aproximando, disse Jesus: "Aí está um verdadeiro israelita, em quem não há falsidade".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26093, 'nvi', 'João', 'jo', 1, 48, 'Perguntou Natanael: "De onde me conheces? " Jesus respondeu: "Eu o vi quando você ainda estava debaixo da figueira, antes de Filipe o chamar".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26094, 'nvi', 'João', 'jo', 1, 49, 'Então Natanael declarou: "Mestre, tu és o Filho de Deus, tu és o Rei de Israel! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26095, 'nvi', 'João', 'jo', 1, 50, 'Jesus disse: "Você crê porque eu disse que o vi debaixo da figueira. Você verá coisas maiores do que essa! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26096, 'nvi', 'João', 'jo', 1, 51, 'E então acrescentou: "Digo-lhes a verdade: Vocês verão o céu aberto e os anjos de Deus subindo e descendo sobre o Filho do homem".');
