INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26483, 'nvi', 'João', 'jo', 10, 1, '"Eu lhes asseguro que aquele que não entra no aprisco das ovelhas pela porta, mas sobe por outro lugar, é ladrão e assaltante.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26484, 'nvi', 'João', 'jo', 10, 2, 'Aquele que entra pela porta é o pastor das ovelhas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26485, 'nvi', 'João', 'jo', 10, 3, 'O porteiro abre-lhe a porta, e as ovelhas ouvem a sua voz. Ele chama as suas ovelhas pelo nome e as leva para fora.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26486, 'nvi', 'João', 'jo', 10, 4, 'Depois de conduzir para fora todas as suas ovelhas, vai adiante delas, e estas o seguem, porque conhecem a sua voz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26487, 'nvi', 'João', 'jo', 10, 5, 'Mas nunca seguirão um estranho; na verdade, fugirão dele, porque não reconhecem a voz de estranhos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26488, 'nvi', 'João', 'jo', 10, 6, 'Jesus usou essa comparação, mas eles não compreenderam o que lhes estava falando.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26489, 'nvi', 'João', 'jo', 10, 7, 'Então Jesus afirmou de novo: "Digo-lhes a verdade: Eu sou a porta das ovelhas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26490, 'nvi', 'João', 'jo', 10, 8, 'Todos os que vieram antes de mim eram ladrões e assaltantes, mas as ovelhas não os ouviram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26491, 'nvi', 'João', 'jo', 10, 9, 'Eu sou a porta; quem entra por mim será salvo. Entrará e sairá, e encontrará pastagem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26492, 'nvi', 'João', 'jo', 10, 10, 'O ladrão vem apenas para furtar, matar e destruir; eu vim para que tenham vida, e a tenham plenamente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26493, 'nvi', 'João', 'jo', 10, 11, '"Eu sou o bom pastor. O bom pastor dá a sua vida pelas ovelhas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26494, 'nvi', 'João', 'jo', 10, 12, 'O assalariado não é o pastor a quem as ovelhas pertencem. Assim, quando vê que o lobo vem, abandona as ovelhas e foge. Então o lobo ataca o rebanho e o dispersa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26495, 'nvi', 'João', 'jo', 10, 13, 'Ele foge porque é assalariado e não se importa com as ovelhas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26496, 'nvi', 'João', 'jo', 10, 14, '"Eu sou o bom pastor; conheço as minhas ovelhas; e elas me conhecem;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26497, 'nvi', 'João', 'jo', 10, 15, 'assim como o Pai me conhece e eu conheço o Pai; e dou a minha vida pelas ovelhas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26498, 'nvi', 'João', 'jo', 10, 16, 'Tenho outras ovelhas que não são deste aprisco. É necessário que eu as conduza também. Elas ouvirão a minha voz, e haverá um só rebanho e um só pastor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26499, 'nvi', 'João', 'jo', 10, 17, 'Por isso é que meu Pai me ama, porque eu dou a minha vida para retomá-la.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26500, 'nvi', 'João', 'jo', 10, 18, 'Ninguém a tira de mim, mas eu a dou por minha espontânea vontade. Tenho autoridade para dá-la e para retomá-la. Esta ordem recebi de meu Pai".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26501, 'nvi', 'João', 'jo', 10, 19, 'Diante dessas palavras, os judeus ficaram outra vez divididos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26502, 'nvi', 'João', 'jo', 10, 20, 'Muitos deles diziam: "Ele está endemoninhado e enlouqueceu. Por que ouvi-lo? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26503, 'nvi', 'João', 'jo', 10, 21, 'Mas outros diziam: "Essas palavras não são de um endemoninhado. Pode um demônio abrir os olhos dos cegos? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26504, 'nvi', 'João', 'jo', 10, 22, 'Celebrava-se a festa da Dedicação, em Jerusalém. Era inverno,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26505, 'nvi', 'João', 'jo', 10, 23, 'e Jesus estava no templo, caminhando pelo Pórtico de Salomão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26506, 'nvi', 'João', 'jo', 10, 24, 'Os judeus reuniram-se ao redor dele e perguntaram: "Até quando nos deixará em suspense? Se é você o Cristo, diga-nos abertamente".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26507, 'nvi', 'João', 'jo', 10, 25, 'Jesus respondeu: "Eu já lhes disse, mas vocês não crêem. As obras que eu realizo em nome de meu Pai falam por mim,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26508, 'nvi', 'João', 'jo', 10, 26, 'mas vocês não crêem, porque não são minhas ovelhas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26509, 'nvi', 'João', 'jo', 10, 27, 'As minhas ovelhas ouvem a minha voz; eu as conheço, e elas me seguem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26510, 'nvi', 'João', 'jo', 10, 28, 'Eu lhes dou a vida eterna, e elas jamais perecerão; ninguém as poderá arrancar da minha mão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26511, 'nvi', 'João', 'jo', 10, 29, 'Meu Pai, que as deu para mim, é maior do que todos; ninguém as pode arrancar da mão de meu Pai.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26512, 'nvi', 'João', 'jo', 10, 30, 'Eu e o Pai somos um".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26513, 'nvi', 'João', 'jo', 10, 31, 'Novamente os judeus pegaram pedras para apedrejá-lo,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26514, 'nvi', 'João', 'jo', 10, 32, 'mas Jesus lhes disse: "Eu lhes mostrei muitas boas obras da parte do Pai. Por qual delas vocês querem me apedrejar? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26515, 'nvi', 'João', 'jo', 10, 33, 'Responderam os judeus: "Não vamos apedrejá-lo por nenhuma boa obra, mas pela blasfêmia, porque você é um simples homem e se apresenta como Deus".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26516, 'nvi', 'João', 'jo', 10, 34, 'Jesus lhes respondeu: "Não está escrito na Lei de vocês: ‘Eu disse: Vocês são deuses’?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26517, 'nvi', 'João', 'jo', 10, 35, 'Se ele chamou ‘deuses’ àqueles a quem veio a palavra de Deus ( e a Escritura não pode ser anulada )');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26518, 'nvi', 'João', 'jo', 10, 36, 'que dizer a respeito daquele a quem o Pai santificou e enviou ao mundo? Então, por que vocês me acusam de blasfêmia porque eu disse: ‘Sou Filho de Deus’?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26519, 'nvi', 'João', 'jo', 10, 37, 'Se eu não realizo as obras do meu Pai, não creiam em mim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26520, 'nvi', 'João', 'jo', 10, 38, 'Mas se as realizo, mesmo que não creiam em mim, creiam nas obras, para que possam saber e entender que o Pai está em mim, e eu no Pai".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26521, 'nvi', 'João', 'jo', 10, 39, 'Outra vez tentaram prendê-lo, mas ele se livrou das mãos deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26522, 'nvi', 'João', 'jo', 10, 40, 'Então Jesus atravessou novamente o Jordão e foi para o lugar onde João batizava nos primeiros dias do seu ministério. Ali ficou,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26523, 'nvi', 'João', 'jo', 10, 41, 'e muita gente foi até onde ele estava, dizendo: "Embora João nunca tenha realizado um sinal miraculoso, tudo o que ele disse a respeito deste homem era verdade".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26524, 'nvi', 'João', 'jo', 10, 42, 'E ali muitos creram em Jesus.');
