INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26525, 'nvi', 'João', 'jo', 11, 1, 'Havia um homem chamado Lázaro. Ele era de Betânia, do povoado de Maria e de sua irmã Marta. E aconteceu que Lázaro ficou doente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26526, 'nvi', 'João', 'jo', 11, 2, 'Maria, sua irmã, era a mesma que derramara perfume sobre o Senhor e lhe enxugara os pés com os cabelos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26527, 'nvi', 'João', 'jo', 11, 3, 'Então as irmãs de Lázaro mandaram dizer a Jesus: "Senhor, aquele a quem amas está doente".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26528, 'nvi', 'João', 'jo', 11, 4, 'Ao ouvir isso, Jesus disse: "Essa doença não acabará em morte; é para a glória de Deus, para que o Filho de Deus seja glorificado por meio dela".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26529, 'nvi', 'João', 'jo', 11, 5, 'Jesus amava Marta, a irmã dela e Lázaro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26530, 'nvi', 'João', 'jo', 11, 6, 'No entanto, quando ouviu falar que Lázaro estava doente, ficou mais dois dias onde estava.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26531, 'nvi', 'João', 'jo', 11, 7, 'Depois disse aos seus discípulos: "Vamos voltar para a Judéia".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26532, 'nvi', 'João', 'jo', 11, 8, 'Estes disseram: "Mestre, há pouco os judeus tentaram apedrejar-te e assim mesmo vais voltar para lá? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26533, 'nvi', 'João', 'jo', 11, 9, 'Jesus respondeu: "O dia não tem doze horas? Quem anda de dia não tropeça, pois vê a luz deste mundo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26534, 'nvi', 'João', 'jo', 11, 10, 'Quando anda de noite, tropeça, pois nele não há luz".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26535, 'nvi', 'João', 'jo', 11, 11, 'Depois de dizer isso, prosseguiu dizendo-lhes: "Nosso amigo Lázaro adormeceu, mas vou até lá para acordá-lo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26536, 'nvi', 'João', 'jo', 11, 12, 'Seus discípulos responderam: "Senhor, se ele dorme, vai melhorar".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26537, 'nvi', 'João', 'jo', 11, 13, 'Jesus tinha falado de sua morte, mas os seus discípulos pensaram que ele estava falando simplesmente do sono.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26538, 'nvi', 'João', 'jo', 11, 14, 'Então lhes disse claramente: "Lázaro morreu,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26539, 'nvi', 'João', 'jo', 11, 15, 'e para o bem de vocês estou contente por não ter estado lá, para que vocês creiam. Mas, vamos até ele".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26540, 'nvi', 'João', 'jo', 11, 16, 'Então Tomé, chamado Dídimo, disse aos outros discípulos: "Vamos também para morrermos com ele".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26541, 'nvi', 'João', 'jo', 11, 17, 'Ao chegar, Jesus verificou que Lázaro já estava no sepulcro havia quatro dias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26542, 'nvi', 'João', 'jo', 11, 18, 'Betânia distava cerca de três quilômetros de Jerusalém,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26543, 'nvi', 'João', 'jo', 11, 19, 'e muitos judeus tinham ido visitar Marta e Maria para confortá-las pela perda do irmão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26544, 'nvi', 'João', 'jo', 11, 20, 'Quando Marta ouviu que Jesus estava chegando, foi encontrá-lo, mas Maria ficou em casa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26545, 'nvi', 'João', 'jo', 11, 21, 'Disse Marta a Jesus: "Senhor, se estivesses aqui meu irmão não teria morrido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26546, 'nvi', 'João', 'jo', 11, 22, 'Mas sei que, mesmo agora, Deus te dará tudo o que pedires".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26547, 'nvi', 'João', 'jo', 11, 23, 'Disse-lhe Jesus: "O seu irmão vai ressuscitar".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26548, 'nvi', 'João', 'jo', 11, 24, 'Marta respondeu: "Eu sei que ele vai ressuscitar na ressurreição, no último dia".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26549, 'nvi', 'João', 'jo', 11, 25, 'Disse-lhe Jesus: "Eu sou a ressurreição e a vida. Aquele que crê em mim, ainda que morra, viverá;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26550, 'nvi', 'João', 'jo', 11, 26, 'e quem vive e crê em mim, não morrerá eternamente. Você crê nisso? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26551, 'nvi', 'João', 'jo', 11, 27, 'Ela lhe respondeu: "Sim, Senhor, eu tenho crido que tu és o Cristo, o Filho de Deus que devia vir ao mundo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26552, 'nvi', 'João', 'jo', 11, 28, 'E depois de dizer isso, foi para casa e, chamando à parte Maria, disse-lhe: "O Mestre está aqui e está chamando você".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26553, 'nvi', 'João', 'jo', 11, 29, 'Ao ouvir isso, Maria levantou-se depressa e foi ao encontro dele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26554, 'nvi', 'João', 'jo', 11, 30, 'Jesus ainda não tinha entrado no povoado, mas estava no lugar onde Marta o encontrara.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26555, 'nvi', 'João', 'jo', 11, 31, 'Quando notaram que ela se levantou depressa e saiu, os judeus, que a estavam confortando em casa, seguiram-na, supondo que ela ia ao sepulcro, para ali chorar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26556, 'nvi', 'João', 'jo', 11, 32, 'Chegando ao lugar onde Jesus estava e vendo-o, Maria prostrou-se aos seus pés e disse: "Senhor, se estivesses aqui meu irmão não teria morrido".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26557, 'nvi', 'João', 'jo', 11, 33, 'Ao ver chorando Maria e os judeus que a acompanhavam, Jesus agitou-se no espírito e perturbou-se.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26558, 'nvi', 'João', 'jo', 11, 34, '"Onde o colocaram? ", perguntou ele. "Vem e vê, Senhor", responderam eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26559, 'nvi', 'João', 'jo', 11, 35, 'Jesus chorou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26560, 'nvi', 'João', 'jo', 11, 36, 'Então os judeus disseram: "Vejam como ele o amava! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26561, 'nvi', 'João', 'jo', 11, 37, 'Mas alguns deles disseram: "Ele, que abriu os olhos do cego, não poderia ter impedido que este homem morresse? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26562, 'nvi', 'João', 'jo', 11, 38, 'Jesus, outra vez profundamente comovido, foi até o sepulcro. Era uma gruta com uma pedra colocada à entrada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26563, 'nvi', 'João', 'jo', 11, 39, '"Tirem a pedra", disse ele. Disse Marta, irmã do morto: "Senhor, ele já cheira mal, pois já faz quatro dias".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26564, 'nvi', 'João', 'jo', 11, 40, 'Disse-lhe Jesus: "Não lhe falei que, se você cresse, veria a glória de Deus? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26565, 'nvi', 'João', 'jo', 11, 41, 'Então tiraram a pedra. Jesus olhou para cima e disse: "Pai, eu te agradeço porque me ouviste.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26566, 'nvi', 'João', 'jo', 11, 42, 'Eu sabia que sempre me ouves, mas disse isso por causa do povo que está aqui, para que creia que tu me enviaste".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26567, 'nvi', 'João', 'jo', 11, 43, 'Depois de dizer isso, Jesus bradou em alta voz: "Lázaro, venha para fora! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26568, 'nvi', 'João', 'jo', 11, 44, 'O morto saiu, com as mãos e os pés envolvidos em faixas de linho, e o rosto envolto num pano. Disse-lhes Jesus: "Tirem as faixas dele e deixem-no ir".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26569, 'nvi', 'João', 'jo', 11, 45, 'Muitos dos judeus que tinham vindo visitar Maria, vendo o que Jesus fizera, creram nele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26570, 'nvi', 'João', 'jo', 11, 46, 'Mas alguns deles foram contar aos fariseus o que Jesus tinha feito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26571, 'nvi', 'João', 'jo', 11, 47, 'Então os chefes dos sacerdotes e os fariseus convocaram uma reunião do Sinédrio. "O que estamos fazendo? ", perguntaram eles. "Aí está esse homem realizando muitos sinais miraculosos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26572, 'nvi', 'João', 'jo', 11, 48, 'Se o deixarmos, todos crerão nele, e então os romanos virão e tirarão tanto o nosso lugar como a nossa nação".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26573, 'nvi', 'João', 'jo', 11, 49, 'Então um deles, chamado Caifás, que naquele ano era o sumo sacerdote, tomou a palavra e disse: "Nada sabeis!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26574, 'nvi', 'João', 'jo', 11, 50, 'Não percebeis que vos é melhor que morra um homem pelo povo, e que não pereça toda a nação".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26575, 'nvi', 'João', 'jo', 11, 51, 'Ele não disse isso de si mesmo, mas, sendo o sumo sacerdote naquele ano, profetizou que Jesus morreria pela nação judaica,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26576, 'nvi', 'João', 'jo', 11, 52, 'e não somente por aquela nação, mas também pelos filhos de Deus que estão espalhados, para reuni-los num povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26577, 'nvi', 'João', 'jo', 11, 53, 'E daquele dia em diante, resolveram tirar-lhe a vida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26578, 'nvi', 'João', 'jo', 11, 54, 'Por essa razão, Jesus não andava mais publicamente entre os judeus. Ao invés disso, retirou-se para uma região próxima do deserto, para um povoado chamado Efraim, onde ficou com os seus discípulos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26579, 'nvi', 'João', 'jo', 11, 55, 'Ao se aproximar a Páscoa judaica, muitos foram daquela região para Jerusalém a fim de participarem das purificações cerimoniais antes da Páscoa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26580, 'nvi', 'João', 'jo', 11, 56, 'Continuavam procurando Jesus e, no templo, perguntavam uns aos outros: "O que vocês acham? Será que ele virá à festa? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26581, 'nvi', 'João', 'jo', 11, 57, 'Mas os chefes dos sacerdotes e os fariseus tinham ordenado que, se alguém soubesse onde Jesus estava, o denunciasse, para que o pudessem prender.');
